                albumArt: 'https://file.zhuyitai.com/feedback/202511/16/b4d50b883afe5d61c73f2123b77eb979.png',
                audioSrc: 'https://files.catbox.moe/qtg3hw.mp3'
            },
            {
                title: 'æ¡ƒèŠ±ç¬‘',
                artist: 'ËšË– ğ“‚‚ê”« Music âŠ¹ê™³â—¦',
                albumArt: 'https://file.zhuyitai.com/feedback/202511/16/b4d50b883afe5d61c73f2123b77eb979.png',
                audioSrc: 'https://files.catbox.moe/vifii4.mp3'
