        for (let i = 0; i < numBeads; i++) {
            const angle = (i / numBeads) * 2 * Math.PI;
            const bead = document.createElement('div');
            bead.className = 'bead';
            const x = Math.cos(angle) * radiusX + (180 / 2) - 6;
            const y = Math.sin(angle) * radiusY + (240 / 2) - 6;
