  overflow: auto;
}

.player-container {
  width: 100%;
  max-width: 400px;
  height: auto;
  min-height: 700px;
  background: linear-gradient(145deg, #2a0808, #000000);
  border-radius: 20px;
  box-shadow: 0 10px 30px rgba(255, 50, 50, 0.3);
  border: 2px solid #440000;
  padding: 20px;
  box-sizing: border-box;
  position: relative;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
}

.main-screen, .character-screen {
  text-align: center;
  color: #ffdddd;
  flex-grow: 1;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
}

.title {
  font-size: 2.5em;
  color: #e74c3c;
  text-shadow: 0 0 15px #e74c3c, 0 0 25px #ff0000;
  margin-bottom: 40px;
  letter-spacing: 5px;
}

.button-group {
  display: flex;
  flex-direction: column;
  gap: 20px;
  width: 80%;
}

.char-button {
  padding: 15px;
  font-size: 1.2em;
  color: #ffdddd;
  background-color: #8b0000;
  border: 2px solid #c0392b;
  border-radius: 10px;
  cursor: pointer;
  transition: all 0.3s ease;
  text-shadow: 0 0 5px #ffdddd;
}

.char-button:hover {
  background-color: #c0392b;
  box-shadow: 0 0 20px #e74c3c;
}

.character-screen {
  display: none;
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: #000;
  box-sizing: border-box;
  padding: 20px;
  border-radius: 20px;
}

.crazy-text {
  font-size: 2.8em;
  color: #e74c3c;
  text-shadow: 0 0 10px #ff0000, 0 0 20px #ff0000;
  animation: shake 0.5s infinite;
  margin-bottom: 20px;
}

.character-image {
  display: none;
  width: 100%;
  max-width: 360px;
  height: auto;
  border-radius: 15px;
  border: 3px solid #e74c3c;
  box-shadow: 0 0 25px #e74c3c;
}

.exit-button {
