<script>
const audio = document.getElementById('audio-player');
const playPauseBtn = document.getElementById('play-pause-btn');
const playIcon = document.getElementById('play-icon');
const pauseIcon = document.getElementById('pause-icon');
const volumeSlider = document.getElementById('volume-slider');

function togglePlay() {
    if (audio.paused) {
        audio.play().then(() => {
            playIcon.style.display = 'none';
            pauseIcon.style.display = 'block';
        });
    } else {
        audio.pause();
        playIcon.style.display = 'block';
        pauseIcon.style.display = 'none';
    }
}
playPauseBtn.addEventListener('click', togglePlay);
volumeSlider.addEventListener('input', (e) => audio.volume = e.target.value);
audio.volume = volumeSlider.value;

let playPromise = audio.play();
if (playPromise !== undefined) {
    playPromise.catch(() => {
        playIcon.style.display = 'block';
        pauseIcon.style.display = 'none';
        const startAudio = () => { if (audio.paused) togglePlay(); };
        document.body.addEventListener('click', startAudio, { once: true });
        document.body.addEventListener('touchend', startAudio, { once: true });
    });
}

const snowBack = document.getElementById('snow-container-back');
const snowFront = document.getElementById('snow-container-front');
const snowDensity = 150; 

function createSnowflake() {
    const snowflake = document.createElement('div');
    snowflake.className = 'snowflake';
    
    const size = Math.random() * 4 + 2;
    const duration = Math.random() * 5 + 6; 
    const delay = Math.random() * -12;
    const rotation = (Math.random() - 0.5) * 720 + 'deg';
    const swayRange = 15; 

    const x1 = (Math.random() - 0.5) * swayRange * 2 + 'vw';
    const x2 = (Math.random() - 0.5) * swayRange * 2 + 'vw';
    const x3 = (Math.random() - 0.5) * swayRange * 2 + 'vw';
    const xEnd = (Math.random() - 0.5) * swayRange * 2 + 'vw';
        
    snowflake.style.left = `${Math.random() * 100}vw`;
    snowflake.style.setProperty('--size', `${size}px`);
    snowflake.style.setProperty('--duration', `${duration}s`);
    snowflake.style.setProperty('--delay', `${delay}s`);
    snowflake.style.setProperty('--rotation', rotation);
    snowflake.style.setProperty('--x1', x1);
    snowflake.style.setProperty('--x2', x2);
    snowflake.style.setProperty('--x3', x3);
    snowflake.style.setProperty('--x-end', xEnd);

    if (Math.random() > 0.7) snowFront.appendChild(snowflake);
    else snowBack.appendChild(snowflake);
}

for (let i = 0; i < snowDensity; i++) {
    createSnowflake();
}
</script>
