    <h4 style="margin: 0 0 8px 0; color: #79c0ff; font-size: 12px;">[ 关联记忆碎片 ]</h4>
    <p id="context-text" style="margin: 0;"></p>
    <div id="context-options" style="margin-top: 10px;"></div>
  </div>

</div>

<script>
  const terminal = {
    logDisplay: document.getElementById('log-display'),
    commandInput: document.getElementById('command-input'),
    popup: document.getElementById('context-popup'),
    contextText: document.getElementById('context-text'),
    contextOptions: document.getElementById('context-options'),
    logs: [],

    init() {
      this.addLog("SYSTEM: 正在连接至目标潜意识数据库...", 'system');
      this.addLog("SYSTEM: 认证通过。访问权限：只读/模拟。");
      this.commandInput.addEventListener('keydown', (e) => {
        if (e.key === 'Enter') {
          this.handleCommand(this.commandInput.value);
          this.commandInput.value = '';
        }
      });
    },

    addLog(message, type = 'user') {
      const p = document.createElement('p');
      p.style.margin = "2px 0";
      if (type === 'system') {
        p.style.color = '#58a6ff';
      } else if (type === 'error') {
        p.style.color = '#f85149';
      }
      p.textContent = message;
      this.logDisplay.appendChild(p);
      this.logDisplay.scrollTop = this.logDisplay.scrollHeight;
    },

    handleCommand(cmd) {
      this.addLog(`> ${cmd}`);
      cmd = cmd.toLowerCase().trim();

      if (cmd === 'load fragment 77b') {
        this.addLog("SYSTEM: 正在加载记忆碎片 '77B' ...");
        setTimeout(() => {
          this.addLog("SYSTEM: 碎片 '77B' 加载成功。主题：【被褥的气味】。");
          this.addLog("SYSTEM: 检测到可交互节点。请输入 'interact' 进行交互。");
        }, 1000);
      } else if (cmd === 'interact') {
        this.showPopup("你决定注入一段模拟感官数据。请选择要注入的气味类型：", [
          { text: "A. 沈长昀身上的皂角与茶香", action: 'inject_scy_scent' },
          { text: "B. 常疏惯用的龙涎香", action: 'inject_cs_scent' }
        ]);
      } else if (cmd.startsWith('action:')) {
        this.handleAction(cmd.split(':')[1]);
      } else {
        this.addLog("ERROR: 未知指令。可用指令: 'load fragment 77b', 'interact'", 'error');
      }
    },

    handleAction(action) {
      this.popup.style.display = 'none';
      if (action === 'inject_scy_scent') {
        this.addLog("SIMULATION: 注入感官数据 [气味：皂角/茶香] ...");
        setTimeout(() => {
          this.addLog("TARGET_RESPONSE: 常疏的呼吸频率出现轻微变化 (+0.2Hz)。脑电波显示与“烦躁”和“困惑”相关的活动增强。他翻了个身，将被子裹得更紧。");
          this.addLog("SYSTEM: 模拟结束。记忆碎片已重置。");
        }, 1500);
      } else if (action === 'inject_cs_scent') {
        this.addLog("SIMULATION: 注入感官数据 [气味：龙涎香] ...");
        setTimeout(() => {
          this.addLog("TARGET_RESPONSE: 目标生理指标无明显变化。脑电波显示深度睡眠的Alpha波稳定。");
          this.addLog("SYSTEM: 模拟结束。记忆碎片已重置。");
        }, 1500);
      }
    },

    showPopup(text, options) {
      this.contextText.textContent = text;
      this.contextOptions.innerHTML = '';
      options.forEach(opt => {
        const button = document.createElement('button');
        button.textContent = opt.text;
        button.style.cssText = `
          display: block; width: 100%; text-align: left;
          background: #1a3a5a; color: #c9d1d9; border: 1px solid #33a1ff;
          padding: 6px 10px; margin-bottom: 5px; border-radius: 3px; font-size: 11px; cursor: pointer;
        `;
        button.onclick = () => this.handleCommand(`action:${opt.action}`);
        this.contextOptions.appendChild(button);
      });
      this.popup.style.display = 'block';
    }
  };

  terminal.init();
</script>
</body>
