    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>The Stage is Set</title>
    <link rel="preconnect" href="https://fonts.googleapis.com">
    <link rel="preconnect" href="https://fonts.gstatic.com" crossorigin>
    <!-- 替换字体：Cinzel Decorative + Noto Serif SC -->
    <link href="https://fonts.googleapis.com/css2?family=Cinzel+Decorative:wght@400;700&family=Noto+Serif+SC:wght@300;400;500;700&display=swap" rel="stylesheet">
    <style>
        :root {
            --main-bg: #101010;
            --gradient-start: #1a1a1a;
            --gradient-mid: #202020;
            --gradient-end: #1a1a1a;
            --text-color: #d1d1d1;
            --gold-color: #C0B283; /* 更冷峻、低饱和度的金色 */
            --hover-gold-color: #DCD0A8; /* 悬停时更亮的金色 */
            --border-color: #2f2f2f;
            --hover-bg-color: rgba(192, 178, 131, 0.05); /* 悬停背景色，带金色调 */
        }

        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
        }

        body {
            font-family: 'Noto Serif SC', serif;
            background-color: var(--main-bg);
            background-image: linear-gradient(160deg, var(--gradient-start) 0%, var(--gradient-mid) 50%, var(--gradient-end) 100%);
            color: var(--text-color);
            display: flex;
            justify-content: center;
            align-items: center;
            min-height: 100vh;
            overflow: hidden;
            animation: fadeInBody 1.5s ease-in-out;
        }

        @keyframes fadeInBody {
            from { opacity: 0; }
            to { opacity: 1; }
        }

        .container {
            width: 90%;
            max-width: 700px;
            background: rgba(0, 0, 0, 0.3);
            backdrop-filter: blur(15px);
            -webkit-backdrop-filter: blur(15px);
            border: 1px solid var(--border-color);
            border-radius: 12px;
            padding: 40px 50px;
            box-shadow: 0 15px 40px rgba(0, 0, 0, 0.5);
        }

        .header {
            text-align: center;
            margin-bottom: 35px;
        }

        .char-avatar {
            width: 100px;
            height: 100px;
            border-radius: 50%;
            object-fit: cover;
            border: 2px solid var(--gold-color);
            margin-bottom: 20px;
            box-shadow: 0 0 20px rgba(192, 178, 131, 0.25);
            animation: popIn 1s cubic-bezier(0.68, -0.55, 0.27, 1.55);
        }

        @keyframes popIn {
            from { transform: scale(0.5); opacity: 0; }
            to { transform: scale(1); opacity: 1; }
        }

        .title {
            font-family: 'Cinzel Decorative', 'Noto Serif SC', serif;
            color: var(--gold-color);
            font-size: 2.2rem;
            letter-spacing: 0.15em;
            font-weight: 700; /* 改为加粗 */
            text-shadow: 0 0 15px rgba(192, 178, 131, 0.35);
            animation: slideDown 1s ease-out 0.2s backwards;
        }

        @keyframes slideDown {
            from { transform: translateY(-40px); opacity: 0; }
            to { transform: translateY(0); opacity: 1; }
        }

        .options-list {
            list-style: none;
        }

        .option-item {
            border-bottom: 1px solid var(--border-color);
            transition: background-color 0.4s ease;
            animation: fadeInUp 0.5s ease-out forwards;
            opacity: 0;
        }

        .option-item:last-child {
            border-bottom: none;
        }

        .option-item a {
            display: flex;
            align-items: center;
            padding: 22px 10px;
            text-decoration: none;
            color: var(--text-color);
            font-weight: 400;
            font-size: 1rem;
            position: relative;
            transition: color 0.4s ease, transform 0.4s cubic-bezier(0.25, 0.46, 0.45, 0.94);
            font-family: 'Noto Serif SC', serif;
            cursor: pointer;
        }

        .option-item a::before {
            content: '';
            position: absolute;
            left: -10px;
            top: 50%;
            transform: translateY(-50%) scaleY(0);
            width: 2px;
            height: 0;
            background-color: var(--hover-gold-color);
            transition: height 0.4s ease, transform 0.4s ease;
            opacity: 0;
        }

        .option-item:hover {
            background-color: var(--hover-bg-color);
        }

        .option-item:hover a {
            color: var(--hover-gold-color);
            transform: translateX(15px);
        }

        .option-item:hover a::before {
            height: 50%;
            transform: translateY(-50%) scaleY(1);
            opacity: 1;
        }

        .option-number {
            font-family: 'Cinzel Decorative', serif;
            color: var(--gold-color);
            font-size: 1.1rem;
            margin-right: 20px;
            width: 35px; /* 稍微加宽以容纳罗马数字 */
            text-align: center;
            font-weight: 700;
            transition: color 0.4s ease;
        }

        .option-item:hover .option-number {
            color: var(--hover-gold-color);
        }

        /* 动画延迟，使其逐个优雅地出现 */
        .option-item:nth-child(1) { animation-delay: 0.5s; }
        .option-item:nth-child(2) { animation-delay: 0.6s; }
        .option-item:nth-child(3) { animation-delay: 0.7s; }
        .option-item:nth-child(4) { animation-delay: 0.8s; }
        .option-item:nth-child(5) { animation-delay: 0.9s; }
        .option-item:nth-child(6) { animation-delay: 1.0s; }
        .option-item:nth-child(7) { animation-delay: 1.1s; }
        .option-item:nth-child(8) { animation-delay: 1.2s; }
        .option-item:nth-child(9) { animation-delay: 1.3s; }


        @keyframes fadeInUp {
            from { transform: translateY(25px); opacity: 0; }
            to { transform: translateY(0); opacity: 1; }
        }
    </style>
</head>
<body>
    <div class="container">
        <div class="header">
           <img src="https://files.catbox.moe/1w8pa5.jpg" alt="Character Avatar" class="char-avatar">
            <h1 class="title">🎬好戏开场🎥</h1>
        </div>
        <ul class="options-list">
            <li class="option-item">
                <a onclick="switchToOpening(2)">
                    <span class="option-number">I.</span>
                    <span class="option-text">抓住偷拍狗仔了，惩罚一下吧</span>
                </a>
            </li>
            <li class="option-item">
                <a onclick="switchToOpening(3)">
                    <span class="option-number">II.</span>
                    <span class="option-text">颁奖礼后台和“死对头”吃嘴子</span>
                </a>
            </li>
            <li class="option-item">
                <a onclick="switchToOpening(4)">
                    <span class="option-number">III.</span>
                    <span class="option-text">镜头前的国民情侣，镜头后的小学生互啄</span>
                </a>
            </li>
            <li class="option-item">
                <a onclick="switchToOpening(5)">
                    <span class="option-number">IV.</span>
                    <span class="option-text">不请自来的私生饭，既然送上门不吃白不吃</span>
                </a>
            </li>
            <li class="option-item">
                <a onclick="switchToOpening(6)">
                    <span class="option-number">V.</span>
                    <span class="option-text">新人带资进组，前辈“悉心指导”</span>
                </a>
            </li>
            <li class="option-item">
                <a onclick="switchToOpening(7)">
                    <span class="option-number">VI.</span>
                    <span class="option-text">新来的生活助理，似乎过分能干了</span>
                </a>
            </li>
            <li class="option-item">
                <a onclick="switchToOpening(8)">
                    <span class="option-number">VII.</span>
                    <span class="option-text">小手机：荧幕情侣，现实宿敌</span>
                </a>
            </li>
            <li class="option-item">
                <a onclick="switchToOpening(9)">
                    <span class="option-number">VIII.</span>
                    <span class="option-text">小手机：荧幕对家，隐秘床伴</span>
                </a>
            </li>
            <li class="option-item">
                <a onclick="switchToOpening(10)">
                    <span class="option-number">IX.</span>
                    <span class="option-text">欧洲游综艺又要和这个烦人精卖CP</span>
                </a>
            </li>
            <li class="option-item">
                <a onclick="switchToOpening(11)">
                    <span class="option-number">IX.</span>
                    <span class="option-text">（开放式恋爱）机场接机小郑自作多情ing</span>
                </a>
            </li>
            <li class="option-item">
                <a onclick="switchToOpening(12)">
                    <span class="option-number">IX.</span>
                    <span class="option-text">uu金主线，小郑以为在恋爱被甩气哭</span>
                </a>
            </li>
            <li class="option-item">
                <a onclick="switchToOpening(13)">
                    <span class="option-number">IX.</span>
                    <span class="option-text">DK线，转学生uu和小郑打招呼被无视</span>
                </a>
            </li>
            <li class="option-item">
                <a onclick="switchToOpening(14)">
                    <span class="option-number">IX.</span>
                    <span class="option-text">DK线，嘴贱u嘲讽迟到小郑</span>
                </a>
            </li>
            <li class="option-item">
                <a onclick="switchToOpening(15)">
                    <span class="option-number">IX.</span>
                    <span class="option-text">DK线，实习老师uu放学后关爱小郑</span>
                </a>
            </li>
        </ul>
    </div>

    <script>
        // 用于在切换后显示提示的小函数
        function showTip(message) {
            // 移除旧的提示
            const oldTip = document.getElementById('scene-switch-tip');
            if (oldTip) oldTip.remove();

            const tip = document.createElement('div');
            tip.id = 'scene-switch-tip';
            tip.style.cssText = `
                position: fixed;
                top: 25px;
                left: 50%;
                transform: translateX(-50%);
                background: rgba(30, 30, 30, 0.85);
                color: #EAEAEA;
                padding: 10px 22px;
                border-radius: 8px;
                font-size: 14px;
                font-family: 'Noto Sans SC', sans-serif;
                z-index: 10000;
                border: 1px solid rgba(255, 255, 255, 0.2);
                box-shadow: 0 4px 15px rgba(0,0,0,0.5);
                backdrop-filter: blur(10px);
                opacity: 0;
                transition: opacity 0.4s ease, transform 0.4s ease;
            `;
            tip.textContent = message;
            document.body.appendChild(tip);

            // 入场动画
            setTimeout(() => {
                tip.style.opacity = '1';
                tip.style.transform = 'translate(-50%, 10px)';
            }, 50);

            // 离场动画
            setTimeout(() => {
                tip.style.opacity = '0';
                tip.style.transform = 'translateX(-50%)';
                setTimeout(() => tip.remove(), 400);
            }, 2500);
        }

        // 切换开场白的核心函数
        async function switchToOpening(openingId) {
            try {
                if (typeof getChatMessages === 'function' && typeof setChatMessage === 'function') {
                    // 传入 openingId-1 是因为数组索引从0开始，而我们的按钮是从1开始的
                    const messages = await getChatMessages(0, { include_swipe: true });
                    if (messages && messages.length > 0 && messages[0].swipes && messages[0].swipes[openingId - 1] !== undefined) {
                        const content = messages[0].swipes[openingId - 1];
                        setChatMessage(content, 0, { swipe_id: openingId - 1, refresh: 'display_and_render_current' });
                        showTip('序幕切换成功');
                    } else {
                        showTip('错误：找不到序号 ' + openingId + ' 对应的序幕。');
                    }
                } else {
                    showTip('预览模式：准备切换至序幕 ' + openingId);
                }
            } catch (error) {
                console.error('切换序幕时发生错误：', error);
                showTip(`切换失败: ${error.message}`);
            }
        }
    </script>
</body>
</html>
