<!DOCTYPE html>
<html lang="zh-CN">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>顾川赦</title>
    <style>
        @import url("https://fontsapi.zeoseven.com/89/main/result.css");

        :root {
            --font-main: "JyunsaiKaai", sans-serif;
            --color-gu-primary: #8B2323; 
            --color-chu-primary: #414A6B; 
            --color-text-light: #F2F2F2;
            --color-text-dark: #A9A9A9;
            --shadow-color: rgba(0, 0, 0, 0.5);
            --transition-speed: 0.8s;
        }

        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
        }

        body {
            font-family: var(--font-main);
            font-weight: normal;
            background-color: var(--color-bg);
            color: var(--color-text-light);
            display: flex;
            justify-content: center;
            align-items: center;
            min-height: 100vh;
            padding: 20px;
        }

  
        .main-container {
            width: 100%;
            max-width: 360px;
            aspect-ratio: 9 / 16; 
            border-radius: 20px;
            padding: 25px;
            box-shadow: 0 10px 40px var(--shadow-color);
            border: 1px solid rgba(255, 255, 255, 0.1);
            display: flex;
            flex-direction: column;
            justify-content: space-between;
            overflow: hidden;
            position: relative;
 
            background-image: url('https://static.eeo.cn/upload/images/20251108/b86ce2902ad80aee2322.png');
            background-size: cover;
            background-position: center;
        }
   
        .main-container::before {
            content: '';
            position: absolute;
            top: 0;
            left: 0;
            right: 0;
            bottom: 0;
            background-color: rgba(22, 22, 29, 0.65); 
            z-index: 1;
            border-radius: 20px;
        }
     
        .main-container > * {
            position: relative;
            z-index: 2;
        }


        .title-area {
            text-align: center;
            flex-shrink: 0;
        }
        .main-title {
            font-size: 1.1em;
            letter-spacing: 5px;
            margin-bottom: 5px;
            text-shadow: 0 0 10px var(--color-gu-primary), 0 0 20px var(--color-chu-primary);
        }
        .author-info {
            font-size: 0.8em;
            color: var(--color-text-dark);
            letter-spacing: 2px;
        }

  
        .character-flipper-container {
            flex-grow: 1;
            display: flex;
            align-items: center;
            justify-content: center;
            margin: 20px 0;
            perspective: 1500px;
        }
        .card-flipper {
            width: 80%;
            aspect-ratio: 3 / 4;
            position: relative;
            transform-style: preserve-3d;
            transition: transform var(--transition-speed) cubic-bezier(0.25, 1, 0.5, 1);
        }
        .character-flipper-container:hover .card-flipper {
            transform: rotateY(180deg);
        }
        .card-face {
            position: absolute;
            width: 100%;
            height: 100%;
            backface-visibility: hidden; 
            border-radius: 15px;
            overflow: hidden;
            display: flex;
            flex-direction: column;
            justify-content: flex-end;
            padding: 20px;
            text-align: left;
            box-shadow: 0 5px 20px rgba(0,0,0,0.3);
            background-size: cover;
            background-position: center;
        }
      
        .card-face-gu {
        
            background-image: linear-gradient(to top, rgba(22, 22, 29, 0.9) 10%, rgba(215, 38, 61, 0.4) 60%, transparent 100%), url('https://saas.chatbot.cn/download/minio/standard/2025-11-08/3f03785de5f9402694e4978e7bc67d8d.png');
            border: 2px solid var(--color-gu-primary);
        }
        .card-face-chu {
       
            background-image: linear-gradient(to top, rgba(22, 22, 29, 0.9) 10%, rgba(0, 166, 166, 0.4) 60%, transparent 100%), url('https://static.eeo.cn/upload/images/20251108/282ccec65c45c5042258.png');
            border: 2px solid var(--color-chu-primary);
            transform: rotateY(180deg);
        }

        .char-name {
            font-size: 1.8em;
            margin-bottom: 5px;
      
            text-shadow: 0 2px 8px rgba(0,0,0,0.9);
        }
        .name-gu { color: var(--color-gu-primary); }
        .name-chu { color: var(--color-chu-primary); }
        .char-quote {
            font-size: 0.9em;
            line-height: 1.4;
            color: var(--color-text-light);
            text-shadow: 0 1px 5px rgba(0,0,0,0.9);
        }

      
        .opening-list {
            height: 150px; 
            overflow-y: auto;
            flex-shrink: 0;
            padding-right: 10px; 
        }
        .opening-list::-webkit-scrollbar { width: 5px; }
        .opening-list::-webkit-scrollbar-track { background: transparent; }
        .opening-list::-webkit-scrollbar-thumb { background: var(--color-text-dark); border-radius: 5px; }
        .opening-list::-webkit-scrollbar-thumb:hover { background: var(--color-text-light); }
        .opening-item {
            background: rgba(0,0,0,0.2);
            border-radius: 8px;
            padding: 12px 15px;
            margin-bottom: 10px;
            cursor: pointer;
            transition: all 0.3s ease;
            position: relative;
            overflow: hidden;
            border-left: 5px solid var(--color-text-dark);
        }
        .opening-item:hover {
            transform: translateX(5px);
            background: rgba(0,0,0,0.4);
            border-left-color: var(--item-hover-color, var(--color-gu-primary));
        }
        .opening-item h4 {
            font-size: 1.1em;
            margin: 0 0 5px 0;
            color: var(--color-text-light);
            transition: color 0.3s ease;
        }
        .opening-item:hover h4 { color: var(--item-hover-color, var(--color-gu-primary)); }
        .opening-item p { font-size: 0.8em; color: var(--color-text-dark); margin: 0; line-height: 1.5; }
        .opening-item:nth-child(2n):hover { --item-hover-color: var(--color-chu-primary); }
        .loading-placeholder { text-align: center; color: var(--color-text-dark); padding: 40px 0; }
    </style>
</head>
<body>
    <div class="main-container">
        <!-- 标题区域 -->
        <header class="title-area">
            <h1 class="main-title">顾川赦&楚宴白</h1>
            <p class="author-info">by: 霸波儿奔奔奔波儿霸</p>
        </header>

        <!-- 角色卡片翻转区域 -->
        <div class="character-flipper-container" onmouseenter="this.querySelector('.card-flipper').style.transform='rotateY(180deg)'" onmouseleave="this.querySelector('.card-flipper').style.transform='rotateY(0deg)'">
            <div class="card-flipper">
                <!-- 正面：顾川赦 -->
                <div class="card-face card-face-gu">
                    <div>
                        <h3 class="char-name name-gu">顾川赦</h3>
                        <p class="char-quote">“规矩？那是给怂人栓的链子，拴不住我。”</p>
                    </div>
                </div>
                <!-- 背面：楚宴白 -->
                <div class="card-face card-face-chu">
                     <div>
                        <h3 class="char-name name-chu">楚宴白</h3>
                        <p class="char-quote">“爱是导致软弱的缺陷，也是罪不可赦的缺陷。”</p>
                    </div>
                </div>
            </div>
        </div>

        <!-- 开场白列表区域 -->
        <section class="opening-list" id="opening-list-container">
            <div class="loading-placeholder">正在加载故事线...</div>
        </section>
    </div>

    <script>
        async function loadOpenings() {
            const container = document.getElementById('opening-list-container');
            if (!container) return;
            if (typeof getChatMessages !== 'function' || typeof setChatMessage !== 'function') {
                container.innerHTML = '<p class="loading-placeholder" style="color: #D7263D;">错误：无法连接到SillyTavern API。</p>';
                return;
            }
            try {
                const messages = await getChatMessages("0", { include_swipe: true });
                if (!messages || !messages[0].swipes || messages[0].swipes.length <= 1) {
                    container.innerHTML = '<p class="loading-placeholder">暂无可用的故事开场。</p>';
                    return;
                }
                container.innerHTML = '';
                const swipes = messages[0].swipes;
                for (let i = 1; i < swipes.length; i++) {
                    const content = swipes[i];
                    let title = `故事线 ${i}`;
                    let description = "点击开启一段新的纠葛。";
                    const titleMatch = content.match(/<!--\s*title:\s*(.*?)\s*-->/);
                    if (titleMatch) title = titleMatch[1].trim();
                    const descMatch = content.match(/<!--\s*desc:\s*(.*?)(?=\s*-->)-->/s);
                    if (descMatch) description = descMatch[1].trim().replace(/\s+/g, ' ');
                    const item = document.createElement('div');
                    item.className = 'opening-item';
                    item.innerHTML = `<h4>${title}</h4><p>${description}</p>`;
                    item.addEventListener('click', (event) => switchToOpening(i, event.currentTarget));
                    container.appendChild(item);
                }
            } catch (error) {
                console.error('加载开场白失败:', error);
                container.innerHTML = `<p class="loading-placeholder" style="color: #D7263D;">加载开场白时出错：${error.message}</p>`;
            }
        }

        async function switchToOpening(swipeId, cardElement) {
            if (cardElement) {
                cardElement.style.pointerEvents = 'none';
                cardElement.style.opacity = '0.5';
            }
            try {
                const messages = await getChatMessages("0", { include_swipe: true });
                if (messages && messages[0].swipes && messages[0].swipes[swipeId] !== undefined) {
                    await setChatMessage(messages[0].swipes[swipeId], 0, { swipe_id: swipeId, refresh: 'display_and_render_current' });
                } else {
                    throw new Error(`开场白索引 ${swipeId} 无效或未找到。`);
                }
            } catch (error) {
                console.error('切换开场白失败:', error);
                alert(`切换失败: ${error.message}`);
                if (cardElement) {
                    cardElement.style.pointerEvents = 'auto';
                    cardElement.style.opacity = '1';
                }
            }
        }
        window.addEventListener('load', loadOpenings);
    </script>
</body>
</html>
