        </div>
    </div>

    <script>
        // 动态加载开场白列表的函数
        async function loadOpenings() {
            const container = document.getElementById('opening-list-container');
            const loadingIndicator = document.getElementById('loading-indicator');

            // 检查SillyTavern API是否存在
            if (typeof getChatMessages !== 'function') {
                loadingIndicator.textContent = '错误：SillyTavern环境未就绪。';
                return;
            }

            try {
                // 获取包含所有开场白（swipes）的消息
                const messages = await getChatMessages("0", { include_swipe: true });

                // 检查是否有有效的开场白
                if (!messages || messages.length === 0 || !messages[0].swipes || messages[0].swipes.length <= 1) {
                    loadingIndicator.textContent = '暂无其他棋局可供选择。';
                    return;
                }

                loadingIndicator.style.display = 'none'; // 隐藏加载提示
                const swipes = messages[0].swipes;

                // 从索引1开始遍历，因为索引0是当前的主页界面
                for (let i = 1; i < swipes.length; i++) {
                    const content = swipes[i];
                    let title = `棋局之 ${i}`;
                    let description = "点击开启一段未知的故事。";

                    // 使用正则表达式从开场白内容中解析标题和描述
                    const titleMatch = content.match(/<!--\s*title:\s*(.*?)\s*-->/);
                    if (titleMatch) title = titleMatch[1].trim();

                    const descMatch = content.match(/<!--\s*desc:\s*(.*?)(?=\s*-->)-->/s);
                    if (descMatch) description = descMatch[1].trim();

                    // 创建开场白条目元素
                    const item = document.createElement('div');
                    item.className = 'opening-item';
                    item.innerHTML = `
                        <span class="item-icon">❖</span>
                        <div class="item-text">
                            <div class="item-title">${title}</div>
                            <div class="item-desc">${description}</div>
                        </div>
                    `;

                    // 绑定点击事件，调用切换函数，并传入准确的swipe索引
                    item.addEventListener('click', () => switchToOpening(i));

                    container.appendChild(item);
                }

            } catch (error) {
                console.error('加载开场白失败:', error);
                loadingIndicator.textContent = '加载棋局时出错，请查看控制台。';
            }
        }

        // 切换到指定开场白的函数
        async function switchToOpening(swipeId) {
            // 在点击时给与视觉反馈，防止重复点击
            const clickedItem = event.currentTarget;
            if (clickedItem) {
                 clickedItem.style.opacity = '0.6';
                 clickedItem.style.pointerEvents = 'none';
                 clickedItem.querySelector('.item-title').textContent = '正在布子...';
            }

            try {
                const messages = await getChatMessages("0", { include_swipe: true });
                if (messages && messages[0].swipes && messages[0].swipes[swipeId]) {
                    // 调用setChatMessage API进行切换
                    await setChatMessage(messages[0].swipes[swipeId], 0, {
                        swipe_id: swipeId,
                        refresh: 'display_and_render_current'
                    });
                } else {
                     throw new Error(`无法找到棋局 ${swipeId}。`);
                }
            } catch (error) {
                console.error('切换开场白失败:', error);
                alert(`切换失败: ${error.message}`);
                // 如果失败，恢复按钮状态
                if(clickedItem){
                    clickedItem.style.opacity = '1';
                    clickedItem.style.pointerEvents = 'auto';
                }
            }
        }

        // 页面加载完成后，自动执行开场白加载函数
        window.addEventListener('load', loadOpenings);
    </script>
</body>
</html>
