<html>
<head>
  <title>恐怖开场选择</title>
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <style>
    @import url('https://fonts.googleapis.com/css2?family=Creepster&family=Cinzel+Decorative:wght@700&display=swap');

    :root {
      --main-bg: #1a0202;
      --burgundy: #6b001a;
      --light-burgundy: #a13333;
      --text-color: #e0b4b4;
      --glow-color: #ff4d4d;
      --ghost-color: rgba(255, 255, 255, 0.6);
    }

    body {
      margin: 0;
      padding: 0;
      background: var(--main-bg);
      background-image: radial-gradient(circle at 50% 50%, #3d0505 0%, var(--main-bg) 70%);
      font-family: 'Helvetica Neue', 'Arial', sans-serif;
      color: var(--text-color);
      overflow-x: hidden;
      min-height: 100vh;
      display: flex;
      align-items: center;
      justify-content: center;
    }

    #ghost-layer {
      position: fixed;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      pointer-events: none;
      z-index: 1;
      overflow: hidden;
    }

    .ghost {
      position: absolute;
      width: 50px;
      height: 70px;
      background: var(--ghost-color);
      border-radius: 50% 50% 0 0;
      animation: float 20s infinite ease-in-out, fade 20s infinite ease-in-out;
      filter: blur(2px);
      box-shadow: 0 0 15px 5px var(--ghost-color);
    }

    .ghost::before, .ghost::after {
      content: '';
      position: absolute;
      bottom: -10px;
      width: 10px;
      height: 10px;
      background: var(--ghost-color);
      border-radius: 50%;
    }

    .ghost::before { left: 10px; }
    .ghost::after { right: 10px; }

    .ghost-tail {
      position: absolute;
      bottom: -30px;
      left: 50%;
      transform: translateX(-50%);
      width: 0;
      height: 0;
      border-left: 15px solid transparent;
      border-right: 15px solid transparent;
      border-top: 30px solid var(--ghost-color);
    }

    @keyframes float {
      0% { transform: translateY(0) translateX(0px); }
      25% { transform: translateY(-20px) translateX(15px); }
      50% { transform: translateY(0) translateX(-15px); }
      75% { transform: translateY(20px) translateX(15px); }
      100% { transform: translateY(0) translateX(0px); }
    }

    @keyframes fade {
       0%, 100% { opacity: 0.1; }
       50% { opacity: 0.7; }
    }

    .archive-container {
      position: relative;
      z-index: 10;
      width: 90%;
      max-width: 600px;
      margin: 40px auto;
      padding: 30px;
      background: rgba(10, 0, 0, 0.5);
      border: 1px solid var(--burgundy);
      border-radius: 10px;
      box-shadow: 0 0 30px rgba(0, 0, 0, 0.7);
      backdrop-filter: blur(5px);
    }

    .scene-section-title {
      font-family: 'Cinzel Decorative', cursive;
      font-size: 2.5em;
      text-align: center;
      color: #fce4e4;
      text-shadow: 0 0 5px var(--glow-color), 0 0 10px var(--glow-color), 0 0 20px var(--burgundy);
      margin-bottom: 30px;
      display: flex;
      align-items: center;
      justify-content: center;
    }

    .title-ghost-icon {
      font-family: 'Creepster', cursive;
      font-size: 1.2em;
      margin-right: 15px;
      animation: title-ghost-float 4s ease-in-out infinite;
      text-shadow: 0 0 10px white;
    }

    @keyframes title-ghost-float {
      0%, 100% { transform: translateY(0) rotate(-5deg); }
      50% { transform: translateY(-8px) rotate(5deg); }
    }

    .scene-list-container {
      display: flex;
      flex-direction: column;
      gap: 15px;
    }

    .scene-item-btn {
      width: 100%;
      padding: 15px;
      background: linear-gradient(145deg, rgba(40, 5, 10, 0.7), rgba(20, 0, 5, 0.7));
      border: 1px solid var(--light-burgundy);
      border-radius: 8px;
      color: var(--text-color);
      text-align: left;
      cursor: pointer;
      transition: all 0.3s ease;
      overflow: hidden;
      position: relative;
      box-shadow: inset 0 0 15px rgba(0,0,0,0.5);
    }

    .scene-item-btn:before {
      content: '';
      position: absolute;
      top: 0;
      left: -100%;
      width: 100%;
      height: 100%;
      background: linear-gradient(90deg, transparent, rgba(255,0,0,0.2), transparent);
      transition: left 0.5s ease;
    }

    .scene-item-btn:hover {
      background: linear-gradient(145deg, rgba(60, 10, 15, 0.8), rgba(40, 5, 10, 0.8));
      border-color: var(--glow-color);
      box-shadow: 0 0 15px rgba(161, 51, 51, 0.5), inset 0 0 15px rgba(0,0,0,0.5);
      transform: translateY(-2px);
    }

    .scene-item-btn:hover:before {
        left: 100%;
    }

    .scene-title {
      font-size: 1.2em;
      font-weight: bold;
      margin-bottom: 5px;
      color: #fff;
    }

    .scene-description {
      font-size: 0.9em;
      color: #c9a9a9;
    }

    /* 移动端适配 */
    @media (max-width: 768px) {
      .archive-container {
        width: 95%;
        padding: 20px;
        margin: 20px auto;
      }
      .scene-section-title {
        font-size: 2em;
      }
      .scene-item-btn {
        padding: 12px;
      }
      .scene-title {
        font-size: 1.1em;
      }
    }
  </style>
</head>
<body>
<!-- disable-default-loading -->

  <div id="ghost-layer"></div>

  <div class="archive-container">
    <div class="scene-section">
      <div class="scene-section-title">
        <span class="title-ghost-icon">👻</span>
        <span>宝宝，你来了</span>
      </div>
      <div class="scene-list-container">
        <button class="scene-item-btn" onclick="switchToOpening(2)">
          <div class="scene-title">恶俗开场白</div>
          <div class="scene-description">【袅袅被男鬼抓住了？】</div>
        </button>
        <button class="scene-item-btn" onclick="switchToOpening(3)">
          <div class="scene-title">恶俗开场白2</div>
          <div class="scene-description">【男鬼来宿舍了？】</div>
        </button>
        <button class="scene-item-btn" onclick="switchToOpening(4)">
          <div class="scene-title">开盖即食...</div>
          <div class="scene-description">【抓男鬼不成反被草】</div>
        </button>
        <button class="scene-item-btn" onclick="switchToOpening(5)">
          <div class="scene-title">爆笑开场白</div>
          <div class="scene-description">【杀鬼不成反被草】</div>
        </button>
        <button class="scene-item-btn" onclick="switchToOpening(6)">
          <div class="scene-title">和师姐一起抓鬼</div>
          <div class="scene-description">【这鬼好像认识我】</div>
        </button>
        <button class="scene-item-btn" onclick="switchToOpening(7)">
          <div class="scene-title">正经抓鬼</div>
          <div class="scene-description">【不要缠上我！】</div>
        </button>
        <button class="scene-item-btn" onclick="switchToOpening(8)">
          <div class="scene-title">被鬼缠上去寺庙</div>
          <div class="scene-description">【结果一样被草】</div>
        </button>
        <button class="scene-item-btn" onclick="switchToOpening(9)">
          <div class="scene-title">两人恋爱后</div>
          <div class="scene-description">【你去同学聚会】</div>
        </button>
        <button class="scene-item-btn" onclick="switchToOpening(10)">
          <div class="scene-title">把鬼收为极品炉鼎</div>
          <div class="scene-description">【桀桀桀榨干吧】</div>
        </button>
        <button class="scene-item-btn" onclick="switchToOpening(11)">
          <div class="scene-title">真的正经抓鬼</div>
          <div class="scene-description">【趁鬼虚要鬼命】</div>
        </button>
        <button class="scene-item-btn" onclick="switchToOpening(12)">
          <div class="scene-title">沈厉秉生前if线</div>
          <div class="scene-description">【拯救迷失的建筑男吧！】</div>
        </button>
        <button class="scene-item-btn" onclick="switchToOpening(13)">
          <div class="scene-title">生前if线2</div>
          <div class="scene-description">【你找他结果在路上相遇，某人一见钟情】</div>
        </button>
        <button class="scene-item-btn" onclick="switchToOpening(14)">
          <div class="scene-title">生前if线</div>
          <div class="scene-description">【救命，他被下迷药了，开袋即食】</div>
        </button>
        <button class="scene-item-btn" onclick="switchToOpening(15)">
          <div class="scene-title">手机开场白</div>
          <div class="scene-description">【桀桀桀 爽聊】</div>
        </button>
      </div>
    </div>
  </div>

  <script>
    // 页面加载动画
    document.addEventListener('DOMContentLoaded', function () {
      const container = document.querySelector('.archive-container');
      if (container) {
          container.style.opacity = '0';
          container.style.transform = 'translateY(30px)';
          setTimeout(() => {
            container.style.transition = 'all 0.8s cubic-bezier(0.25, 0.46, 0.45, 0.94)';
            container.style.opacity = '1';
            container.style.transform = 'translateY(0)';
          }, 100);
      }

      // 创建鬼魂
      createFloatingGhosts(8);
    });

    // 创建飘动的鬼魂
    function createFloatingGhosts(count) {
        const ghostLayer = document.getElementById('ghost-layer');
        if (!ghostLayer) return;

        for (let i = 0; i < count; i++) {
            const ghost = document.createElement('div');
            ghost.className = 'ghost';
            const tail = document.createElement('div');
            tail.className = 'ghost-tail';
            ghost.appendChild(tail);

            const duration = Math.random() * 20 + 15; // 15-35s
            const delay = Math.random() * 15;
            const scale = Math.random() * 0.6 + 0.4; // 0.4x - 1.0x size

            ghost.style.left = `${Math.random() * 100}vw`;
            ghost.style.top = `${Math.random() * 100}vh`;
            ghost.style.transform = `scale(${scale})`;
            ghost.style.animationDuration = `${duration}s, ${duration}s`;
            ghost.style.animationDelay = `${delay}s, ${delay}s`;

            ghostLayer.appendChild(ghost);
        }
    }

    // 显示提示信息
    function showTip(message) {
      const tip = document.createElement('div');
      tip.style.cssText = `
        position: fixed; top: 50%; left: 50%; transform: translate(-50%, -50%);
        background: rgba(10, 0, 0, 0.8); color: #e0b4b4; padding: 12px 24px;
        border-radius: 8px; font-size: 14px; font-weight: 500;
        z-index: 2000; backdrop-filter: blur(10px);
        border: 1px solid var(--burgundy);
        box-shadow: 0 8px 32px rgba(0, 0, 0, 0.3);
        font-family: inherit;
        transition: all 0.3s ease;
        opacity: 0;
      `;
      tip.textContent = message;
      document.body.appendChild(tip);

      setTimeout(() => {
        tip.style.opacity = '1';
        tip.style.transform = 'translate(-50%, -50%) scale(1)';
      }, 10);

      setTimeout(() => {
        tip.style.opacity = '0';
        tip.style.transform = 'translate(-50%, -50%) scale(0.8)';
        setTimeout(() => tip.remove(), 300);
      }, 2000);
    }

    // 切换开场白 (SillyTavern API)
    async function switchToOpening(openingId) {
      try {
        if (typeof getChatMessages === 'function' && typeof setChatMessage === 'function') {
          const messages = await getChatMessages(0, { include_swipe: true });
          if (messages && messages.length > 0 && messages[0].swipes && messages[0].swipes.length >= openingId) {
            const content = messages[0].swipes[openingId - 1];
            setChatMessage(content, 0, {
              swipe_id: openingId - 1,
              refresh: 'display_and_render_current',
            });
            showTip(`已切换至开场 ${openingId}`);
          } else {
            showTip('错误：无法找到指定的开场白');
          }
        } else {
          // 非SillyTavern环境下的模拟
          console.log(`模拟切换到开场白 ${openingId}`);
          showTip(`模拟: 切换至开场 ${openingId}`);
        }
      } catch (error) {
        console.error('切换开场白时出错:', error);
        showTip(`错误: ${error.message}`);
      }
    }
  </script>
</body>
</html>
