    <!-- éŸ³ä¹æ¥æº -->
    <audio id="myAudio"></audio>

    <script>
        // --- DOM å…ƒç´ è·å– ---
        const playerAndListWrapper = document.querySelector('.player-and-list-wrapper');
        const player = document.querySelector('.player');
        const albumArtImg = document.querySelector('.album-art img');
        const songTitle = document.querySelector('.song-title');
        const artistName = document.querySelector('.artist');
        const playBtn = document.querySelector('.play-btn');
        const prevBtn = document.querySelector('.prev-btn');
        const nextBtn = document.querySelector('.next-btn');
        const listBtn = document.querySelector('.list-btn'); // æ­Œæ›²åˆ—è¡¨æŒ‰é’®
        const progressSlider = document.querySelector('.progress-slider');
        const volumeSlider = document.querySelector('.volume-slider');
        const currentTimeSpan = document.querySelector('.current-time');
        const durationSpan = document.querySelector('.duration');
        const myAudio = document.getElementById('myAudio');

        const songListPanel = document.querySelector('.song-list-panel'); // æ­Œæ›²åˆ—è¡¨é¢æ¿
        const songListUl = document.querySelector('.song-list');

        // --- æ­Œæ›²æ•°æ® (åœ¨è¿™é‡Œæ·»åŠ /ä¿®æ”¹æ­Œæ›²ä¿¡æ¯) ---
        const songs = [
            {
                title: '',
                artist: 'ËšË– ğ“‚‚ê”« Music âŠ¹ê™³â—¦',
                albumArt: 'https://www.yn12377.cn/jubao/upload/smjb/2025/11/15/7fbb2ca371e040e1812d46174ded7267.png',
                audioSrc: 'https://files.catbox.moe/o6h6ya.mp3'
            },
            {
                title: '',
                artist: 'ËšË– ğ“‚‚ê”« Music âŠ¹ê™³â—¦',
                albumArt: 'https://www.yn12377.cn/jubao/upload/smjb/2025/11/15/7fbb2ca371e040e1812d46174ded7267.png',
                audioSrc: 'https://files.catbox.moe/p6k1fk.mp3'
            },
            
            // ä½ å¯ä»¥åœ¨è¿™é‡Œç»§ç»­æ·»åŠ æ›´å¤šæ­Œæ›²
            // ç¤ºä¾‹ï¼š
            // {
            //     title: 'ä½ çš„æ–°æ­Œæ›²æ ‡é¢˜',
            //     artist: 'ä½ çš„æ–°è‰ºæœ¯å®¶',
            //     albumArt: 'https://example.com/your-new-album-cover.jpg', // æ–°ä¸“è¾‘å°é¢é“¾æ¥
            //     audioSrc: 'https://example.com/your-new-audio.mp3' // æ–°éŸ³é¢‘é“¾æ¥
            // },
        ];

        let currentSongIndex = 0; // å½“å‰æ’­æ”¾æ­Œæ›²çš„ç´¢å¼•

        // --- å‡½æ•°å®šä¹‰ ---

        // æ ¼å¼åŒ–æ—¶é—´
        function formatTime(seconds) {
            const minutes = Math.floor(seconds / 60);
            const secs = Math.floor(seconds % 60);
            return `${minutes}:${secs < 10 ? '0' : ''}${secs}`;
        }

        // åŠ è½½æŒ‡å®šç´¢å¼•çš„æ­Œæ›²
        function loadSong(index) {
            if (index < 0) { // å¦‚æœæ˜¯ä¸Šä¸€é¦–ï¼Œä¸”å½“å‰æ˜¯ç¬¬ä¸€é¦–ï¼Œåˆ™è·³åˆ°æœ€åä¸€é¦–
                currentSongIndex = songs.length - 1;
            } else if (index >= songs.length) { // å¦‚æœæ˜¯ä¸‹ä¸€é¦–ï¼Œä¸”å½“å‰æ˜¯æœ€åä¸€é¦–ï¼Œåˆ™è·³åˆ°ç¬¬ä¸€é¦–
                currentSongIndex = 0;
            } else {
                currentSongIndex = index;
            }

            const song = songs[currentSongIndex];
            
            albumArtImg.src = song.albumArt;
            songTitle.textContent = song.title;
            artistName.textContent = song.artist;
            myAudio.src = song.audioSrc;

            // é‡æ–°è®¾ç½®æ’­æ”¾å™¨çŠ¶æ€
            player.classList.remove('playing');
            playBtn.textContent = 'â–¶';
            progressSlider.value = 0;
            progressSlider.style.setProperty('--current-fill', `0%`);
            currentTimeSpan.textContent = formatTime(0);

            // æ›´æ–°æ­Œæ›²åˆ—è¡¨ä¸­çš„é«˜äº®æ˜¾ç¤º
            updateSongListHighlight();
        }

        // åŠ¨æ€ç”Ÿæˆæ­Œæ›²åˆ—è¡¨
        function renderSongList() {
            songListUl.innerHTML = ''; // æ¸…ç©ºç°æœ‰åˆ—è¡¨
            songs.forEach((song, index) => {
                const li = document.createElement('li');
                li.classList.add('song-list-item');
                if (index === currentSongIndex) {
                    li.classList.add('playing-now');
                }
                li.dataset.index = index; // å­˜å‚¨æ­Œæ›²ç´¢å¼•

                li.innerHTML = `
                    <div class="album-art-sm">
                        <img src="${song.albumArt}" alt="ä¸“è¾‘å°é¢">
                    </div>
                    <div class="song-list-text">
                        <div class="song-title-sm">${song.title}</div>
                        <div class="artist-sm">${song.artist}</div>
                    </div>
                `;
                songListUl.appendChild(li);

                // ç»™æ¯ä¸ªåˆ—è¡¨é¡¹æ·»åŠ ç‚¹å‡»äº‹ä»¶
                li.addEventListener('click', () => {
                    loadSong(index); // åŠ è½½ç‚¹å‡»çš„æ­Œæ›²
                    myAudio.play(); // æ’­æ”¾
                    player.classList.add('playing');
                    playBtn.textContent = 'â¸';
                    songListPanel.classList.remove('active'); // éšè—åˆ—è¡¨
                });
            });
        }

        // æ›´æ–°æ­Œæ›²åˆ—è¡¨ä¸­çš„é«˜äº®æ˜¾ç¤º
        function updateSongListHighlight() {
            document.querySelectorAll('.song-list-item').forEach((item, index) => {
                if (index === currentSongIndex) {
                    item.classList.add('playing-now');
                    // ç¡®ä¿å½“å‰æ’­æ”¾çš„æ­Œæ›²åœ¨åˆ—è¡¨ä¸­å¯è§
                    setTimeout(() => {
                        item.scrollIntoView({ behavior: 'smooth', block: 'nearest' });
                    }, 0);
                } else {
                    item.classList.remove('playing-now');
                }
            });
        }

        // --- äº‹ä»¶ç›‘å¬å™¨ ---

        // æ’­æ”¾/æš‚åœåŠŸèƒ½
        playBtn.addEventListener('click', function() {
            if (myAudio.paused) {
                myAudio.play();
                player.classList.add('playing');
                playBtn.textContent = 'â¸';
            } else {
                myAudio.pause();
                player.classList.remove('playing');
                playBtn.textContent = 'â–¶';
            }
        });

        // ä¸Šä¸€é¦–
        prevBtn.addEventListener('click', () => {
            loadSong(currentSongIndex - 1);
            if (!myAudio.paused || player.classList.contains('playing')) { // å¦‚æœä¹‹å‰åœ¨æ’­æ”¾ï¼Œåˆ™ç»§ç»­æ’­æ”¾
                myAudio.play();
            }
        });

        // ä¸‹ä¸€é¦–
        nextBtn.addEventListener('click', () => {
            loadSong(currentSongIndex + 1);
            if (!myAudio.paused || player.classList.contains('playing')) {
                myAudio.play();
            }
        });

        // æ­Œæ›²åˆ—è¡¨æŒ‰é’® - ç°åœ¨ä¹Ÿè´Ÿè´£å…³é—­åˆ—è¡¨
        listBtn.addEventListener('click', () => {
            if (songListPanel.classList.contains('active')) {
                // å¦‚æœåˆ—è¡¨å·²æ˜¾ç¤ºï¼Œåˆ™å…³é—­å®ƒ
                songListPanel.classList.remove('active');
            } else {
                // å¦‚æœåˆ—è¡¨æœªæ˜¾ç¤ºï¼Œåˆ™æ˜¾ç¤ºå®ƒå¹¶é‡æ–°æ¸²æŸ“
                renderSongList();
                songListPanel.classList.add('active');
            }
        });

        // éŸ³ä¹åŠ è½½å®Œæˆåï¼Œæ›´æ–°æ€»æ—¶é•¿å’Œåˆå§‹åŒ–éŸ³é‡æ»‘å—
        myAudio.addEventListener('loadedmetadata', function() {
            durationSpan.textContent = formatTime(myAudio.duration);
            if (myAudio.volume === 1) { 
                myAudio.volume = 0.7;
                volumeSlider.value = 70;
            }
            volumeSlider.style.setProperty('--current-fill', `${volumeSlider.value}%`);
        });

        // éŸ³ä¹æ’­æ”¾æ—¶ï¼Œæ›´æ–°å½“å‰æ—¶é—´å’Œè¿›åº¦æ¡
        myAudio.addEventListener('timeupdate', function() {
            const progressPercent = (myAudio.currentTime / myAudio.duration) * 100;
            progressSlider.value = progressPercent;
            progressSlider.style.setProperty('--current-fill', `${progressPercent}%`);
            currentTimeSpan.textContent = formatTime(myAudio.currentTime);
        });

        // è¿›åº¦æ¡æ»‘åŠ¨æ—¶ï¼Œæ”¹å˜éŸ³ä¹æ’­æ”¾ä½ç½®
        progressSlider.addEventListener('input', function() {
            const seekTime = (this.value / 100) * myAudio.duration;
            myAudio.currentTime = seekTime;
            this.style.setProperty('--current-fill', `${this.value}%`);
            currentTimeSpan.textContent = formatTime(seekTime);
        });

        // éŸ³ä¹æ’­æ”¾ç»“æŸæ—¶
        myAudio.addEventListener('ended', function() {
            if (currentSongIndex < songs.length - 1) {
                loadSong(currentSongIndex + 1);
