        nextBtn.addEventListener('click', () => {
            // 这里可以添加切换下一首歌曲的逻辑
            console.log('下一曲');
            // 示例：跳到歌曲末尾（模拟切换）
            audio.currentTime = audio.duration - 1;
        });
    </script>
