    <script>
        const profileCard = document.getElementById('profileCard');
        const imageUpload = document.getElementById('imageUpload');
        const characterImage = document.getElementById('characterImage');

        // 初始化：从localStorage加载图片
        const savedImage = localStorage.getItem('characterImage');
        if (savedImage) {
            characterImage.src = savedImage;
        }

        // 翻转卡片
        profileCard.addEventListener('click', function(e) {
            // 防止点击上传按钮时触发翻转
            if (!e.target.closest('.image-upload') && !e.target.closest('#imageUpload')) {
                this.classList.toggle('flipped');
            }
        });

        // 上传图片
        imageUpload.addEventListener('change', function(e) {
            const file = e.target.files[0];
            if (file) {
                const reader = new FileReader();
                reader.onload = function(event) {
                    const imageData = event.target.result;
                    characterImage.src = imageData;
                    // 保存到本地存储
                    try {
                        localStorage.setItem('characterImage', imageData);
                    } catch (error) {
                        console.error("无法保存图片到 localStorage:", error);
                    }
                };
                reader.readAsDataURL(file);
            }
        });

        // 明确阻止 image-upload 标签的点击事件冒泡
