    <script>
        const audioPlayer = document.getElementById('audioPlayer');
        const musicPlayer = document.getElementById('musicPlayer');
        const playButton = document.getElementById('playButton');
        const playIcon = document.getElementById('playIcon');
        const pauseIcon = document.getElementById('pauseIcon');
        const progressBar = document.getElementById('progressBar');
        const timeDisplay = document.getElementById('timeDisplay');
        const statusText = document.getElementById('statusText');
        
        let isPlaying = false;
        let isLoaded = false;
        
        // 音频加载完成后自动播放
        audioPlayer.addEventListener('canplaythrough', function() {
            if (!isLoaded) {
                isLoaded = true;
                playAudio();
            }
        });
        
        // 更新进度条和时间显示
        audioPlayer.addEventListener('timeupdate', function() {
            if (audioPlayer.duration) {
                const progress = (audioPlayer.currentTime / audioPlayer.duration) * 100;
                progressBar.style.width = progress + '%';
                
                const currentMinutes = Math.floor(audioPlayer.currentTime / 60);
                const currentSeconds = Math.floor(audioPlayer.currentTime % 60);
                timeDisplay.textContent = `${currentMinutes}:${currentSeconds.toString().padStart(2, '0')}`;
            }
        });
        
        // 音频播放结束时会自动循环，进度条重置
        audioPlayer.addEventListener('ended', function() {
            // 由于设置了loop，这个事件基本不会触发
            // 但保留以防万一
            progressBar.style.width = '0%';
        });
        
        function togglePlay() {
            if (isPlaying) {
                pauseAudio();
            } else {
                playAudio();
            }
        }
        
        function playAudio() {
            audioPlayer.play().then(() => {
                isPlaying = true;
                playIcon.style.display = 'none';
                pauseIcon.style.display = 'inline';
                musicPlayer.classList.add('playing');
                statusText.textContent = '循环播放中 ♪♫♪';
            }).catch(error => {
                console.log('播放失败:', error);
                statusText.textContent = '播放失败 ╯︿╰';
            });
        }
        
        function pauseAudio() {
            audioPlayer.pause();
            isPlaying = false;
            playIcon.style.display = 'inline';
            pauseIcon.style.display = 'none';
            musicPlayer.classList.remove('playing');
            statusText.textContent = '循环已暂停 ❀';
        }
        
        // 尝试自动播放（现代浏览器可能会阻止）
        window.addEventListener('load', function() {
            // 先尝试静音自动播放，如果成功再取消静音
            audioPlayer.muted = true;
            audioPlayer.play().then(() => {
                audioPlayer.muted = false;
                if (!isPlaying) {
                    playAudio();
                }
            }).catch(() => {
                // 自动播放被阻止，等待用户交互
                audioPlayer.muted = false;
                statusText.textContent = '点我开始循环 ♡';
            });
        });
    </script>
