  <title>Fluxo Core Player</title>
  <style>
    @import url('https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0/css/all.min.css');
    @import url('https://fonts.googleapis.com/css2?family=Orbitron:wght@700&display=swap');
    @import url('https://fonts.googleapis.com/css2?family=Noto+Sans+SC:wght@300&display=swap');

    /* å®šä¹‰æ±Ÿå±¿å£°é£æ ¼çš„ä¸»é¢˜è‰² */
    :root {
        --primary-accent: #00BFFF;      /* é”åˆ©çš„å†°è“è‰²ï¼Œä»£è¡¨ç†æ€§å’Œç§‘æŠ€æ„Ÿ */
        --secondary-accent: #333333; /* æ·±ç°è‰²ï¼Œç”¨äºè¾…åŠ©å’Œè¾¹æ¡† */
        --glow-color: rgba(0, 191, 255, 0.6); /* è¾‰å…‰é¢œè‰² */
        --text-color: #EAEAEA;        /* äº®ç°è‰²æ–‡å­—ï¼Œåœ¨æš—èƒŒæ™¯ä¸‹æ¸…æ™° */
        --light-text-color: #888888;  /* ç¨æš—çš„ç°è‰²æ–‡å­— */
        --bg-gradient: linear-gradient(145deg, #1e1e1e, #121212); /* èƒŒæ™¯æ¸å˜ï¼Œæ·±é‚ƒçš„ç‚­é»‘è‰² */
        --glitch-shadow-1: rgba(0, 255, 255, 0.5); /* æ•…éšœæ•ˆæœé¢œè‰²1 */
