    /* æ’­æ”¾å™¨è„‰å†²åŠ¨ç”» -> æ”¹ä¸ºæ›´å…·å‘¼å¸æ„Ÿçš„è¾‰å…‰ */
    @keyframes pulse-cyber {
      0% { box-shadow: 0 0 5px var(--glow-color), inset 0 0 8px rgba(0, 0, 0, 0.4); border-color: var(--primary-accent); }
      50% { box-shadow: 0 0 12px var(--glow-color), inset 0 0 12px rgba(0, 0, 0, 0.6); border-color: white; }
      100% { box-shadow: 0 0 5px var(--glow-color), inset 0 0 8px rgba(0, 0, 0, 0.4); border-color: var(--primary-accent); }
    }

    /* æ ‡é¢˜æ–‡å­—æ•…éšœå¾®å…‰åŠ¨ç”» */
    @keyframes text-glitch-glow {
      0% { text-shadow: -1px -1px 0 var(--glitch-shadow-1), 1px 1px 0 var(--glitch-shadow-2), 0 0 3px var(--primary-accent); }
      25% { text-shadow: 1px 1px 0 var(--glitch-shadow-1), -1px -1px 0 var(--glitch-shadow-2), 0 0 5px var(--primary-accent); }
      50% { text-shadow: 1px -1px 0 var(--glitch-shadow-1), -1px 1px 0 var(--glitch-shadow-2), 0 0 3px var(--primary-accent); }
      75% { text-shadow: -1px 1px 0 var(--glitch-shadow-1), 1px -1px 0 var(--glitch-shadow-2), 0 0 5px var(--primary-accent); }
      100% { text-shadow: 1px 1px 0 var(--glitch-shadow-1), -1px -1px 0 var(--glitch-shadow-2), 0 0 3px var(--primary-accent); }
    }

    body {
      margin: 0;
      background: transparent; /* ä¿æŒå¤–éƒ¨é€æ˜ */
      font-family: 'Noto Sans SC', sans-serif;
      display: flex;
      justify-content: center;
      padding: 20px 0;
    }

    .music-player-container {
      width: 100%;
      max-width: 380px;
    }

    .music-player {
      background: var(--bg-gradient);
      border: 1px solid var(--secondary-accent);
      border-radius: 8px; /* ç¨å¾®ç¡¬æœ—çš„è¾¹è§’ */
      padding: 15px;
      position: relative;
      overflow: hidden;
      transition: max-height 0.5s cubic-bezier(0.25, 0.1, 0.25, 1);
      max-height: 70px;
      cursor: pointer;
      animation: pulse-cyber 6s infinite;
      color: var(--text-color);
      box-sizing: border-box;
      box-shadow: 0 8px 25px rgba(0, 0, 0, 0.3);
    }

    .music-player.expanded {
      max-height: 280px;
    }

    .song-title {
      font-family: 'Orbitron', sans-serif;
      color: var(--text-color);
      font-weight: 700;
      font-size: 1.3em;
      text-align: center;
      text-transform: uppercase;
      user-select: none;
      padding-top: 5px;
      animation: text-glitch-glow 3s infinite linear;
    }

    .details {
      margin-top: 15px;
      display: none;
    }

    .music-player.expanded .details {
      display: block;
    }

    .sub-info {
        color: var(--light-text-color);
        font-size: 0.9em;
        text-align: center;
        margin: 10px 0 20px 0;
        user-select: none;
        letter-spacing: 1px;
    }

    .controls {
      display: flex;
      justify-content: center;
      align-items: center;
      margin-top: 15px;
      gap: 30px;
    }

    .control-btn {
      background: transparent;
      border: 1px solid var(--secondary-accent);
      border-radius: 50%;
      width: 50px;
      height: 50px;
      color: var(--primary-accent);
      font-size: 20px;
      cursor: pointer;
      box-shadow: 0 0 10px rgba(0, 0, 0, 0.5);
      transition: all 0.3s ease;
      backdrop-filter: blur(5px);
    }

    .control-btn:hover {
      background: var(--primary-accent);
      color: #121212;
      box-shadow: 0 0 20px var(--glow-color);
      transform: scale(1.1) rotate(360deg);
    }

    .volume-container {
      margin-top: 25px;
      display: flex;
      align-items: center;
      justify-content: center;
      gap: 12px;
      color: var(--light-text-color);
      user-select: none;
    }

    .volume-slider {
      width: 60%;
      cursor: pointer;
      -webkit-appearance: none;
      background: var(--secondary-accent);
      border-radius: 2px;
      height: 4px;
      outline: none;
      transition: background 0.3s;
    }

