    <script>
      function showTip(message) {
        const existingTip = document.querySelector('.custom-tip-message');
        if (existingTip) {
          existingTip.remove();
        }

        const tip = document.createElement('div');
        tip.className = 'custom-tip-message';
        tip.style.cssText = `
          position: fixed; top: 20px; left: 50%; transform: translateX(-50%);
          background: #fff; color: #212529; padding: 10px 20px;
          font-size: 1em; font-weight: 500; z-index: 9999; border-radius: 0;
          border: 1px solid #dee2e6;
          box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1); font-family: inherit;
          opacity: 0; transition: all 0.4s ease;
        `;
        tip.textContent = message;
        document.body.appendChild(tip);
        setTimeout(() => { tip.style.opacity = '1'; tip.style.top = '30px'; }, 10);
        setTimeout(() => {
          tip.style.opacity = '0';
          tip.style.top = '20px';
          setTimeout(() => tip.remove(), 400);
        }, 2200);
