                  `
              }
          };

          function createAndShowModal(charId) {
              const existingModal = document.querySelector('.modal-overlay');
              if (existingModal) existingModal.remove();

              const data = characterData[charId];
              if (!data) return;

              const modalOverlay = document.createElement('div');
              modalOverlay.className = 'modal-overlay';
              
              modalOverlay.innerHTML = `
                <div class="modal-content">
                  <button class="modal-close-btn">&times;</button>
                  <div class="modal-body-container">
                       ${data.info}
                  </div>
                </div>
              `;

              document.body.appendChild(modalOverlay);

              setTimeout(() => modalOverlay.classList.add('active'), 10);

              const closeModal = () => {
                  modalOverlay.classList.remove('active');
                  setTimeout(() => modalOverlay.remove(), 300);
              };

              modalOverlay.querySelector('.modal-close-btn').addEventListener('click', closeModal);
              modalOverlay.addEventListener('click', (event) => {
                  if (event.target === modalOverlay) closeModal();
              });

              document.addEventListener('keydown', function escListener(event) {
                  if (event.key === 'Escape') {
                      closeModal();
                      document.removeEventListener('keydown', escListener);
                  }
              });
          }

          document.querySelectorAll('.avatar[data-char]').forEach(avatar => {
              avatar.addEventListener('click', (event) => {
                  createAndShowModal(event.currentTarget.dataset.char);
              });
          });
      });
    </script>
