<html>
<head>
    <style>
        /* --- 引入外部字体 --- */
        @import url('https://fonts.googleapis.com/css2?family=Crimson+Text:ital,wght@0,400;0,600&display=swap');
        @import url('https://fonts.googleapis.com/css2?family=Cinzel:wght@700&display=swap');
        @import url('https://fonts.googleapis.com/css2?family=EB+Garamond:ital,wght@1,400&display=swap');

        /* --- 全局与背景设计 --- */
        body {
            /* 禁用默认加载动画 */
            /* <!-- disable-default-loading --> */
            margin: 0;
            padding: 1rem;
            box-sizing: border-box;
            background-color: #0A0F1E;
            font-family: 'Crimson Text', serif;
            display: flex;
            align-items: center;
            justify-content: center;
            min-height: 100vh;
            text-align: center;
            overflow-x: hidden;
            position: relative;
        }

        /* --- 动态粒子背景 --- */
        .particle {
            position: absolute;
            background: #4A919E;
            border-radius: 50%;
            opacity: 0;
            animation: rise 15s infinite linear;
            box-shadow: 0 0 5px #4A919E, 0 0 10px #4A919E;
        }

        @keyframes rise {
            0% { transform: translateY(100vh); opacity: 1; }
            100% { transform: translateY(-10vh); opacity: 0; }
        }

        /* --- 新增：欢迎横幅 --- */
        .welcome-banner {
            font-family: 'Cinzel', serif;
            font-size: 2.5rem;
            font-weight: 700;
            color: #E0D4B0; /* 一种柔和的金色 */
            text-shadow: 0 0 5px #E0D4B0, 0 0 15px rgba(224, 212, 176, 0.5);
            margin-bottom: 2.5rem;
            letter-spacing: 0.3rem; /* 增加字母间距，更显庄重 */
            animation: banner-fade-in 2s ease-out forwards;
        }

        @keyframes banner-fade-in {
            from { opacity: 0; letter-spacing: -0.5rem; }
            to { opacity: 1; letter-spacing: 0.3rem; }
        }

        /* --- 主容器 --- */
        .container {
            width: 95%;
            max-width: 650px;
            background: rgba(11, 19, 43, 0.65);
            border: 1px solid rgba(74, 145, 158, 0.2);
            border-radius: 15px;
            padding: 2rem;
            box-shadow: 0 10px 30px rgba(0, 0, 0, 0.5);
            backdrop-filter: blur(6px);
            position: relative;
            z-index: 1;
            /* 将container也变成flex容器，以便管理横幅和内容 */
            display: flex;
            flex-direction: column;
            animation: fadeIn 1.5s 0.5s ease-out forwards; /* 延迟0.5s开始，让横幅先出现 */
            opacity: 0; /* 初始不可见 */
        }

        /* --- 简介文本 (更换字体) --- */
        .intro-text {
            /* 使用新的手稿字体 */
            font-family: 'EB Garamond', serif;
            color: #bdc5d1;
            font-size: 1.1rem; /* 移动端字体稍大一点，更易读 */
            line-height: 1.7;
            margin-bottom: 2rem;
            white-space: pre-wrap;
            font-style: italic;
            font-weight: 400;
        }

        /* --- 选项列表 --- */
        .options-list {
            list-style: none;
            padding: 0;
            margin: 0;
            width: 100%;
            display: flex;
            flex-direction: column;
            gap: 0.8rem;
        }

        .options-list li {
            font-family: 'Crimson Text', serif; /* 按钮字体保持不变 */
            font-size: 0.8rem; /* 调整按钮字体大小以适应移动端 */
            font-weight: 600;
            padding: 0.9rem 1.5rem;
            border: 1px solid rgba(74, 145, 158, 0.25);
            border-radius: 8px;
            cursor: pointer;
            position: relative;
            text-align: left;
            color: #EAEAEA;
            background-color: #1C2A4A;
            transition: all 0.4s cubic-bezier(0.19, 1, 0.22, 1);
            display: flex;
            justify-content: flex-start;
            align-items: center;
        }

        .options-list li .decoration {
            font-size: 0.9rem;
            color: rgba(74, 145, 158, 0.6);
            transition: all 0.4s ease;
        }

        .options-list li:hover {
            border-color: rgba(102, 219, 237, 0.5);
            background-color: #24345C;
            color: #FFFFFF;
            transform: translateY(-2px);
            box-shadow: 0 5px 20px rgba(74, 145, 158, 0.2);
        }

        .options-list li:hover .decoration {
            color: #63d3e8;
            text-shadow: 0 0 8px #63d3e8;
        }

        /* --- 响应式设计：PC端 --- */
        @media (min-width: 768px) {
            .welcome-banner {
                font-size: 4rem; /* PC端横幅更大气 */
            }
            .container {
                max-width: 900px;
                padding: 3rem 4rem;
            }
            .intro-text {
                font-size: 1.2rem;
            }
            .options-list {
                gap: 1rem;
            }
            .options-list li {
                font-size: 1.2rem;
                padding: 1rem 2.5rem;
            }
        }

        .error-message { color: #ff8a8a; font-size: 1rem; }

        @keyframes fadeIn {
            from { opacity: 0; }
            to { opacity: 1; }
        }
    </style>
</head>
<body>
    <div id="particle-container"></div>
    <!-- 将所有内容包裹在一个flex容器中，实现上下排列 -->
    <div style="display: flex; flex-direction: column; align-items: center; width: 100%;">
        <div class="welcome-banner">MONDAY</div>
        <div class="container">
            <p class="intro-text" id="intro-text-area"></p>
            <ul class="options-list" id="opening-options">
                <!-- 按钮将在这里自动生成 -->
            </ul>
        </div>
    </div>

    <script>
        // --- 简介文本 ---
        const introText = `口嫌体正直的章鱼男，欢迎repo（点击跳转开场白）`;

        // --- 按钮标题 ---
        const buttonTitles = [
            "⒈秘境初遇",
            "⒉喝醉了被臭章鱼逮到了捏",
            "⒊发情期+见家长环节",
            "⒋吃醋后试图土味情话攻击但user是木头",
            "⒌大章鱼我捏捏",
            "⒍简单恋爱日常",
            "⒎意外穿越现代社会",
            "⒏你这毒舌男怎么可能有这么可爱的弟弟？？？",
            "⒐回到他小时候，短短触手的小Monday...嘿嘿",
            "⒑{{user}}要退休，Monday何去何从？",
            "⒒空开场白自roll"
        ];

        // --- 核心跳转魔法 (无需改动) ---
        async function switchToOpening(swipeIndex) {
            try {
                if (typeof getChatMessages === 'function' && typeof setChatMessage === 'function') {
                    const messages = await getChatMessages(0, { include_swipe: true });
                    if (messages && messages.length > 0 && messages[0].swipes) {
                        const content = messages[0].swipes[swipeIndex];
                        if (content !== undefined) {
                            setChatMessage(content, 0, { swipe_id: swipeIndex, refresh: 'display_and_render_current' });
                            if (typeof toastr !== 'undefined') { toastr.success(`已切换至开场白 ${swipeIndex}`); }
                        }
                    }
                }
            } catch (err) {
                console.error('切换开场白时发生错误：', err);
            }
        }

        // --- 启动魔法阵 (无需改动) ---
        document.addEventListener('DOMContentLoaded', async () => {
            document.getElementById('intro-text-area').textContent = introText;
            const optionsList = document.getElementById('opening-options');
            if (!optionsList) return;
            buttonTitles.forEach((title, index) => {
                const listItem = document.createElement('li');
                const decoration_div = document.createElement('div');
                decoration_div.className = 'decoration';
                decoration_div.textContent = '❖';
                const title_div = document.createElement('div');
                title_div.textContent = title;
                listItem.appendChild(decoration_div);
                listItem.appendChild(title_div);
                const targetSwipeIndex = index + 1;
                listItem.addEventListener('click', () => { switchToOpening(targetSwipeIndex); });
                optionsList.appendChild(listItem);
            });
            const particleContainer = document.getElementById('particle-container');
            if (particleContainer) {
                const particleCount = 30;
                for (let i = 0; i < particleCount; i++) {
                    const particle = document.createElement('div');
                    particle.classList.add('particle');
                    const size = Math.random() * 4 + 1;
                    particle.style.width = `${size}px`;
                    particle.style.height = `${size}px`;
                    particle.style.left = `${Math.random() * 100}%`;
                    particle.style.animationDuration = `${Math.random() * 10 + 5}s`;
                    particle.style.animationDelay = `${Math.random() * 10}s`;
                    particleContainer.appendChild(particle);
                }
            }
        });
    </script>
</body>
</html>
