                                if (isLargeSummaryEntry && !entry.enabled) {
                                    entriesToUpdate.push({ uid: entry.uid, enabled: true });
                                    logDebug(`å¯ç”¨å½“å‰èŠå¤©çš„ å¤§æ€»ç»“ æ¡ç›®: "${entry.comment}" (UID: ${entry.uid})`);
