            let desc;

            switch(id) {
                case 'scene1': desc = '为了生存下去，抽取随机任务与男主们亲密互动，也许有一天能摆脱系统的寿命限制吧。'; break;
                case 'scene2': desc = '【请开启魅魔副本世界书】面对抵死不从的你，系统发动天外的力量，让你成为魅魔体质，1天不做浑身难受，3天不做病入膏肓，7天不做原地升天。好消息：和男主做一次续命7天。'; break;
                case 'scene3': desc = '【请开启大群聊世界书53的三个，关闭原本的手机角色无大群世界书】手机开场白，可以直接玩手机，增加了大群npc，也可以后面接剧情玩。npc列表单拆出来了，不玩手机也可以开在其他副本里'; break;
                case 'scene4': desc = '【请开启世界书7幼少人设，世界书55穿越】面对惨淡的好感度，系统决定帮你穿越回12岁订婚宴上，仅限三日改写局部过去，换回更好的开局。'; break;
                case 'scene5': desc = '【请开启世界书56变猫记】系统能量不足，就把你变成了一只猫，作为猫去完成任务吧！在世界书中修改猫的外形和品种'; break;
                default: desc = '为了生存下去，抽取随机任务与男主们亲密互动，也许有一天能摆脱系统的寿命限制吧。';
            }

            content.innerHTML = `
                <p class="text-sm text-cherry-600 mb-3">${desc}</p>
            `;
        }
    </script>
</body>
