  <title>Winter Waltz</title>
  <style>
    @import url('https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0/css/all.min.css');
    @import url('https://fonts.googleapis.com/css2?family=Orbitron&display=swap');

    @keyframes glow-border {
      0% { box-shadow: 0 0 8px #00ccff, 0 0 12px #00aaff; }
      50% { box-shadow: 0 0 12px #33ddff, 0 0 16px #0099dd; }
      100% { box-shadow: 0 0 8px #00ccff, 0 0 12px #00aaff; }
    }

    @keyframes fall {
      0% { transform: translateY(-20px); opacity: 1; }
      100% { transform: translateY(300px); opacity: 0; }
    }

    body {
      margin: 0;
      background: transparent;
      font-family: 'Orbitron', 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
      display: flex;
      justify-content: center;
      padding: 20px 0;
    }

    .music-player-container {
      width: 100%;
      max-width: 400px;
    }

    .music-player {
      background: linear-gradient(145deg, #0a0a0a, #1a1a1a);
      border: 2px solid #00ccff;
      border-radius: 16px;
      padding: 12px;
      position: relative;
      overflow: hidden;
      box-shadow:
        0 0 12px #00ccff,
        inset 0 0 15px rgba(0, 204, 255, 0.25);
      backdrop-filter: blur(10px);
      transition: max-height 0.5s ease;
      max-height: 60px;
      cursor: pointer;
      animation: glow-border 2s infinite;
      color: #aeefff;
      box-sizing: border-box;
    }

    .music-player.expanded {
      max-height: 300px;
    }

    .snowflake {
      position: absolute;
      color: #66d9ff;
      animation: fall 6s linear infinite;
      font-size: 16px;
      pointer-events: none;
      user-select: none;
      text-shadow: 0 0 6px #00ccff;
    }

    /* é›ªèŠ±éšæœºä½ç½®å’Œå»¶è¿Ÿ */
    .snowflake:nth-child(1) { left: 10%; animation-delay: 0s; }
    .snowflake:nth-child(2) { left: 30%; animation-delay: 1s; }
    .snowflake:nth-child(3) { left: 50%; animation-delay: 2s; }
    .snowflake:nth-child(4) { left: 70%; animation-delay: 3s; }
    .snowflake:nth-child(5) { left: 90%; animation-delay: 4s; }

    .song-title {
      color: #33ddff;
      font-weight: bold;
      font-size: 1.1em;
      text-align: center;
      text-shadow: 0 0 8px #00ccff;
      user-select: none;
    }

    .details {
      margin-top: 10px;
      display: none;
      animation: fadeIn 0.5s ease-in-out;
    }

    .music-player.expanded .details {
      display: block;
    }

    .artist-name,
    .lyrics {
      color: #66d9ff;
      font-size: 0.9em;
      text-align: center;
      margin: 6px 0;
      text-shadow: 0 0 6px #00ccff;
      user-select: none;
    }

    .controls {
      display: flex;
      justify-content: center;
      align-items: center;
      margin-top: 10px;
      gap: 20px;
    }

    .control-btn {
      background: #00ccff;
      border: none;
      border-radius: 50%;
      width: 42px;
      height: 42px;
      color: #001a2e;
      font-size: 18px;
      cursor: pointer;
      box-shadow: 0 0 12px #00ccff;
      transition: transform 0.2s;
    }

    .control-btn:hover {
      transform: scale(1.1);
      box-shadow: 0 0 20px #33ddff;
    }

    .volume-container {
      margin-top: 10px;
      display: flex;
      align-items: center;
      justify-content: center;
      gap: 10px;
      color: #66d9ff;
      user-select: none;
    }

    .volume-slider {
      width: 60%;
      cursor: pointer;
      -webkit-appearance: none;
      background: #00334d;
      border-radius: 6px;
      height: 6px;
      box-shadow: inset 0 0 8px #00ccff;
      outline: none;
      transition: background 0.3s;
    }

    .volume-slider::-webkit-slider-thumb {
      -webkit-appearance: none;
      width: 18px;
      height: 18px;
      background: #00ccff;
      border-radius: 50%;
      box-shadow: 0 0 12px #00ccff;
      cursor: pointer;
      transition: background 0.3s;
      margin-top: -6px;
    }

    .volume-slider:hover {
      background: #004d80;
    }

    .volume-slider:active::-webkit-slider-thumb {
      background: #33ddff;
      box-shadow: 0 0 16px #33ddff;
    }
  </style>
</head>
