<script>
    let activeCard = null;
    const profileList = document.querySelector('.profile-list');
    
    function selectProfile(cardElement) {
        if (profileList.classList.contains('is-confirmed')) return;

        if (cardElement === activeCard) {
            profileList.classList.add('is-confirmed');
            const characterName = cardElement.dataset.charName;
            
            const commandText = `(我仔细浏览后，最终选择了代号为“${characterName}”的服务。很快，我就收到了他发来的夜莺消息。)`;

            const overlay = document.querySelector('.confirmation-overlay');
            if(overlay) overlay.textContent = `♡ 订单已发送给 ${characterName} ♡`;

            try {
                if (typeof triggerSlash === 'function') {
                    triggerSlash(`/send ${commandText}`);
                } else {
                    console.warn("triggerSlash function not found.");
                    navigator.clipboard.writeText(commandText).then(() => {
                        if(overlay) overlay.textContent = "指令已复制!";
                    });
                }
            } catch (e) {
                console.error("Error sending command:", e);
                if(overlay) overlay.textContent = "发送指令失败!";
            }

            const frame = document.querySelector('.iphone-frame');
            if (frame) {
                setTimeout(() => {
                    frame.style.transition = 'opacity 0.5s ease, transform 0.5s ease';
                    frame.style.opacity = '0';
                    frame.style.transform = 'scale(0.9)';
                    setTimeout(() => frame.remove(), 500);
                }, 1500);
            }
        } else {
            if (activeCard) {
                activeCard.classList.remove('is-active');
            }
            cardElement.classList.add('is-active');
            activeCard = cardElement;
        }
    }
</script>
