        function syncLyrics(forceUpdate = false) {
            const currentTime = audioPlayer.currentTime;
            let newLyricIndex = lyrics.findIndex((line, i) => currentTime >= line.time && (lyrics[i + 1] ? currentTime < lyrics[i + 1].time : true));

