<script>
  // 确保音频自动播放
  window.onload = function() {
    const audio = document.getElementById('myAudio');
    
    // 尝试播放
    const playPromise = audio.play();
    
    if (playPromise !== undefined) {
      playPromise.then(_ => {
        // 自动播放成功
        console.log("自动播放成功");
      })
      .catch(error => {
        // 自动播放被阻止
        console.log("自动播放被阻止:", error);
        
        // 创建一个点击事件监听器来在用户交互后播放音频
        document.addEventListener('click', function audioClickHandler() {
          audio.play();
          document.removeEventListener('click', audioClickHandler);
          
          // 显示播放已开始的通知
          const notification = document.createElement('div');
          notification.className = 'audio-notification';
          notification.innerHTML = `
            <div class="audio-icon">
              <div class="audio-wave"></div>
              <div class="audio-wave"></div>
              <div class="audio-wave"></div>
              <div class="audio-wave"></div>
            </div>
            <span>背景音乐已开始播放</span>
          `;
          document.body.appendChild(notification);
          
          // 5秒后移除通知
          setTimeout(() => {
            document.body.removeChild(notification);
          }, 5500);
        });
      });
    }
    
    // 5秒后淡出通知
    setTimeout(() => {
      const notifications = document.getElementsByClassName('audio-notification');
      if (notifications.length > 0) {
        setTimeout(() => {
          for (let notification of notifications) {
            if (notification.parentNode) {
              notification.parentNode.removeChild(notification);
            }
          }
        }, 500);
      }
    }, 5000);
  };
</script>
