      color: #121212;
      box-shadow: 0 0 20px var(--glow-color);
      transform: scale(1.1) rotate(360deg);
    }

    .volume-container {
      margin-top: 25px;
      display: flex;
      align-items: center;
      justify-content: center;
      gap: 12px;
      color: var(--light-text-color);
      user-select: none;
    }

    .volume-slider {
      width: 60%;
      cursor: pointer;
      -webkit-appearance: none;
      background: var(--secondary-accent);
      border-radius: 2px;
      height: 4px;
      outline: none;
      transition: background 0.3s;
    }

    .volume-slider::-webkit-slider-thumb {
      -webkit-appearance: none;
      width: 14px;
      height: 14px;
      background: #EAEAEA;
      border: 2px solid var(--primary-accent);
      border-radius: 50%;
      box-shadow: 0 0 8px var(--glow-color);
      cursor: pointer;
      margin-top: -5px;
    }

    .volume-slider:hover {
      background: var(--primary-accent);
    }
  </style>
</head>
<body>

<div class="music-player-container">
  <div class="music-player" id="playerBox">

    <div class="song-title" id="songTitle">微醺夜</div>

    <div class="details">
      <div class="sub-info"><p>太危险，我的爱意指向明确</p>
 <p>温热蔓延
，你的香味萦绕 在 指尖</p>
</div>

      <div class="controls">
        <button class="control-btn" id="playBtn">
          <i class="fas fa-play" id="playIcon"></i>
        </button>
      </div>

      <div class="volume-container">
        <i class="fas fa-volume-mute"></i>
        <input type="range" id="volumeSlider" class="volume-slider" min="0" max="100" value="70" />
        <i class="fas fa-volume-up"></i>
      </div>
    </div>

    <audio id="audioPlayer" loop>
      <source src="https://files.catbox.moe/jwruam.mp3" />
    </audio>
  </div>
</div>

 <script>
    const audio = document.getElementById('audioPlayer');
    const playBtn = document.getElementById('playBtn');
    const playIcon = document.getElementById('playIcon');
    const volumeSlider = document.getElementById('volumeSlider');
    const playerBox = document.getElementById('playerBox');

    audio.volume = volumeSlider.value / 100;

    // 自动播放尝试
    document.addEventListener('DOMContentLoaded', async () => {
      // 展开播放器以示加载完成
      playerBox.classList.add('expanded');
      try {
        await audio.play();
        playIcon.className = 'fas fa-pause';
      } catch {
        console.log('自动播放失败，需要用户交互');
        // 如果自动播放失败，则不展开播放器，等待用户点击
        playerBox.classList.remove('expanded');
      }
    });

    playBtn.addEventListener('click', (e) => {
      e.stopPropagation(); // 防止点击事件冒泡到父元素，导致播放器折叠
      if (audio.paused) {
        audio.play().then(() => {
          playIcon.className = 'fas fa-pause';
        }).catch(error => console.log("播放失败: ", error));
      } else {
        audio.pause();
        playIcon.className = 'fas fa-play';
      }
    });

    volumeSlider.addEventListener('input', (e) => {
      e.stopPropagation();
      audio.volume = e.target.value / 100;
    });

    // 点击音量条本身不应导致播放器折叠
    volumeSlider.addEventListener('click', (e) => {
      e.stopPropagation();
    });

    playerBox.addEventListener('click', (e) => {
      // 确保点击的不是控制区域
      if (!e.target.closest('.controls') && !e.target.closest('.volume-container')) {
        playerBox.classList.toggle('expanded');
      }
    });
  </script>

</body>
</html>
