    @import url('https://fonts.googleapis.com/css2?family=Noto+Sans+SC:wght@300&display=swap');

    /* 定义江屿声风格的主题色 */
    :root {
        --primary-accent: #00BFFF;      /* 锐利的冰蓝色，代表理性和科技感 */
        --secondary-accent: #333333; /* 深灰色，用于辅助和边框 */
        --glow-color: rgba(0, 191, 255, 0.6); /* 辉光颜色 */
        --text-color: #EAEAEA;        /* 亮灰色文字，在暗背景下清晰 */
        --light-text-color: #888888;  /* 稍暗的灰色文字 */
        --bg-gradient: linear-gradient(145deg, #1e1e1e, #121212); /* 背景渐变，深邃的炭黑色 */
        --glitch-shadow-1: rgba(0, 255, 255, 0.5); /* 故障效果颜色1 */
        --glitch-shadow-2: rgba(255, 0, 191, 0.5); /* 故障效果颜色2 */
    }

    /* 播放器脉冲动画 -> 改为更具呼吸感的辉光 */
    @keyframes pulse-cyber {
      0% { box-shadow: 0 0 5px var(--glow-color), inset 0 0 8px rgba(0, 0, 0, 0.4); border-color: var(--primary-accent); }
      50% { box-shadow: 0 0 12px var(--glow-color), inset 0 0 12px rgba(0, 0, 0, 0.6); border-color: white; }
      100% { box-shadow: 0 0 5px var(--glow-color), inset 0 0 8px rgba(0, 0, 0, 0.4); border-color: var(--primary-accent); }
    }

    /* 标题文字故障微光动画 */
    @keyframes text-glitch-glow {
      0% { text-shadow: -1px -1px 0 var(--glitch-shadow-1), 1px 1px 0 var(--glitch-shadow-2), 0 0 3px var(--primary-accent); }
      25% { text-shadow: 1px 1px 0 var(--glitch-shadow-1), -1px -1px 0 var(--glitch-shadow-2), 0 0 5px var(--primary-accent); }
      50% { text-shadow: 1px -1px 0 var(--glitch-shadow-1), -1px 1px 0 var(--glitch-shadow-2), 0 0 3px var(--primary-accent); }
      75% { text-shadow: -1px 1px 0 var(--glitch-shadow-1), 1px -1px 0 var(--glitch-shadow-2), 0 0 5px var(--primary-accent); }
      100% { text-shadow: 1px 1px 0 var(--glitch-shadow-1), -1px -1px 0 var(--glitch-shadow-2), 0 0 3px var(--primary-accent); }
    }

    body {
      margin: 0;
      background: transparent; /* 保持外部透明 */
      font-family: 'Noto Sans SC', sans-serif;
      display: flex;
      justify-content: center;
      padding: 20px 0;
    }

    .music-player-container {
      width: 100%;
      max-width: 380px;
    }

    .music-player {
      background: var(--bg-gradient);
      border: 1px solid var(--secondary-accent);
      border-radius: 8px; /* 稍微硬朗的边角 */
      padding: 15px;
      position: relative;
      overflow: hidden;
      transition: max-height 0.5s cubic-bezier(0.25, 0.1, 0.25, 1);
      max-height: 70px;
      cursor: pointer;
      animation: pulse-cyber 6s infinite;
      color: var(--text-color);
      box-sizing: border-box;
      box-shadow: 0 8px 25px rgba(0, 0, 0, 0.3);
    }

    .music-player.expanded {
      max-height: 280px;
    }

    .song-title {
      font-family: 'Orbitron', sans-serif;
      color: var(--text-color);
      font-weight: 700;
      font-size: 1.3em;
      text-align: center;
      text-transform: uppercase;
      user-select: none;
      padding-top: 5px;
      animation: text-glitch-glow 3s infinite linear;
    }

    .details {
      margin-top: 15px;
      display: none;
    }

    .music-player.expanded .details {
      display: block;
    }

    .sub-info {
        color: var(--light-text-color);
        font-size: 0.9em;
        text-align: center;
        margin: 10px 0 20px 0;
        user-select: none;
        letter-spacing: 1px;
    }

    .controls {
      display: flex;
      justify-content: center;
      align-items: center;
      margin-top: 15px;
      gap: 30px;
    }

    .control-btn {
      background: transparent;
      border: 1px solid var(--secondary-accent);
      border-radius: 50%;
      width: 50px;
      height: 50px;
      color: var(--primary-accent);
      font-size: 20px;
      cursor: pointer;
      box-shadow: 0 0 10px rgba(0, 0, 0, 0.5);
      transition: all 0.3s ease;
      backdrop-filter: blur(5px);
    }

    .control-btn:hover {
      background: var(--primary-accent);
      color: #121212;
      box-shadow: 0 0 20px var(--glow-color);
      transform: scale(1.1) rotate(360deg);
    }

    .volume-container {
      margin-top: 25px;
      display: flex;
      align-items: center;
      justify-content: center;
      gap: 12px;
      color: var(--light-text-color);
      user-select: none;
    }

    .volume-slider {
      width: 60%;
      cursor: pointer;
      -webkit-appearance: none;
      background: var(--secondary-accent);
      border-radius: 2px;
      height: 4px;
      outline: none;
      transition: background 0.3s;
    }

    .volume-slider::-webkit-slider-thumb {
      -webkit-appearance: none;
      width: 14px;
      height: 14px;
      background: #EAEAEA;
      border: 2px solid var(--primary-accent);
      border-radius: 50%;
      box-shadow: 0 0 8px var(--glow-color);
      cursor: pointer;
      margin-top: -5px;
    }

    .volume-slider:hover {
      background: var(--primary-accent);
    }
  </style>
</head>
<body>

<div class="music-player-container">
  <div class="music-player" id="playerBox">

    <div class="song-title" id="songTitle">两难</div>

    <div class="details">
      <div class="sub-info"><p>你到底在为谁等待


</p>
 <p>无人内心潮澎湃
</p>
</div>

      <div class="controls">
        <button class="control-btn" id="playBtn">
          <i class="fas fa-play" id="playIcon"></i>
        </button>
      </div>

      <div class="volume-container">
        <i class="fas fa-volume-mute"></i>
        <input type="range" id="volumeSlider" class="volume-slider" min="0" max="100" value="70" />
        <i class="fas fa-volume-up"></i>
      </div>
    </div>

    <audio id="audioPlayer" loop>
      <source src="https://files.catbox.moe/dg4m3j.mp3" />
    </audio>
  </div>
</div>

 <script>
    const audio = document.getElementById('audioPlayer');
    const playBtn = document.getElementById('playBtn');
    const playIcon = document.getElementById('playIcon');
    const volumeSlider = document.getElementById('volumeSlider');
    const playerBox = document.getElementById('playerBox');

    audio.volume = volumeSlider.value / 100;

    // 自动播放尝试
    document.addEventListener('DOMContentLoaded', async () => {
      // 展开播放器以示加载完成
      playerBox.classList.add('expanded');
      try {
        await audio.play();
        playIcon.className = 'fas fa-pause';
      } catch {
        console.log('自动播放失败，需要用户交互');
        // 如果自动播放失败，则不展开播放器，等待用户点击
        playerBox.classList.remove('expanded');
      }
    });

    playBtn.addEventListener('click', (e) => {
      e.stopPropagation(); // 防止点击事件冒泡到父元素，导致播放器折叠
      if (audio.paused) {
        audio.play().then(() => {
          playIcon.className = 'fas fa-pause';
        }).catch(error => console.log("播放失败: ", error));
      } else {
        audio.pause();
        playIcon.className = 'fas fa-play';
      }
    });

    volumeSlider.addEventListener('input', (e) => {
      e.stopPropagation();
      audio.volume = e.target.value / 100;
    });

    // 点击音量条本身不应导致播放器折叠
    volumeSlider.addEventListener('click', (e) => {
      e.stopPropagation();
    });

    playerBox.addEventListener('click', (e) => {
      // 确保点击的不是控制区域
      if (!e.target.closest('.controls') && !e.target.closest('.volume-container')) {
        playerBox.classList.toggle('expanded');
      }
    });
  </script>

</body>
</html>
