 <script>
    const audio = document.getElementById('audioPlayer');
    const playBtn = document.getElementById('playBtn');
    const playIcon = document.getElementById('playIcon');
    const volumeSlider = document.getElementById('volumeSlider');
    const playerBox = document.getElementById('playerBox');

    audio.volume = volumeSlider.value / 100;

    // 自动播放尝试
    document.addEventListener('DOMContentLoaded', async () => {
      // 展开播放器以示加载完成
      playerBox.classList.add('expanded');
      try {
        await audio.play();
        playIcon.className = 'fas fa-pause';
      } catch {
        console.log('自动播放失败，需要用户交互');
        // 如果自动播放失败，则不展开播放器，等待用户点击
        playerBox.classList.remove('expanded');
      }
    });

    playBtn.addEventListener('click', (e) => {
      e.stopPropagation(); // 防止点击事件冒泡到父元素，导致播放器折叠
      if (audio.paused) {
        audio.play().then(() => {
          playIcon.className = 'fas fa-pause';
        }).catch(error => console.log("播放失败: ", error));
      } else {
        audio.pause();
        playIcon.className = 'fas fa-play';
      }
    });

    volumeSlider.addEventListener('input', (e) => {
      e.stopPropagation();
      audio.volume = e.target.value / 100;
    });

    // 点击音量条本身不应导致播放器折叠
    volumeSlider.addEventListener('click', (e) => {
      e.stopPropagation();
    });

    playerBox.addEventListener('click', (e) => {
      // 确保点击的不是控制区域
      if (!e.target.closest('.controls') && !e.target.closest('.volume-container')) {
        playerBox.classList.toggle('expanded');
      }
    });
  </script>

</body>
