    @keyframes pulse-cyber {
      0% { box-shadow: 0 0 5px var(--glow-color), inset 0 0 8px rgba(0, 0, 0, 0.4); border-color: var(--primary-accent); }
      50% { box-shadow: 0 0 12px var(--glow-color), inset 0 0 12px rgba(0, 0, 0, 0.6); border-color: white; }
