            --text-secondary: #8E8D8A; /* 柔和的灰色 */
            --accent-color: #BF9A75; /* 复古的棕褐色，用于强调 */
            --accent-hover: #A68462; /* 悬停时的深棕褐色 */
            --border-color: #DCD9D4; /* 边框与分割线颜色 */
            --shadow-color-light: rgba(0, 0, 0, 0.08); /* 浅阴影 */
            --shadow-color-dark: rgba(0, 0, 0, 0.15); /* 深阴影 */
        }

        /* --- 容器与布局 --- */
        .main-container {
            font-family: var(--font-serif);
            color: var(--text-main);
            background-color: var(--bg-color);
