            max-width: 800px;
            margin: auto;
            padding: 40px;
            border-radius: 4px;
            box-shadow: 0 8px 25px var(--shadow-color-dark);
            border: 1px solid #EAE6DF;
            position: relative;
        }

        /* --- 主标题与作者信息 --- */
        .header {
            text-align: center;
            margin-bottom: 40px;
            border-bottom: 1px solid var(--border-color);
            padding-bottom: 30px;
        }
        .main-title {
            font-size: 2.5em;
            font-weight: 600;
            margin-bottom: 10px;
            letter-spacing: 2px;
        }
        .author-info {
            font-size: 0.9em;
            color: var(--text-secondary);
        }
        .author-info a {
            color: var(--text-secondary);
            text-decoration: none;
            transition: color 0.3s;
        }
        .author-info a:hover {
            color: var(--accent-color);
        }

        /* --- 内容区块 --- */
        .content-section {
            margin-bottom: 40px;
        }
        .section-title {
            font-size: 1.5em;
            font-weight: 600;
            margin-bottom: 25px;
            padding-bottom: 10px;
            border-bottom: 1px solid var(--border-color);
            position: relative;
        }
        /* 标题装饰线 */
        .section-title::after {
            content: '';
            position: absolute;
            bottom: -1px;
            left: 0;
            width: 60px;
            height: 2px;
            background-color: var(--accent-color);
        }

        /* --- 背景信息 --- */
        .background-text {
            line-height: 1.9;
            color: var(--text-main);
            text-align: justify;
        }
        .background-text p {
            margin-bottom: 1em;
        }

        /* --- 角色信息 --- */
        .character-profile {
            display: flex;
            gap: 30px;
            align-items: flex-start;
        }
        /* 头像 */
        .profile-avatar {
            width: 150px;
            height: 150px;
            flex-shrink: 0;
            overflow: hidden;
            border-radius: 50%;
            border: 4px solid #fff;
            box-shadow: 0 4px 15px var(--shadow-color-dark);
        }
        .profile-avatar img {
            width: 100%;
            height: 100%;
            object-fit: cover;
            filter: grayscale(40%) sepia(20%); /* 应用灰度和复古滤镜 */
            transition: transform 0.4s ease;
        }
        .profile-avatar:hover img {
            transform: scale(1.1);
        }
        /* 基础信息 */
        .profile-details {
            flex-grow: 1;
        }
        .profile-name {
            font-size: 2em;
            font-weight: 600;
            margin-bottom: 5px;
        }
        .profile-occupation {
            font-size: 1.1em;
            color: var(--text-secondary);
            margin-bottom: 20px;
        }
        /* 性格标签（互动气泡）*/
        .profile-tags {
            display: flex;
            flex-wrap: wrap;
            gap: 10px;
            margin-bottom: 20px;
        }
        .tag {
            background-color: #EAE6DF;
            color: var(--text-main);
            padding: 6px 14px;
            border-radius: 20px;
            font-size: 0.9em;
            box-shadow: 0 2px 4px var(--shadow-color-light);
            cursor: default;
            transition: all 0.3s ease;
        }
        .tag:hover {
            transform: translateY(-2px);
            background-color: #DCD9D4;
            box-shadow: 0 4px 8px var(--shadow-color-light);
        }
        /* 角色引言，体现悲伤感 */
        .profile-quote {
            font-style: italic;
            padding: 15px;
            border-left: 3px solid var(--accent-color);
            background-color: rgba(191, 154, 117, 0.05);
            line-height: 1.8;
            color: #6a6662;
        }

        /* --- 开场白列表 --- */
        .opening-list {
            display: flex;
            flex-direction: column;
            gap: 15px;
        }
        .opening-item {
            background: #fff;
            border: 1px solid var(--border-color);
            padding: 20px;
            border-radius: 4px;
            cursor: pointer;
            transition: all 0.3s ease;
            opacity: 0;
            transform: translateY(10px);
            animation: fadeInItem 0.5s ease-out forwards;
            animation-delay: calc(var(--index) * 0.1s); /* 交错动画 */
        }
        /* 点击反馈 */
        .opening-item:hover {
            transform: translateY(-3px);
            box-shadow: 0 6px 20px var(--shadow-color-light);
            border-color: var(--accent-color);
        }
        .opening-item:active {
            transform: translateY(0);
            background-color: #fcfaf7;
        }
        .opening-title {
            font-size: 1.2em;
            font-weight: 600;
            margin-bottom: 8px;
            color: var(--accent-color);
        }
        .opening-desc {
            font-size: 0.95em;
            color: var(--text-secondary);
            line-height: 1.7;
        }

        /* --- 动画 --- */
        @keyframes fadeInItem {
            to {
                opacity: 1;
                transform: translateY(0);
            }
        }

        /* 响应式设计 */
        @media (max-width: 600px) {
            .main-container {
                padding: 20px;
            }
            .character-profile {
                flex-direction: column;
                align-items: center;
                text-align: center;
            }
            .profile-details {
                width: 100%;
            }
            .profile-tags {
                justify-content: center;
            }
        }
    </style>
</head>
<body>
    <div class="main-container">
        <!-- 页面标题 -->
        <header class="header">
            <h1 class="main-title">沈教授，爱是什么</h1>
            <div class="author-info">
                <span>作者: 沈季白</span> | <a href="https://yanfa-upload.oss-cn-zhangjiakou.aliyuncs.com/images/20251113/1762989077_wqbs_tpzb.jpg" target="_blank">更新日志</a>
            </div>
        </header>

        <!-- 背景信息 -->
        <section class="content-section">
            <h2 class="section-title">背景</h2>
            <div class="background-text">
                <p>江城大学是一所兼具历史底蕴与现代气息的百年名校。于他而言，这里是他用以维持社会身份、逃避独自在家时那份窒息思念的“表世界”。规律的教学活动、与同事学生间的互动，构成了他白日的面具，让他能暂时将那个破碎的自我，隐藏在“沈教授”这个得体的身份之后。</p>
                <p>时间仿佛在这里被分为两半，一半喧嚣，一半沉寂。而他，总是属于沉寂的那一半。</p>
            </div>
        </section>

        <!-- 角色信息 -->
        <section class="content-section">
            <h2 class="section-title">侧写</h2>
            <div class="character-profile">
                <!-- 头像 -->
                <div class="profile-avatar">
                   <!-- 提醒：这里的头像链接来自您的要求。 -->
                   <img src="https://yanfa-upload.oss-cn-zhangjiakou.aliyuncs.com/images/20251113/1762989077_wqbs_tpzb.jpg" alt="沈季白">
                </div>
                <!-- 详细信息 -->
                <div class="profile-details">
                    <h3 class="profile-name">沈季白</h3>
                    <p class="profile-occupation">31岁 / 中文系教授</p>
                    <!-- 互动气泡式标签 -->
                    <div class="profile-tags">
                        <span class="tag">温柔儒雅</span>
                        <span class="tag">极致人夫感</span>
                        <span class="tag">心思细腻</span>
                        <span class="tag">破碎感</span>
                        <span class="tag">共情力强</span>
                    </div>
                    <!-- 引言 -->
                    <div class="profile-quote">
                        “家中一切照旧，只是少了一个人……不，是少了一个世界。我每天为她热牛奶，摆两副碗筷，擦拭那枚刻着她名字的婚戒……好像这样，她就从未离开过。”
                    </div>
                </div>
            </div>
        </section>

        <!-- 动态开场白列表 -->
        <section class="content-section">
            <h2 class="section-title">故事开端</h2>
            <!-- 这个容器将由JavaScript动态填充 -->
            <div class="opening-list" id="opening-list-container">
                <!-- 加载中或无开场白时的提示 -->
            </div>
        </section>
    </div>

    <!-- 交互逻辑脚本 -->
    <script>
        // --- 开场白跳转逻辑 ---

        /**
         * 异步加载并显示所有开场白
         */
        async function loadOpenings() {
            const container = document.getElementById('opening-list-container');
            if (!container) return;

            // 检查SillyTavern的API函数是否存在
            if (typeof getChatMessages !== 'function') {
                container.innerHTML = '<p style="color: #999;">错误：无法加载开场白，SillyTavern API未找到。</p>';
                return;
            }

            try {
                // 获取所有消息，包含所有开场白（swipes）
                const messages = await getChatMessages("0", { include_swipe: true });

                // 检查是否有有效的开场白（大于1个，因为第0个是当前主页）
                if (!messages || messages.length === 0 || !messages[0].swipes || messages[0].swipes.length <= 1) {
                    container.innerHTML = '<p style="color: #999;">暂无其他故事线。</p>';
                    return;
                }

                const swipes = messages[0].swipes;
                // 从索引1开始遍历，跳过当前主页
                for (let i = 1; i < swipes.length; i++) {
                    const content = swipes[i];
                    let title = `故事线 ${i}`;
                    let description = "点击开启一段回忆。";

                    // 使用正则表达式从注释中解析标题和描述
                    const titleMatch = content.match(/<!--\s*title:\s*(.*?)\s*-->/);
                    if (titleMatch) title = titleMatch[1].trim();

                    const descMatch = content.match(/<!--\s*desc:\s*(.*?)(?=\s*-->)-->/s);
                    if (descMatch) description = descMatch[1].trim();

                    // 创建开场白卡片元素
                    const item = document.createElement('div');
                    item.className = 'opening-item';
                    item.style.setProperty('--index', i - 1); // 用于交错显示动画
                    item.innerHTML = `
                        <h4 class="opening-title">${title}</h4>
                        <p class="opening-desc">${description}</p>
                    `;

                    // 为每个卡片绑定点击事件，并传入准确的swipe索引
                    item.addEventListener('click', () => switchToOpening(i));

                    container.appendChild(item);
                }

            } catch (error) {
                console.error('加载开场白时出错:', error);
                container.innerHTML = '<p style="color: #E85A4F;">加载失败，请检查控制台信息。</p>';
            }
        }

        /**
         * 切换到指定的开场白
         * @param {number} swipeId - 开场白在swipes数组中的索引
         */
        async function switchToOpening(swipeId) {
            const card = event.currentTarget;
            if (card) {
                 // 提供点击反馈，防止重复点击
                 card.style.opacity = '0.6';
                 card.style.pointerEvents = 'none';
                 card.querySelector('.opening-title').innerText = "正在进入回忆...";
            }

            try {
                const messages = await getChatMessages("0", { include_swipe: true });
                if (messages && messages[0].swipes && messages[0].swipes[swipeId]) {
                    // 调用SillyTavern API执行切换
                    await setChatMessage(messages[0].swipes[swipeId], 0, {
                        swipe_id: swipeId,
                        refresh: 'display_and_render_current'
                    });
                } else {
                     throw new Error(`无法找到索引为 ${swipeId} 的故事线。`);
                }
            } catch (error) {
                console.error('切换开场白失败:', error);
                alert(`切换失败: ${error.message}`);
                // 如果切换失败，恢复卡片状态
                if(card){
                    card.style.opacity = '1';
                    card.style.pointerEvents = 'auto';
                }
            }
        }

        // 页面加载完成后，自动执行开场白加载函数
        window.addEventListener('load', loadOpenings);

    </script>
</body>
</html>
