        // --- DOM 元素获取 ---
        const playerAndListWrapper = document.querySelector('.player-and-list-wrapper');
        const player = document.querySelector('.player');
        const albumArtImg = document.querySelector('.album-art img');
        const songTitle = document.querySelector('.song-title');
        const artistName = document.querySelector('.artist');
        const playBtn = document.querySelector('.play-btn');
        const prevBtn = document.querySelector('.prev-btn');
        const nextBtn = document.querySelector('.next-btn');
        const listBtn = document.querySelector('.list-btn'); // 歌曲列表按钮
        const progressSlider = document.querySelector('.progress-slider');
        const volumeSlider = document.querySelector('.volume-slider');
        const currentTimeSpan = document.querySelector('.current-time');
        const durationSpan = document.querySelector('.duration');
        const myAudio = document.getElementById('myAudio');

        const songListPanel = document.querySelector('.song-list-panel'); // 歌曲列表面板
        const songListUl = document.querySelector('.song-list');

        // --- 歌曲数据 (在这里添加/修改歌曲信息) ---
        const songs = [
            {
                title: '恋人',
                artist: '˚˖ 𓂂ꔫ Music ⊹꙳◦',
                albumArt: 'https://xiaoiwg.dongfeng-nissan.com.cn/aicc-workbench/res/download/default/temp/images/20251113/437aec29cafa798290dbd9f98fa7d3be3c5b01b4.png',
                audioSrc: 'https://files.catbox.moe/j127nd.mp3'
            },
            {
                title: 'HANABI',
                artist: '˚˖ 𓂂ꔫ Music ⊹꙳◦',
                albumArt: 'https://xiaoiwg.dongfeng-nissan.com.cn/aicc-workbench/res/download/default/temp/images/20251113/437aec29cafa798290dbd9f98fa7d3be3c5b01b4.png',
                audioSrc: 'https://files.catbox.moe/65mmn2.mp3'
            },
            {
                title: '乌托邦',
                artist: '˚˖ 𓂂ꔫ Music ⊹꙳◦',
                albumArt: 'https://xiaoiwg.dongfeng-nissan.com.cn/aicc-workbench/res/download/default/temp/images/20251113/437aec29cafa798290dbd9f98fa7d3be3c5b01b4.png',
                audioSrc: 'https://files.catbox.moe/u9h0dh.mp3'
            },
            {
                title: 'time machine',
                artist: '˚˖ 𓂂ꔫ Music ⊹꙳◦',
                albumArt: 'https://xiaoiwg.dongfeng-nissan.com.cn/aicc-workbench/res/download/default/temp/images/20251113/437aec29cafa798290dbd9f98fa7d3be3c5b01b4.png',
                audioSrc: 'https://files.catbox.moe/knlt98.mp3'
            },
            {
                title: '劳伦斯先生圣诞快乐',
                artist: '˚˖ 𓂂ꔫ Music ⊹꙳◦',
                albumArt: 'https://xiaoiwg.dongfeng-nissan.com.cn/aicc-workbench/res/download/default/temp/images/20251113/437aec29cafa798290dbd9f98fa7d3be3c5b01b4.png',
                audioSrc: 'https://files.catbox.moe/gw8cv1.mp3'
            },
            // 你可以在这里继续添加更多歌曲
            // 示例：
            // {
            //     title: '你的新歌曲标题',
            //     artist: '你的新艺术家',
            //     albumArt: 'https://xiaoiwg.dongfeng-nissan.com.cn/aicc-workbench/res/download/default/temp/images/20251113/437aec29cafa798290dbd9f98fa7d3be3c5b01b4.png', // 新专辑封面链接
            //     audioSrc: 'https://example.com/your-new-audio.mp3' // 新音频链接
            // },
        ];

        let currentSongIndex = 0; // 当前播放歌曲的索引

        // --- 函数定义 ---

        // 格式化时间
        function formatTime(seconds) {
            const minutes = Math.floor(seconds / 60);
            const secs = Math.floor(seconds % 60);
            return `${minutes}:${secs < 10 ? '0' : ''}${secs}`;
        }

        // 加载指定索引的歌曲
        function loadSong(index) {
            if (index < 0) { // 如果是上一首，且当前是第一首，则跳到最后一首
                currentSongIndex = songs.length - 1;
            } else if (index >= songs.length) { // 如果是下一首，且当前是最后一首，则跳到第一首
                currentSongIndex = 0;
            } else {
                currentSongIndex = index;
            }

            const song = songs[currentSongIndex];
            
            albumArtImg.src = song.albumArt;
            songTitle.textContent = song.title;
            artistName.textContent = song.artist;
            myAudio.src = song.audioSrc;

            // 重新设置播放器状态
            player.classList.remove('playing');
            playBtn.textContent = '▶';
            progressSlider.value = 0;
            progressSlider.style.setProperty('--current-fill', `0%`);
            currentTimeSpan.textContent = formatTime(0);

            // 更新歌曲列表中的高亮显示
            updateSongListHighlight();
        }

        // 动态生成歌曲列表
        function renderSongList() {
            songListUl.innerHTML = ''; // 清空现有列表
            songs.forEach((song, index) => {
                const li = document.createElement('li');
                li.classList.add('song-list-item');
                if (index === currentSongIndex) {
                    li.classList.add('playing-now');
                }
                li.dataset.index = index; // 存储歌曲索引

                li.innerHTML = `
                    <div class="album-art-sm">
                        <img src="${song.albumArt}" alt="专辑封面">
                    </div>
                    <div class="song-list-text">
                        <div class="song-title-sm">${song.title}</div>
                        <div class="artist-sm">${song.artist}</div>
                    </div>
                `;
                songListUl.appendChild(li);

                // 给每个列表项添加点击事件
                li.addEventListener('click', () => {
                    loadSong(index); // 加载点击的歌曲
                    myAudio.play(); // 播放
                    player.classList.add('playing');
                    playBtn.textContent = '⏸';
                    songListPanel.classList.remove('active'); // 隐藏列表
                });
            });
        }

        // 更新歌曲列表中的高亮显示
        function updateSongListHighlight() {
            document.querySelectorAll('.song-list-item').forEach((item, index) => {
                if (index === currentSongIndex) {
                    item.classList.add('playing-now');
                    // 确保当前播放的歌曲在列表中可见
                    setTimeout(() => {
                        item.scrollIntoView({ behavior: 'smooth', block: 'nearest' });
                    }, 0);
                } else {
                    item.classList.remove('playing-now');
                }
            });
        }

        // --- 事件监听器 ---

        // 播放/暂停功能
        playBtn.addEventListener('click', function() {
            if (myAudio.paused) {
                myAudio.play();
                player.classList.add('playing');
                playBtn.textContent = '⏸';
            } else {
                myAudio.pause();
                player.classList.remove('playing');
                playBtn.textContent = '▶';
            }
        });

        // 上一首
        prevBtn.addEventListener('click', () => {
            loadSong(currentSongIndex - 1);
            if (!myAudio.paused || player.classList.contains('playing')) { // 如果之前在播放，则继续播放
                myAudio.play();
            }
        });

        // 下一首
        nextBtn.addEventListener('click', () => {
            loadSong(currentSongIndex + 1);
            if (!myAudio.paused || player.classList.contains('playing')) {
                myAudio.play();
            }
        });

        // 歌曲列表按钮 - 现在也负责关闭列表
        listBtn.addEventListener('click', () => {
            if (songListPanel.classList.contains('active')) {
                // 如果列表已显示，则关闭它
                songListPanel.classList.remove('active');
            } else {
                // 如果列表未显示，则显示它并重新渲染
                renderSongList();
                songListPanel.classList.add('active');
            }
        });

        // 音乐加载完成后，更新总时长和初始化音量滑块
        myAudio.addEventListener('loadedmetadata', function() {
            durationSpan.textContent = formatTime(myAudio.duration);
            if (myAudio.volume === 1) { 
                myAudio.volume = 0.7;
                volumeSlider.value = 70;
            }
            volumeSlider.style.setProperty('--current-fill', `${volumeSlider.value}%`);
        });

        // 音乐播放时，更新当前时间和进度条
        myAudio.addEventListener('timeupdate', function() {
            const progressPercent = (myAudio.currentTime / myAudio.duration) * 100;
            progressSlider.value = progressPercent;
            progressSlider.style.setProperty('--current-fill', `${progressPercent}%`);
            currentTimeSpan.textContent = formatTime(myAudio.currentTime);
        });

        // 进度条滑动时，改变音乐播放位置
        progressSlider.addEventListener('input', function() {
            const seekTime = (this.value / 100) * myAudio.duration;
            myAudio.currentTime = seekTime;
            this.style.setProperty('--current-fill', `${this.value}%`);
            currentTimeSpan.textContent = formatTime(seekTime);
        });

        // 音乐播放结束时
        myAudio.addEventListener('ended', function() {
            if (currentSongIndex < songs.length - 1) {
                loadSong(currentSongIndex + 1);
                myAudio.play();
                player.classList.add('playing');
                playBtn.textContent = '⏸';
            } else {
                loadSong(0);
                player.classList.remove('playing');
                playBtn.textContent = '▶';
            }
        });

        // 音量滑块滑动时，改变音乐音量
        volumeSlider.addEventListener('input', function() {
            myAudio.volume = this.value / 100;
            this.style.setProperty('--current-fill', `${this.value}%`);
        });

        // --- 初始化 ---
        loadSong(currentSongIndex);
        volumeSlider.value = myAudio.volume * 100;
        volumeSlider.style.setProperty('--current-fill', `${volumeSlider.value}%`);
    </script>
</body>
</html>
```



<!-- title: 沈季白的白日梦 -->
<!-- desc:  -->


 晨雾像一层薄纱，悄无声息地漫过窗帘缝隙，在地板上晕开一片朦胧的白。沈季白是被一缕若有似无的香气勾醒的，那香气很淡，是你常用的那款护发素，带着点清甜的味道，曾无数次萦绕在他的枕畔、鼻尖，成了他衡量安稳的标尺。
 
他没有立刻睁眼，指尖先于意识，轻轻触碰到身侧温热的被褥。柔软的触感传来，带着人体残留的暖意，他心头一松，缓缓睁开眼。
 
你还在熟睡。
 
你侧躺着，长发松松散散地铺在枕头上，几缕碎发落在光洁的额前，随着均匀的呼吸轻轻起伏。嘴角似乎还噙着一丝若有若无的笑意，像是做了什么甜美的梦。
 
沈季白的心跳慢了半拍，眼底瞬间漫上化不开的柔。他不敢动，怕惊扰了这份难得的静谧，只是保持着侧卧的姿势，目光一寸寸描摹着你的眉眼。每一处都刻在他心底最深处，刻了多年，刻得生疼，却也刻得滚烫。
 
他记得第一次见你时，你也是这样笑着，眉眼弯弯，像盛了一整个春天的阳光。
 
 
沈季白的指尖微微颤抖，小心翼翼地伸出手，避开你的脸颊，只轻轻捻起落在枕头上的一缕长发。发丝柔软顺滑，带着那股熟悉的香味，他凑到鼻尖，轻轻嗅闻。香气萦绕在鼻间，真实得不像幻觉，他甚至能感受到发丝拂过皮肤的微凉触感，眼眶忽然就热了。
 

他怕惊扰你，动作轻得像一片羽毛，缓缓掀开被子，蹑手蹑脚地下了床。脚刚落地，就踩进了一双米白色的棉拖里，鞋边绣着小小的绒球，是你去年生日时非要买的。

 沈季白穿着这双带着绒球的拖鞋，轻轻走向厨房。厨房的推拉门是木质的，他怕发出声响，特意放慢了脚步，一点点推开。阳光已经渐渐升高，透过厨房的玻璃窗斜斜淌进来，落在米白色的地砖上，映出细碎的光斑，像撒了一把星星。
 
他开始准备早餐，动作娴熟得像是刻进了骨子里。从冰箱里拿出黄豆，倒进豆浆机，加水，按下开关。嗡嗡的轰鸣声立刻在厨房里响起，不算刺耳，反而带着一种烟火气的安稳。接着，他拿出纯牛奶，倒进小奶锅，开小火慢慢加热。牛奶在锅里渐渐升温，泛起细密的奶泡，氤氲出淡淡的奶香，和豆浆机里飘出的豆香交织在一起，弥漫在整个空间里。
 
他靠在料理台边，听着豆浆机的轰鸣，听着牛奶咕嘟咕嘟的轻响，还有卧室里传来的妻子浅浅的呼吸声。一切都那么平和，那么熟悉，就像你们曾经共度的无数个早晨。

 
沈季白的嘴角不自觉地扬起一个温柔的弧度。他想象着你醒来时的样子，你大概会揉揉惺忪的睡眼，带着刚睡醒的鼻音，从卧室里走出来，看到餐桌上的早餐，眼睛会瞬间亮起来，然后扑到他身边，抱住他的腰，仰着脸夸奖他：“阿白，你做的早餐最香啦！”
 
他甚至能想象到妻子柔软的脸颊贴在他胸膛上的温度，能想象到妻子说话时温热的气息拂过他的皮肤。心脏像是被什么东西填满了，暖暖的，却又带着一丝不易察觉的酸涩。
 
真好啊，他这么想着。

豆浆机的轰鸣声渐渐停了，厨房内一时安静下来，只剩下牛奶在锅里偶尔冒泡的轻响。沈季白关掉火，将豆浆和牛奶分别倒进两个杯子里——杯子是情侣款。
 
他端着杯子，轻轻走向卧室。脚步放得极慢，每一步都像是在珍视着什么。卧室里的阳光比刚才更暖了，落在你的脸上，给你镀上了一层柔和的光晕。你还没醒，呼吸依旧均匀，长长的睫毛偶尔轻轻颤动一下，像是蝴蝶欲飞的翅膀。
 
“宝宝，该吃早饭了。”
 
沈季白走到床边，弯下腰，凑到你的耳边，用只有两人能听到的音量低语。他的声音放得极柔，带着小心翼翼的宠溺，和过去的每一天一样。
 
然而，床上的人没有任何反应，依旧沉睡着。
 
沈季白笑了笑，眼底的温柔更甚。他知道你睡觉沉，便伸出手，轻轻拂去你额前的碎发，然后低下头，唇瓣朝着额头凑去。他想给你一个早安吻，就像过去的无数个早晨那样，带着豆浆和牛奶的香气，带着他满心的爱意。
 
沈季白笑着睁开眼。


这里不是温暖的，充满妻子味道的卧室。


这里是墓地。


他的额头刚刚磕在了妻子的墓碑上。
```html
<!DOCTYPE html>
<html lang="zh-CN">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Apple风格音乐播放器</title>
    <style>
        /* 通用样式重置和字体设置 */
        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
            font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
            color: white; /* 设置默认文本颜色为白色 */
        }

        /* 页面主体样式 - 浅灰色背景 */
        body {
            padding: 40px;
            display: flex; /* 使用flexbox居中播放器 */
            justify-content: center;
            align-items: center;
            min-height: 100vh; /* 确保body至少占满视口高度 */
        }

        /* 新增：包裹播放器和歌曲列表的容器 */
        /* 这个容器现在是垂直方向的Flexbox */
        .player-and-list-wrapper {
            display: flex;
            flex-direction: column; /* 垂直堆叠播放器和列表 */
            align-items: center; /* 水平居中 */
            gap: 25px; /* 播放器和列表之间的垂直间距 */
            position: relative; /* 列表按钮需要相对定位到播放器内部，而非这里 */
            max-width: 320px; /* 整体最大宽度由播放器决定 */
            /* overflow: hidden; /* 仅在必要时使用，以免裁剪过度 */
        }

        /* 播放器容器样式 - 固定宽度，并移除阴影 */
        .player {
            width: 320px; /* 固定宽度，点击列表前后尺寸不变 */
            flex-shrink: 0; /* 防止播放器在flex容器中缩小 */
            background: rgba(0, 0, 0, 0.15); /* 半透明黑色背景 */
            backdrop-filter: blur(20px); /* 背景模糊效果 */
            border-radius: 24px;
            padding: 25px; /* 稍微缩小内边距 */
            border: 1px solid rgba(255, 255, 255, 0.05); /* 替换为内部边框效果 */
            color: white;
            position: relative; /* 相对定位，用于伪元素和歌曲列表按钮 */
            overflow: hidden; /* 隐藏超出部分，用于伪元素和按钮的圆角裁剪 */
        }

        /* 增强发光效果的伪元素 */
        .player::before {
            content: '';
            position: absolute;
            top: -10px;
            left: -10px;
            right: -10px;
            bottom: -10px;
            border-radius: 34px;
            background: linear-gradient(45deg,
                      rgba(255,255,255,0.05) 0%,
                      rgba(255,255,255,0) 50%);
            z-index: -1;
            filter: blur(15px);
        }

        /* 歌曲列表按钮 - 放置在播放器右上角 */
        .list-btn {
            position: absolute;
            top: 20px; /* 距离顶部 */
            right: 20px; /* 距离右侧 */
            background: none;
            border: none;
            color: white; /* 纯白色图标 */
            font-size: 28px; /* 大小适中 */
            cursor: pointer;
            transition: transform 0.2s cubic-bezier(0.2, 0.8, 0.2, 1);
            line-height: 1;
            padding: 0;
            width: 28px; /* 稍微缩小 */
            height: 28px;
            display: flex;
            justify-content: center;
            align-items: center;
            z-index: 10; /* 确保在其他内容之上 */
            -webkit-tap-highlight-color: transparent;
        }

        /* 列表按钮图标样式 */
        .list-btn span {
            display: block;
            width: 20px; /* 稍微缩小 */
            height: 2px;
            background: white;
            position: relative;
            box-shadow: 0 5px 0 white, 0 -5px 0 white; /* 稍微缩小间距 */
            border-radius: 1px;
        }

        .list-btn:hover {
            transform: scale(1.1);
            opacity: 0.9;
        }

        /* 专辑封面容器样式 - 移除阴影 */
        .album-art {
            width: 180px; /* 缩小专辑封面尺寸 */
            height: 180px; /* 缩小专辑封面尺寸 */
