<!DOCTYPE html>
<html lang="zh-CN">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Apple风格音乐播放器</title>
    <style>
        /* 通用样式重置和字体设置 */
        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
            font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
            color: white; /* 设置默认文本颜色为白色 */
        }

        /* 页面主体样式 - 浅灰色背景 */
        body {
            padding: 40px;
            display: flex; /* 使用flexbox居中播放器 */
            justify-content: center;
            align-items: center;
            min-height: 100vh; /* 确保body至少占满视口高度 */
        }

        /* 新增：包裹播放器和歌曲列表的容器 */
        /* 这个容器现在是垂直方向的Flexbox */
        .player-and-list-wrapper {
            display: flex;
            flex-direction: column; /* 垂直堆叠播放器和列表 */
            align-items: center; /* 水平居中 */
            gap: 25px; /* 播放器和列表之间的垂直间距 */
            position: relative; /* 列表按钮需要相对定位到播放器内部，而非这里 */
            max-width: 320px; /* 整体最大宽度由播放器决定 */
            /* overflow: hidden; /* 仅在必要时使用，以免裁剪过度 */
        }

        /* 播放器容器样式 - 固定宽度，并移除阴影 */
        .player {
            width: 320px; /* 固定宽度，点击列表前后尺寸不变 */
            flex-shrink: 0; /* 防止播放器在flex容器中缩小 */
            background: rgba(0, 0, 0, 0.15); /* 半透明黑色背景 */
            backdrop-filter: blur(20px); /* 背景模糊效果 */
            border-radius: 24px;
            padding: 25px; /* 稍微缩小内边距 */
            border: 1px solid rgba(255, 255, 255, 0.05); /* 替换为内部边框效果 */
            color: white;
            position: relative; /* 相对定位，用于伪元素和歌曲列表按钮 */
            overflow: hidden; /* 隐藏超出部分，用于伪元素和按钮的圆角裁剪 */
        }

        /* 增强发光效果的伪元素 */
        .player::before {
            content: '';
            position: absolute;
            top: -10px;
            left: -10px;
            right: -10px;
            bottom: -10px;
            border-radius: 34px;
            background: linear-gradient(45deg,
                      rgba(255,255,255,0.05) 0%,
                      rgba(255,255,255,0) 50%);
            z-index: -1;
            filter: blur(15px);
        }

        /* 歌曲列表按钮 - 放置在播放器右上角 */
        .list-btn {
            position: absolute;
            top: 20px; /* 距离顶部 */
            right: 20px; /* 距离右侧 */
            background: none;
            border: none;
            color: white; /* 纯白色图标 */
            font-size: 28px; /* 大小适中 */
            cursor: pointer;
            transition: transform 0.2s cubic-bezier(0.2, 0.8, 0.2, 1);
            line-height: 1;
            padding: 0;
            width: 28px; /* 稍微缩小 */
            height: 28px;
            display: flex;
            justify-content: center;
            align-items: center;
            z-index: 10; /* 确保在其他内容之上 */
            -webkit-tap-highlight-color: transparent;
        }

        /* 列表按钮图标样式 */
        .list-btn span {
            display: block;
            width: 20px; /* 稍微缩小 */
            height: 2px;
            background: white;
            position: relative;
            box-shadow: 0 5px 0 white, 0 -5px 0 white; /* 稍微缩小间距 */
            border-radius: 1px;
        }

        .list-btn:hover {
            transform: scale(1.1);
            opacity: 0.9;
        }

        /* 专辑封面容器样式 - 移除阴影 */
        .album-art {
            width: 180px; /* 缩小专辑封面尺寸 */
            height: 180px; /* 缩小专辑封面尺寸 */
            border-radius: 50%;
            margin: 0 auto 20px; /* 缩小外边距 */
            overflow: hidden;
            animation: rotateAlbumArt 15s linear infinite paused;
            border: 2px solid rgba(255, 255, 255, 0.1);
            flex-shrink: 0;
        }

        /* 播放状态下专辑封面动画运行 */
        .player.playing .album-art {
            animation-play-state: running;
        }

        /* 专辑封面图片样式 */
        .album-art img {
            width: 100%;
            height: 100%;
            object-fit: cover;
        }

        /* 歌曲信息容器样式 */
        .song-info {
            text-align: center;
            margin-bottom: 25px; /* 缩小外边距 */
        }

        /* 歌曲标题样式 */
        .song-title {
            font-size: 22px; /* 稍微缩小字体 */
            font-weight: 600;
            margin-bottom: 6px;
            text-shadow: 0 2px 10px rgba(0, 0, 0, 0.3); /* 保持文字阴影，增强可读性 */
            white-space: nowrap;
            overflow: hidden;
            text-overflow: ellipsis;
        }

        /* 艺术家姓名样式 */
        .artist {
            font-size: 15px; /* 稍微缩小字体 */
            color: rgba(255, 255, 255, 0.8);
            text-shadow: 0 1px 5px rgba(0, 0, 0, 0.2); /* 保持文字阴影，增强可读性 */
            white-space: nowrap;
            overflow: hidden;
            text-overflow: ellipsis;
        }

        /* 统一滑块的CSS变量定义 */
        .slider-base {
            -webkit-appearance: none;
            width: 100%;
            height: 5px; /* 缩小滑块高度 */
            background: transparent;
            border-radius: 3px;
            cursor: pointer;
            outline: none;
            overflow: hidden;
            margin: 0;
            
            /* 定义通用滑块样式变量 */
            --fill-color: white;
            --track-bg-color: rgba(255, 255, 255, 0.2);
            --thumb-size: 12px; /* 缩小滑块圆点大小 */
            --thumb-shadow: 0 2px 5px rgba(0, 0, 0, 0.2);
            --thumb-margin-top: -3.5px; /* 调整垂直居中 */
        }

        /* 统一滑块轨道样式 */
        .slider-base::-webkit-slider-runnable-track {
            -webkit-appearance: none;
            width: 100%;
            height: 5px;
            border-radius: 3px;
            background: linear-gradient(to right, 
                        var(--fill-color), var(--fill-color)) 0% 0% / var(--current-fill) 100% no-repeat,
                        var(--track-bg-color) 100% 0% / calc(100% - var(--current-fill)) 100% no-repeat;
            transition: background-size 0.1s linear;
        }

        /* 统一滑块圆点样式 */
        .slider-base::-webkit-slider-thumb {
            -webkit-appearance: none;
            width: var(--thumb-size);
            height: var(--thumb-size);
            background: white;
            border-radius: 50%;
            margin-top: var(--thumb-margin-top);
            box-shadow: var(--thumb-shadow);
            transition: transform 0.2s cubic-bezier(0.2, 0.8, 0.2, 1), box-shadow 0.2s cubic-bezier(0.2, 0.8, 0.2, 1);
        }

        /* 统一滑块圆点悬停效果 */
        .slider-base::-webkit-slider-thumb:hover {
            transform: scale(1.1);
            box-shadow: 0 3px 8px rgba(0, 0, 0, 0.3);
        }

        /* 播放进度条滑块样式 - 继承通用滑块样式 */
        .progress-slider {
            margin-bottom: 20px; /* 缩小外边距 */
            --current-fill: 0%;
        }

        /* 时间信息容器样式 */
        .time-info {
            display: flex;
            justify-content: space-between;
            font-size: 11px; /* 缩小字体 */
            color: rgba(255, 255, 255, 0.7);
            margin-bottom: 25px; /* 缩小外边距 */
        }

        /* 控件按钮容器样式 */
        .controls {
            display: flex;
            justify-content: center;
            align-items: center;
            gap: 25px; /* 缩小间距 */
        }

        /* 上一首/下一首按钮基础样式 */
        .control-btn {
            background: none;
            border: none;
            color: rgba(255, 255, 255, 0.7);
            font-size: 26px; /* 稍微缩小字体 */
            cursor: pointer;
            transition: all 0.2s cubic-bezier(0.2, 0.8, 0.2, 1);
            padding: 5px;
            line-height: 1;
            -webkit-tap-highlight-color: transparent;
        }

        /* 上一首/下一首按钮悬停效果 */
        .control-btn:hover {
            color: white;
            transform: scale(1.05);
        }

        /* 播放/暂停按钮样式 - 与其他按钮UI尺寸一致 */
        .play-btn {
            font-size: 26px;
            padding: 0 5px;
        }

        /* 音量控制容器样式 */
        .volume-control {
            display: flex;
            align-items: center;
            gap: 8px; /* 缩小间距 */
            margin-top: 20px; /* 缩小外边距 */
        }

        /* 音量图标样式 */
        .volume-icon {
            font-size: 18px; /* 缩小音量图标大小 */
            color: rgba(255, 255, 255, 0.7);
            line-height: 1;
        }

        /* 音量调节滑块样式 (input[type="range"]) - 继承通用滑块样式 */
        .volume-slider {
            --current-fill: 70%;
        }

        /* 专辑封面旋转动画 */
        @keyframes rotateAlbumArt {
            from { transform: rotate(0deg); }
            to { transform: rotate(360deg); }
        }

        /* --- 歌曲列表面板样式 --- */

        /* 歌曲列表面板 - 垂直出现，只显示两首歌曲高度，无内容形变 */
        .song-list-panel {
            width: 320px; /* 与播放器宽度一致 */
            /* 预估高度：标题(22px+15mb) + 2首歌曲(2*(45px+2*10px padding+4px mb)) + 2*25px (panel padding) = ~225px */
            height: 225px; /* 固定高度，确保内部内容不变形 */
            flex-shrink: 0; /* 防止列表在Flex容器中收缩 */
            background: rgba(255, 255, 255, 0.15); /* 浅灰色半透明背景 */
            backdrop-filter: blur(20px); /* 模糊效果 */
            border-radius: 24px; /* 匹配播放器圆角 */
            border: 1px solid rgba(255, 255, 255, 0.05); /* 替换为内部边框效果 */
            padding: 25px; /* 列表面板内部的内边距 */
            display: flex;
            flex-direction: column;
            overflow: hidden; /* 隐藏超出滚动区域的内容 */

            /* 隐藏状态：完全透明，不可见，并通过 transform 向下平移 */
            opacity: 0;
            visibility: hidden;
            /* 向下平移自身高度 + 间距，使其初始位置在播放器下方 */
            transform: translateY(calc(100% + 25px)); 

            /* 动画过渡：更精细的控制 visibility 和 easing */
            /* max-height 也包含在内，但它会从 0 展开到固定 height，
               transform 的视觉滑动是主导 */
            transition: transform 0.5s cubic-bezier(0.2, 0.8, 0.2, 1), /* 动画曲线 */
                        opacity 0.5s cubic-bezier(0.2, 0.8, 0.2, 1),
                        visibility 0s linear 0.5s, /* 隐藏在动画结束时 */
                        max-height 0.5s cubic-bezier(0.2, 0.8, 0.2, 1); /* 同步 max-height 动画 */
            will-change: transform, opacity, max-height; /* 提升动画性能 */
            
            /* 当 max-height 为 0 时，内容会被裁剪。这里是隐藏时的额外控制 */
            max-height: 0; /* 默认隐藏时高度为0 */
        }

        /* 歌曲列表面板激活状态 */
        .song-list-panel.active {
            opacity: 1;
            visibility: visible; /* 立即显示 */
            transform: translateY(0); /* 向上滑动到其自然位置 */
            max-height: 225px; /* 展开到固定高度 */

            transition: transform 0.5s cubic-bezier(0.2, 0.8, 0.2, 1),
                        opacity 0.5s cubic-bezier(0.2, 0.8, 0.2, 1),
                        visibility 0s linear 0s, /* 立即显示 */
                        max-height 0.5s cubic-bezier(0.2, 0.8, 0.2, 1);
        }
        
        /* 确保隐藏时内部内容完全折叠 */
        /* 由于我们使用固定的 height 和 padding，这些规则是为了防止意外的可见性 */
        .song-list-panel:not(.active) h2,
        .song-list-panel:not(.active) .song-list-item {
            /* 强制隐藏子元素，因为父元素的 overflow:hidden 只能裁剪，不能让内容不可见 */
            opacity: 0;
            pointer-events: none; /* 禁用点击事件 */
            transition: opacity 0.1s; /* 让子元素也淡出 */
        }

        /* 歌曲列表标题 */
        .song-list-panel h2 {
            font-size: 22px;
            font-weight: 600;
            text-align: center;
            margin-bottom: 15px;
            text-shadow: 0 2px 10px rgba(0, 0, 0, 0.3);
            flex-shrink: 0;
        }

        /* 歌曲列表 ul */
        .song-list {
            list-style: none;
            flex-grow: 1;
            overflow-y: auto; /* 列表过长时滚动 */
            -webkit-overflow-scrolling: touch;
            padding-right: 5px;
        }
        /* 美化滚动条 (仅 Webkit 浏览器) */
        .song-list::-webkit-scrollbar {
            width: 6px;
        }
        .song-list::-webkit-scrollbar-track {
            background: rgba(255, 255, 255, 0.05);
            border-radius: 3px;
        }
        .song-list::-webkit-scrollbar-thumb {
            background-color: rgba(255, 255, 255, 0.2);
            border-radius: 3px;
            transition: background-color 0.2s ease;
        }
        .song-list::-webkit-scrollbar-thumb:hover {
            background-color: rgba(255, 255, 255, 0.3);
        }

        /* 歌曲列表项 */
        .song-list-item {
            display: flex;
            align-items: center;
            padding: 10px 12px;
            border-bottom: 1px solid rgba(255, 255, 255, 0.08); /* 分隔线透明度降低 */
            cursor: pointer;
            transition: background-color 0.2s ease, transform 0.1s ease;
            border-radius: 8px;
            margin-bottom: 4px;
        }

        .song-list-item:last-child {
            border-bottom: none;
        }

        .song-list-item:hover {
            background-color: rgba(255, 255, 255, 0.08);
            transform: translateY(-2px);
        }

        /* 当前播放歌曲的高亮效果 - 移除阴影 */
        .song-list-item.playing-now {
            background-color: rgba(255, 255, 255, 0.15);
            border-color: rgba(255, 255, 255, 0.2);
            position: relative;
        }

        /* 列表项中的专辑封面 - 移除阴影 */
        .song-list-item .album-art-sm {
            width: 45px;
            height: 45px;
            border-radius: 50%;
            overflow: hidden;
            margin-right: 12px;
            flex-shrink: 0;
            border: 1px solid rgba(255, 255, 255, 0.08);
        }

        .song-list-item .album-art-sm img {
            width: 100%;
            height: 100%;
            object-fit: cover;
        }

        /* 列表项中的文本信息 */
        .song-list-text {
            flex-grow: 1;
            min-width: 0;
        }

        .song-list-text .song-title-sm {
            font-size: 15px;
            font-weight: 500;
            margin-bottom: 2px;
            white-space: nowrap;
            overflow: hidden;
            text-overflow: ellipsis;
        }

        /* 列表项的文本内容在隐藏时完全不可见 */
        .song-list-panel:not(.active) .song-list-text .song-title-sm,
        .song-list-panel:not(.active) .song-list-text .artist-sm {
            line-height: 0; /* 确保文字行高也为0 */
            height: 0;
            margin: 0;
        }

        .song-list-text .artist-sm {
            font-size: 12px;
            color: rgba(255, 255, 255, 0.7);
            white-space: nowrap;
            overflow: hidden;
            text-overflow: ellipsis;
        }
    </style>
</head>
<body>
    <div class="player-and-list-wrapper">
        <!-- 歌曲列表面板现在位于播放器上方 -->
        <div class="song-list-panel">
            <h2>歌曲列表</h2>
            <ul class="song-list">
                <!-- 歌曲列表项将由 JavaScript 动态生成 -->
            </ul>
        </div>

        <div class="player">
            <div class="album-art">
                <img src="" alt="专辑封面">
            </div>
            
            <div class="song-info">
                <h1 class="song-title"></h1>
                <p class="artist"></p>
            </div>
            
            <!-- 播放进度条滑块 -->
            <input type="range" class="progress-slider slider-base" min="0" max="100" value="0">
            
            <div class="time-info">
                <span class="current-time">0:00</span>
                <span class="duration">0:00</span>
            </div>
            
            <div class="controls">
                <button class="control-btn prev-btn">⏮</button>
                <button class="control-btn play-btn">▶</button>
                <button class="control-btn next-btn">⏭</button>
            </div>
            
            <div class="volume-control">
                <span class="volume-icon">🔈</span> 
                <input type="range" class="volume-slider slider-base" min="0" max="100" value="70">
            </div>

            <!-- 歌曲列表按钮 -->
            <button class="list-btn"><span></span></button>
        </div>
    </div>

    <!-- 音乐来源 -->
    <audio id="myAudio"></audio>

    <script>
        // --- DOM 元素获取 ---
        const playerAndListWrapper = document.querySelector('.player-and-list-wrapper');
        const player = document.querySelector('.player');
        const albumArtImg = document.querySelector('.album-art img');
        const songTitle = document.querySelector('.song-title');
        const artistName = document.querySelector('.artist');
        const playBtn = document.querySelector('.play-btn');
        const prevBtn = document.querySelector('.prev-btn');
        const nextBtn = document.querySelector('.next-btn');
        const listBtn = document.querySelector('.list-btn'); // 歌曲列表按钮
        const progressSlider = document.querySelector('.progress-slider');
        const volumeSlider = document.querySelector('.volume-slider');
        const currentTimeSpan = document.querySelector('.current-time');
        const durationSpan = document.querySelector('.duration');
        const myAudio = document.getElementById('myAudio');

        const songListPanel = document.querySelector('.song-list-panel'); // 歌曲列表面板
        const songListUl = document.querySelector('.song-list');

        // --- 歌曲数据 (在这里添加/修改歌曲信息) ---
        const songs = [
            {
                title: '恋人',
                artist: '˚˖ 𓂂ꔫ Music ⊹꙳◦',
                albumArt: 'https://xiaoiwg.dongfeng-nissan.com.cn/aicc-workbench/res/download/default/temp/images/20251113/437aec29cafa798290dbd9f98fa7d3be3c5b01b4.png',
                audioSrc: 'https://files.catbox.moe/j127nd.mp3'
            },
            {
                title: 'HANABI',
                artist: '˚˖ 𓂂ꔫ Music ⊹꙳◦',
                albumArt: 'https://xiaoiwg.dongfeng-nissan.com.cn/aicc-workbench/res/download/default/temp/images/20251113/437aec29cafa798290dbd9f98fa7d3be3c5b01b4.png',
                audioSrc: 'https://files.catbox.moe/65mmn2.mp3'
            },
            {
                title: '乌托邦',
                artist: '˚˖ 𓂂ꔫ Music ⊹꙳◦',
                albumArt: 'https://xiaoiwg.dongfeng-nissan.com.cn/aicc-workbench/res/download/default/temp/images/20251113/437aec29cafa798290dbd9f98fa7d3be3c5b01b4.png',
                audioSrc: 'https://files.catbox.moe/u9h0dh.mp3'
            },
            {
                title: 'time machine',
                artist: '˚˖ 𓂂ꔫ Music ⊹꙳◦',
                albumArt: 'https://xiaoiwg.dongfeng-nissan.com.cn/aicc-workbench/res/download/default/temp/images/20251113/437aec29cafa798290dbd9f98fa7d3be3c5b01b4.png',
                audioSrc: 'https://files.catbox.moe/knlt98.mp3'
            },
            {
                title: '劳伦斯先生圣诞快乐',
                artist: '˚˖ 𓂂ꔫ Music ⊹꙳◦',
                albumArt: 'https://xiaoiwg.dongfeng-nissan.com.cn/aicc-workbench/res/download/default/temp/images/20251113/437aec29cafa798290dbd9f98fa7d3be3c5b01b4.png',
                audioSrc: 'https://files.catbox.moe/gw8cv1.mp3'
            },
            // 你可以在这里继续添加更多歌曲
            // 示例：
            // {
            //     title: '你的新歌曲标题',
            //     artist: '你的新艺术家',
            //     albumArt: 'https://xiaoiwg.dongfeng-nissan.com.cn/aicc-workbench/res/download/default/temp/images/20251113/437aec29cafa798290dbd9f98fa7d3be3c5b01b4.png', // 新专辑封面链接
            //     audioSrc: 'https://example.com/your-new-audio.mp3' // 新音频链接
            // },
        ];

        let currentSongIndex = 0; // 当前播放歌曲的索引

        // --- 函数定义 ---

        // 格式化时间
        function formatTime(seconds) {
            const minutes = Math.floor(seconds / 60);
            const secs = Math.floor(seconds % 60);
            return `${minutes}:${secs < 10 ? '0' : ''}${secs}`;
        }

        // 加载指定索引的歌曲
        function loadSong(index) {
            if (index < 0) { // 如果是上一首，且当前是第一首，则跳到最后一首
                currentSongIndex = songs.length - 1;
            } else if (index >= songs.length) { // 如果是下一首，且当前是最后一首，则跳到第一首
                currentSongIndex = 0;
            } else {
                currentSongIndex = index;
            }

            const song = songs[currentSongIndex];
            
            albumArtImg.src = song.albumArt;
            songTitle.textContent = song.title;
            artistName.textContent = song.artist;
            myAudio.src = song.audioSrc;

            // 重新设置播放器状态
            player.classList.remove('playing');
            playBtn.textContent = '▶';
            progressSlider.value = 0;
            progressSlider.style.setProperty('--current-fill', `0%`);
            currentTimeSpan.textContent = formatTime(0);

            // 更新歌曲列表中的高亮显示
            updateSongListHighlight();
        }

        // 动态生成歌曲列表
        function renderSongList() {
            songListUl.innerHTML = ''; // 清空现有列表
            songs.forEach((song, index) => {
                const li = document.createElement('li');
                li.classList.add('song-list-item');
                if (index === currentSongIndex) {
                    li.classList.add('playing-now');
                }
                li.dataset.index = index; // 存储歌曲索引

                li.innerHTML = `
                    <div class="album-art-sm">
                        <img src="${song.albumArt}" alt="专辑封面">
                    </div>
                    <div class="song-list-text">
                        <div class="song-title-sm">${song.title}</div>
                        <div class="artist-sm">${song.artist}</div>
                    </div>
                `;
                songListUl.appendChild(li);

                // 给每个列表项添加点击事件
                li.addEventListener('click', () => {
                    loadSong(index); // 加载点击的歌曲
                    myAudio.play(); // 播放
                    player.classList.add('playing');
                    playBtn.textContent = '⏸';
                    songListPanel.classList.remove('active'); // 隐藏列表
                });
            });
        }

        // 更新歌曲列表中的高亮显示
        function updateSongListHighlight() {
            document.querySelectorAll('.song-list-item').forEach((item, index) => {
                if (index === currentSongIndex) {
                    item.classList.add('playing-now');
                    // 确保当前播放的歌曲在列表中可见
                    setTimeout(() => {
                        item.scrollIntoView({ behavior: 'smooth', block: 'nearest' });
                    }, 0);
                } else {
                    item.classList.remove('playing-now');
                }
            });
        }

        // --- 事件监听器 ---

        // 播放/暂停功能
        playBtn.addEventListener('click', function() {
            if (myAudio.paused) {
                myAudio.play();
                player.classList.add('playing');
                playBtn.textContent = '⏸';
            } else {
                myAudio.pause();
                player.classList.remove('playing');
                playBtn.textContent = '▶';
            }
        });

        // 上一首
        prevBtn.addEventListener('click', () => {
            loadSong(currentSongIndex - 1);
            if (!myAudio.paused || player.classList.contains('playing')) { // 如果之前在播放，则继续播放
                myAudio.play();
            }
        });

        // 下一首
        nextBtn.addEventListener('click', () => {
            loadSong(currentSongIndex + 1);
            if (!myAudio.paused || player.classList.contains('playing')) {
                myAudio.play();
            }
        });

        // 歌曲列表按钮 - 现在也负责关闭列表
        listBtn.addEventListener('click', () => {
            if (songListPanel.classList.contains('active')) {
                // 如果列表已显示，则关闭它
                songListPanel.classList.remove('active');
            } else {
                // 如果列表未显示，则显示它并重新渲染
                renderSongList();
                songListPanel.classList.add('active');
            }
        });

        // 音乐加载完成后，更新总时长和初始化音量滑块
        myAudio.addEventListener('loadedmetadata', function() {
            durationSpan.textContent = formatTime(myAudio.duration);
            if (myAudio.volume === 1) { 
                myAudio.volume = 0.7;
                volumeSlider.value = 70;
            }
            volumeSlider.style.setProperty('--current-fill', `${volumeSlider.value}%`);
        });

        // 音乐播放时，更新当前时间和进度条
        myAudio.addEventListener('timeupdate', function() {
            const progressPercent = (myAudio.currentTime / myAudio.duration) * 100;
            progressSlider.value = progressPercent;
            progressSlider.style.setProperty('--current-fill', `${progressPercent}%`);
            currentTimeSpan.textContent = formatTime(myAudio.currentTime);
        });

        // 进度条滑动时，改变音乐播放位置
        progressSlider.addEventListener('input', function() {
            const seekTime = (this.value / 100) * myAudio.duration;
            myAudio.currentTime = seekTime;
            this.style.setProperty('--current-fill', `${this.value}%`);
            currentTimeSpan.textContent = formatTime(seekTime);
        });

        // 音乐播放结束时
        myAudio.addEventListener('ended', function() {
            if (currentSongIndex < songs.length - 1) {
                loadSong(currentSongIndex + 1);
                myAudio.play();
                player.classList.add('playing');
                playBtn.textContent = '⏸';
            } else {
                loadSong(0);
                player.classList.remove('playing');
                playBtn.textContent = '▶';
            }
        });

        // 音量滑块滑动时，改变音乐音量
        volumeSlider.addEventListener('input', function() {
            myAudio.volume = this.value / 100;
            this.style.setProperty('--current-fill', `${this.value}%`);
        });

        // --- 初始化 ---
        loadSong(currentSongIndex);
        volumeSlider.value = myAudio.volume * 100;
        volumeSlider.style.setProperty('--current-fill', `${volumeSlider.value}%`);
    </script>
</body>
</html>
```

<!-- title:心理治疗  -->
<!-- desc:  -->


午后的阳光斜着穿过窗户，在浅色的木地板上切割出几道明亮的条纹。空气里浮动着细小的尘埃，和加湿器喷出的、带着淡淡柑橘香气的薄雾。

陈聿霖将一杯温水推到对面，看着沈季白接过去。他的手指很干净，骨节分明，无名指上的婚戒在光线下折射出一道柔和却清晰的金属光泽。那上面有一道不甚明显的划痕，像是长久佩戴中无意间磕碰留下的印记。

一年前，这个男人第一次坐在这里时，整个人像一座被抽空了内里的建筑，只剩下摇摇欲坠的框架。他的眼睛里没有任何东西，那种空洞几乎能把人的视线一并吸进去。而现在，他至少会笑了。

“嗯……有几个学生的论文写得很好。”沈季白轻轻开口，声音是温润的男中音，语速平缓，一如既往。他嘴角噙着一抹淡得几乎看不见的笑意，视线落在桌面上那几道光斑上，没有聚焦。

“是吗？”陈聿霖点点头，身体微微前倾，做出认真倾听的姿态，他没有接着问“你呢”，而是顺着对方的话题聊了下去，“可以说说吗？什么主题的论文让沈教授觉得不错。”

他知道，对于沈季白这样的人，直接探问情绪的深井，只会让他把井盖封得更严实。不如就站在井边，聊聊天气，聊聊风景，等他自己愿意的时候，或许会露出一点缝隙。

“有一篇是关于晚明小品文的流变，”沈季白似乎真的在认真思考这个问题，他稍微坐直了一些，目光终于从光斑上移开，落在了陈聿霖的脸上，“那个学生找到的切入点很有意思，他认为张岱的叙事风格，其实是一种对‘物是人非’这个母题的刻意疏离，用一种近乎炫耀的笔触去写昔日繁华，文字越是热闹，内里的悲凉就越是刺骨。”

他说得很流畅，条理清晰，像是正在进行一场小型的学术研讨。这是他最熟悉的领域，是他可以完全掌控的安全区。在这里，所有的情感都可以被转化为文本分析，所有的悲欢离合都能被结构、解构，然后归档。

陈聿霖安静地听着，没有打断。他注意到，沈季白在说到“物是人非”四个字时，端着水杯的手指几不可查地收紧了一下，指腹在那枚戒指的边缘无意识地来回摩挲。一个非常细微的动作，几乎立刻就消失了。

“听起来确实很深刻。”陈聿霖给予了肯定的回应，他拿起自己的那杯水，却没有喝，只是握在手里感受着温度，“用极致的热闹去反衬寂灭，是很高明的手法。不过，写的人或许觉得高明，读的人……恐怕会觉得更残忍。”

沈季白嘴角的笑意淡去了，他没有反驳，只是垂下眼帘，长长的睫毛在眼下投下一小片阴影。“或许吧。但总得写下来。不写，不代表它就不存在。”他的声音很轻，像是在说服自己。

咨询室里安静了几秒，只有加湿器运作的微弱声响。

“你最近……睡得怎么样？”陈聿霖终于还是将话题从那些故纸堆里拉了回来，他的语气非常温和，像是在询问一件再平常不过的日常小事。

沈季白沉默了片刻，似乎在回忆。“还可以。”他回答，然后像是为了增加可信度，又补充了一句，“至少不会在半夜醒来。我睡前会看会儿书，开着床头那盏暖光的灯，看着看着就睡着了。”

“嗯，这是个好习惯。”陈聿天应着，一边在本子上写着什么。他的笔尖划过纸张，发出沙沙的轻响。“早餐呢？还是豆浆和油条吗？”

这是他们之前聊过的话题。沈季白偏爱中式早餐，而他的妻子……陈聿霖记得，每天早上一杯热牛奶。

“嗯。”沈季白应了一声，视线再次飘远，落在了窗外那棵被阳光照得透亮的银杏树上。秋天已经很深了，叶子黄得像金箔。

“还是会准备两份吗？”陈聿霖问得很直接，但声音放得更轻了。

这个问题像一把没有鞘的刀，安静地摆在了桌面上。沈季白没有立刻回答，他看着窗外，过了很久，久到陈聿霖以为他不会再开口时，才听到他极轻的声音。

“……习惯了。”他说，像是在陈述一个物理定律，“热豆浆的时候，顺手就把牛奶也放进去了。摆餐具的时候，也是。好像……身体已经记住了这个流程，脑子反应不过来。”

他没有说为谁准备，也没有提任何名字。但他口中那个温热的词汇——“牛奶”，却比任何名字都更具体，更滚烫。它和“豆浆”并列在一起，构成了一个再也无法完整的清晨。

“你的意思是，这些行为已经变成了无意识的动作，对吗？”陈聿霖换了一种更中性的说法，将情感的部分剥离出去，只留下行为本身，“就像我们每天会下意识地刷牙、洗脸一样，它成了你身体节律的一部分。”

“嗯。”沈季白低低地应了一声，他终于端起水杯，喝了一小口温水。喉结上下滚动了一下，他似乎想说什么，但最终只是抿了抿唇。

他放在膝盖上的另一只手，不知何时已经攥成了拳，但很快又松开了。

“季白，”陈聿霖叫了他的名字，语气里多了一分朋友间的温存，而不仅仅是医生对患者的称呼，“你觉得，‘习惯’本身，是一种保护，还是一种……囚禁？”

沈季白抬起头，看向陈聿霖。他的眼睛里没有了初见时的那种死寂，但也没有太多光彩，像蒙着一层薄薄的雾气。他认真地思考着这个问题，眉头微蹙。

“我不知道。”他诚实地摇了摇头，声音里带着一丝自己都未曾察觉的疲惫，“我只知道，如果有一天，我早上起来，只给自己热了一杯豆浆……那可能比什么都让我害怕。”

害怕改变，害怕承认那个空缺是真的存在，害怕亲手打破那个由习惯构筑的、虚假的“完整”。

陈聿霖没有再追问。他只是静静地看着沈季白，目光里带着理解和悲悯。他知道，今天的谈话，到这里就足够了。有些伤口，只能让时间慢慢去舔舐，任何外力的介入，都可能让它撕裂得更深。

他合上了自己的笔记本，发出“啪”的一声轻响。

“时间差不多了。”陈聿霖站起身，走到窗边，将百叶窗的角度调大了一些，让更多的阳光照进室内，驱散那份挥之不去的阴郁。“下周还是老时间？”

“好。”沈季白也跟着站起来，他将那杯基本没怎么动过的温水放回桌上，冲着陈聿霖点点头，露出一个礼貌而疏离的微笑，“麻烦你了，陈医生。”

“不麻烦。”陈聿霖也笑了笑，他送沈季白到门口，看着他穿上那件浅灰色的风衣，身形清瘦挺拔，一如往常。

门被轻轻带上，隔绝了室内的温暖。走廊里的光线有些昏暗，沈季白独自站在那里，低头整理了一下自己的衣领，然后迈开脚步，走向那片被秋日阳光铺满的、没有尽头的长路。
```html
<!DOCTYPE html>
<html lang="zh-CN">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Apple风格音乐播放器</title>
    <style>
        /* 通用样式重置和字体设置 */
        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
            font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
            color: white; /* 设置默认文本颜色为白色 */
        }

        /* 页面主体样式 - 浅灰色背景 */
        body {
            padding: 40px;
            display: flex; /* 使用flexbox居中播放器 */
            justify-content: center;
            align-items: center;
            min-height: 100vh; /* 确保body至少占满视口高度 */
        }

        /* 新增：包裹播放器和歌曲列表的容器 */
        /* 这个容器现在是垂直方向的Flexbox */
        .player-and-list-wrapper {
            display: flex;
            flex-direction: column; /* 垂直堆叠播放器和列表 */
            align-items: center; /* 水平居中 */
            gap: 25px; /* 播放器和列表之间的垂直间距 */
            position: relative; /* 列表按钮需要相对定位到播放器内部，而非这里 */
            max-width: 320px; /* 整体最大宽度由播放器决定 */
            /* overflow: hidden; /* 仅在必要时使用，以免裁剪过度 */
        }

        /* 播放器容器样式 - 固定宽度，并移除阴影 */
        .player {
            width: 320px; /* 固定宽度，点击列表前后尺寸不变 */
            flex-shrink: 0; /* 防止播放器在flex容器中缩小 */
            background: rgba(0, 0, 0, 0.15); /* 半透明黑色背景 */
            backdrop-filter: blur(20px); /* 背景模糊效果 */
            border-radius: 24px;
            padding: 25px; /* 稍微缩小内边距 */
            border: 1px solid rgba(255, 255, 255, 0.05); /* 替换为内部边框效果 */
            color: white;
            position: relative; /* 相对定位，用于伪元素和歌曲列表按钮 */
            overflow: hidden; /* 隐藏超出部分，用于伪元素和按钮的圆角裁剪 */
        }

        /* 增强发光效果的伪元素 */
        .player::before {
            content: '';
            position: absolute;
            top: -10px;
            left: -10px;
            right: -10px;
            bottom: -10px;
            border-radius: 34px;
            background: linear-gradient(45deg,
                      rgba(255,255,255,0.05) 0%,
                      rgba(255,255,255,0) 50%);
            z-index: -1;
            filter: blur(15px);
        }

        /* 歌曲列表按钮 - 放置在播放器右上角 */
        .list-btn {
            position: absolute;
            top: 20px; /* 距离顶部 */
            right: 20px; /* 距离右侧 */
            background: none;
            border: none;
            color: white; /* 纯白色图标 */
            font-size: 28px; /* 大小适中 */
            cursor: pointer;
            transition: transform 0.2s cubic-bezier(0.2, 0.8, 0.2, 1);
            line-height: 1;
            padding: 0;
            width: 28px; /* 稍微缩小 */
            height: 28px;
            display: flex;
            justify-content: center;
            align-items: center;
            z-index: 10; /* 确保在其他内容之上 */
            -webkit-tap-highlight-color: transparent;
        }

        /* 列表按钮图标样式 */
        .list-btn span {
            display: block;
            width: 20px; /* 稍微缩小 */
            height: 2px;
            background: white;
            position: relative;
            box-shadow: 0 5px 0 white, 0 -5px 0 white; /* 稍微缩小间距 */
            border-radius: 1px;
        }

        .list-btn:hover {
            transform: scale(1.1);
            opacity: 0.9;
        }

        /* 专辑封面容器样式 - 移除阴影 */
        .album-art {
            width: 180px; /* 缩小专辑封面尺寸 */
            height: 180px; /* 缩小专辑封面尺寸 */
            border-radius: 50%;
            margin: 0 auto 20px; /* 缩小外边距 */
            overflow: hidden;
            animation: rotateAlbumArt 15s linear infinite paused;
            border: 2px solid rgba(255, 255, 255, 0.1);
            flex-shrink: 0;
        }

        /* 播放状态下专辑封面动画运行 */
        .player.playing .album-art {
            animation-play-state: running;
        }

        /* 专辑封面图片样式 */
        .album-art img {
            width: 100%;
            height: 100%;
            object-fit: cover;
        }

        /* 歌曲信息容器样式 */
        .song-info {
            text-align: center;
            margin-bottom: 25px; /* 缩小外边距 */
        }

        /* 歌曲标题样式 */
        .song-title {
            font-size: 22px; /* 稍微缩小字体 */
            font-weight: 600;
            margin-bottom: 6px;
            text-shadow: 0 2px 10px rgba(0, 0, 0, 0.3); /* 保持文字阴影，增强可读性 */
            white-space: nowrap;
            overflow: hidden;
            text-overflow: ellipsis;
        }

        /* 艺术家姓名样式 */
        .artist {
            font-size: 15px; /* 稍微缩小字体 */
            color: rgba(255, 255, 255, 0.8);
            text-shadow: 0 1px 5px rgba(0, 0, 0, 0.2); /* 保持文字阴影，增强可读性 */
            white-space: nowrap;
            overflow: hidden;
            text-overflow: ellipsis;
        }

        /* 统一滑块的CSS变量定义 */
        .slider-base {
            -webkit-appearance: none;
            width: 100%;
            height: 5px; /* 缩小滑块高度 */
            background: transparent;
            border-radius: 3px;
            cursor: pointer;
            outline: none;
            overflow: hidden;
            margin: 0;
            
            /* 定义通用滑块样式变量 */
            --fill-color: white;
            --track-bg-color: rgba(255, 255, 255, 0.2);
            --thumb-size: 12px; /* 缩小滑块圆点大小 */
            --thumb-shadow: 0 2px 5px rgba(0, 0, 0, 0.2);
            --thumb-margin-top: -3.5px; /* 调整垂直居中 */
        }

        /* 统一滑块轨道样式 */
        .slider-base::-webkit-slider-runnable-track {
            -webkit-appearance: none;
            width: 100%;
            height: 5px;
            border-radius: 3px;
            background: linear-gradient(to right, 
                        var(--fill-color), var(--fill-color)) 0% 0% / var(--current-fill) 100% no-repeat,
                        var(--track-bg-color) 100% 0% / calc(100% - var(--current-fill)) 100% no-repeat;
            transition: background-size 0.1s linear;
        }

        /* 统一滑块圆点样式 */
        .slider-base::-webkit-slider-thumb {
            -webkit-appearance: none;
            width: var(--thumb-size);
            height: var(--thumb-size);
            background: white;
            border-radius: 50%;
            margin-top: var(--thumb-margin-top);
            box-shadow: var(--thumb-shadow);
            transition: transform 0.2s cubic-bezier(0.2, 0.8, 0.2, 1), box-shadow 0.2s cubic-bezier(0.2, 0.8, 0.2, 1);
        }

        /* 统一滑块圆点悬停效果 */
        .slider-base::-webkit-slider-thumb:hover {
            transform: scale(1.1);
            box-shadow: 0 3px 8px rgba(0, 0, 0, 0.3);
        }

        /* 播放进度条滑块样式 - 继承通用滑块样式 */
        .progress-slider {
            margin-bottom: 20px; /* 缩小外边距 */
            --current-fill: 0%;
        }

        /* 时间信息容器样式 */
        .time-info {
            display: flex;
            justify-content: space-between;
            font-size: 11px; /* 缩小字体 */
            color: rgba(255, 255, 255, 0.7);
            margin-bottom: 25px; /* 缩小外边距 */
        }

        /* 控件按钮容器样式 */
        .controls {
            display: flex;
            justify-content: center;
            align-items: center;
            gap: 25px; /* 缩小间距 */
        }

        /* 上一首/下一首按钮基础样式 */
        .control-btn {
            background: none;
            border: none;
            color: rgba(255, 255, 255, 0.7);
            font-size: 26px; /* 稍微缩小字体 */
            cursor: pointer;
            transition: all 0.2s cubic-bezier(0.2, 0.8, 0.2, 1);
            padding: 5px;
            line-height: 1;
            -webkit-tap-highlight-color: transparent;
        }

        /* 上一首/下一首按钮悬停效果 */
        .control-btn:hover {
            color: white;
            transform: scale(1.05);
        }

        /* 播放/暂停按钮样式 - 与其他按钮UI尺寸一致 */
        .play-btn {
            font-size: 26px;
            padding: 0 5px;
        }

        /* 音量控制容器样式 */
        .volume-control {
            display: flex;
            align-items: center;
            gap: 8px; /* 缩小间距 */
            margin-top: 20px; /* 缩小外边距 */
        }

        /* 音量图标样式 */
        .volume-icon {
            font-size: 18px; /* 缩小音量图标大小 */
            color: rgba(255, 255, 255, 0.7);
            line-height: 1;
        }

        /* 音量调节滑块样式 (input[type="range"]) - 继承通用滑块样式 */
        .volume-slider {
            --current-fill: 70%;
        }

        /* 专辑封面旋转动画 */
        @keyframes rotateAlbumArt {
            from { transform: rotate(0deg); }
            to { transform: rotate(360deg); }
        }

        /* --- 歌曲列表面板样式 --- */

        /* 歌曲列表面板 - 垂直出现，只显示两首歌曲高度，无内容形变 */
        .song-list-panel {
            width: 320px; /* 与播放器宽度一致 */
            /* 预估高度：标题(22px+15mb) + 2首歌曲(2*(45px+2*10px padding+4px mb)) + 2*25px (panel padding) = ~225px */
            height: 225px; /* 固定高度，确保内部内容不变形 */
            flex-shrink: 0; /* 防止列表在Flex容器中收缩 */
            background: rgba(255, 255, 255, 0.15); /* 浅灰色半透明背景 */
            backdrop-filter: blur(20px); /* 模糊效果 */
            border-radius: 24px; /* 匹配播放器圆角 */
            border: 1px solid rgba(255, 255, 255, 0.05); /* 替换为内部边框效果 */
            padding: 25px; /* 列表面板内部的内边距 */
            display: flex;
            flex-direction: column;
            overflow: hidden; /* 隐藏超出滚动区域的内容 */

            /* 隐藏状态：完全透明，不可见，并通过 transform 向下平移 */
            opacity: 0;
            visibility: hidden;
            /* 向下平移自身高度 + 间距，使其初始位置在播放器下方 */
            transform: translateY(calc(100% + 25px)); 

            /* 动画过渡：更精细的控制 visibility 和 easing */
            /* max-height 也包含在内，但它会从 0 展开到固定 height，
               transform 的视觉滑动是主导 */
            transition: transform 0.5s cubic-bezier(0.2, 0.8, 0.2, 1), /* 动画曲线 */
                        opacity 0.5s cubic-bezier(0.2, 0.8, 0.2, 1),
                        visibility 0s linear 0.5s, /* 隐藏在动画结束时 */
                        max-height 0.5s cubic-bezier(0.2, 0.8, 0.2, 1); /* 同步 max-height 动画 */
            will-change: transform, opacity, max-height; /* 提升动画性能 */
            
            /* 当 max-height 为 0 时，内容会被裁剪。这里是隐藏时的额外控制 */
            max-height: 0; /* 默认隐藏时高度为0 */
        }

        /* 歌曲列表面板激活状态 */
        .song-list-panel.active {
            opacity: 1;
            visibility: visible; /* 立即显示 */
            transform: translateY(0); /* 向上滑动到其自然位置 */
            max-height: 225px; /* 展开到固定高度 */

            transition: transform 0.5s cubic-bezier(0.2, 0.8, 0.2, 1),
                        opacity 0.5s cubic-bezier(0.2, 0.8, 0.2, 1),
                        visibility 0s linear 0s, /* 立即显示 */
                        max-height 0.5s cubic-bezier(0.2, 0.8, 0.2, 1);
        }
        
        /* 确保隐藏时内部内容完全折叠 */
        /* 由于我们使用固定的 height 和 padding，这些规则是为了防止意外的可见性 */
        .song-list-panel:not(.active) h2,
        .song-list-panel:not(.active) .song-list-item {
            /* 强制隐藏子元素，因为父元素的 overflow:hidden 只能裁剪，不能让内容不可见 */
            opacity: 0;
            pointer-events: none; /* 禁用点击事件 */
            transition: opacity 0.1s; /* 让子元素也淡出 */
        }

        /* 歌曲列表标题 */
        .song-list-panel h2 {
            font-size: 22px;
            font-weight: 600;
            text-align: center;
            margin-bottom: 15px;
            text-shadow: 0 2px 10px rgba(0, 0, 0, 0.3);
            flex-shrink: 0;
        }

        /* 歌曲列表 ul */
        .song-list {
            list-style: none;
            flex-grow: 1;
            overflow-y: auto; /* 列表过长时滚动 */
            -webkit-overflow-scrolling: touch;
            padding-right: 5px;
        }
        /* 美化滚动条 (仅 Webkit 浏览器) */
        .song-list::-webkit-scrollbar {
            width: 6px;
        }
        .song-list::-webkit-scrollbar-track {
            background: rgba(255, 255, 255, 0.05);
            border-radius: 3px;
        }
        .song-list::-webkit-scrollbar-thumb {
            background-color: rgba(255, 255, 255, 0.2);
            border-radius: 3px;
            transition: background-color 0.2s ease;
        }
        .song-list::-webkit-scrollbar-thumb:hover {
            background-color: rgba(255, 255, 255, 0.3);
        }

        /* 歌曲列表项 */
        .song-list-item {
            display: flex;
            align-items: center;
            padding: 10px 12px;
            border-bottom: 1px solid rgba(255, 255, 255, 0.08); /* 分隔线透明度降低 */
            cursor: pointer;
            transition: background-color 0.2s ease, transform 0.1s ease;
            border-radius: 8px;
            margin-bottom: 4px;
        }

        .song-list-item:last-child {
            border-bottom: none;
        }

        .song-list-item:hover {
            background-color: rgba(255, 255, 255, 0.08);
            transform: translateY(-2px);
        }

        /* 当前播放歌曲的高亮效果 - 移除阴影 */
        .song-list-item.playing-now {
            background-color: rgba(255, 255, 255, 0.15);
            border-color: rgba(255, 255, 255, 0.2);
            position: relative;
        }

        /* 列表项中的专辑封面 - 移除阴影 */
        .song-list-item .album-art-sm {
            width: 45px;
            height: 45px;
            border-radius: 50%;
            overflow: hidden;
            margin-right: 12px;
            flex-shrink: 0;
            border: 1px solid rgba(255, 255, 255, 0.08);
        }

        .song-list-item .album-art-sm img {
            width: 100%;
            height: 100%;
            object-fit: cover;
        }

        /* 列表项中的文本信息 */
        .song-list-text {
            flex-grow: 1;
            min-width: 0;
        }

        .song-list-text .song-title-sm {
            font-size: 15px;
            font-weight: 500;
            margin-bottom: 2px;
            white-space: nowrap;
            overflow: hidden;
            text-overflow: ellipsis;
        }

        /* 列表项的文本内容在隐藏时完全不可见 */
        .song-list-panel:not(.active) .song-list-text .song-title-sm,
        .song-list-panel:not(.active) .song-list-text .artist-sm {
            line-height: 0; /* 确保文字行高也为0 */
            height: 0;
            margin: 0;
        }

        .song-list-text .artist-sm {
            font-size: 12px;
            color: rgba(255, 255, 255, 0.7);
            white-space: nowrap;
            overflow: hidden;
            text-overflow: ellipsis;
        }
    </style>
</head>
<body>
    <div class="player-and-list-wrapper">
        <!-- 歌曲列表面板现在位于播放器上方 -->
        <div class="song-list-panel">
            <h2>歌曲列表</h2>
            <ul class="song-list">
                <!-- 歌曲列表项将由 JavaScript 动态生成 -->
            </ul>
        </div>

        <div class="player">
            <div class="album-art">
                <img src="" alt="专辑封面">
            </div>
            
            <div class="song-info">
                <h1 class="song-title"></h1>
                <p class="artist"></p>
            </div>
            
            <!-- 播放进度条滑块 -->
            <input type="range" class="progress-slider slider-base" min="0" max="100" value="0">
            
            <div class="time-info">
                <span class="current-time">0:00</span>
                <span class="duration">0:00</span>
            </div>
            
            <div class="controls">
                <button class="control-btn prev-btn">⏮</button>
                <button class="control-btn play-btn">▶</button>
                <button class="control-btn next-btn">⏭</button>
            </div>
            
            <div class="volume-control">
                <span class="volume-icon">🔈</span> 
                <input type="range" class="volume-slider slider-base" min="0" max="100" value="70">
            </div>

            <!-- 歌曲列表按钮 -->
            <button class="list-btn"><span></span></button>
        </div>
    </div>

    <!-- 音乐来源 -->
    <audio id="myAudio"></audio>

    <script>
        // --- DOM 元素获取 ---
        const playerAndListWrapper = document.querySelector('.player-and-list-wrapper');
        const player = document.querySelector('.player');
        const albumArtImg = document.querySelector('.album-art img');
        const songTitle = document.querySelector('.song-title');
        const artistName = document.querySelector('.artist');
        const playBtn = document.querySelector('.play-btn');
        const prevBtn = document.querySelector('.prev-btn');
        const nextBtn = document.querySelector('.next-btn');
        const listBtn = document.querySelector('.list-btn'); // 歌曲列表按钮
        const progressSlider = document.querySelector('.progress-slider');
        const volumeSlider = document.querySelector('.volume-slider');
        const currentTimeSpan = document.querySelector('.current-time');
        const durationSpan = document.querySelector('.duration');
        const myAudio = document.getElementById('myAudio');

        const songListPanel = document.querySelector('.song-list-panel'); // 歌曲列表面板
        const songListUl = document.querySelector('.song-list');

        // --- 歌曲数据 (在这里添加/修改歌曲信息) ---
        const songs = [
            {
                title: '恋人',
                artist: '˚˖ 𓂂ꔫ Music ⊹꙳◦',
                albumArt: 'https://xiaoiwg.dongfeng-nissan.com.cn/aicc-workbench/res/download/default/temp/images/20251113/437aec29cafa798290dbd9f98fa7d3be3c5b01b4.png',
                audioSrc: 'https://files.catbox.moe/j127nd.mp3'
            },
            {
                title: 'HANABI',
                artist: '˚˖ 𓂂ꔫ Music ⊹꙳◦',
                albumArt: 'https://xiaoiwg.dongfeng-nissan.com.cn/aicc-workbench/res/download/default/temp/images/20251113/437aec29cafa798290dbd9f98fa7d3be3c5b01b4.png',
                audioSrc: 'https://files.catbox.moe/65mmn2.mp3'
            },
            {
                title: '乌托邦',
                artist: '˚˖ 𓂂ꔫ Music ⊹꙳◦',
                albumArt: 'https://xiaoiwg.dongfeng-nissan.com.cn/aicc-workbench/res/download/default/temp/images/20251113/437aec29cafa798290dbd9f98fa7d3be3c5b01b4.png',
                audioSrc: 'https://files.catbox.moe/u9h0dh.mp3'
            },
            {
                title: 'time machine',
                artist: '˚˖ 𓂂ꔫ Music ⊹꙳◦',
                albumArt: 'https://xiaoiwg.dongfeng-nissan.com.cn/aicc-workbench/res/download/default/temp/images/20251113/437aec29cafa798290dbd9f98fa7d3be3c5b01b4.png',
                audioSrc: 'https://files.catbox.moe/knlt98.mp3'
            },
            {
                title: '劳伦斯先生圣诞快乐',
                artist: '˚˖ 𓂂ꔫ Music ⊹꙳◦',
                albumArt: 'https://xiaoiwg.dongfeng-nissan.com.cn/aicc-workbench/res/download/default/temp/images/20251113/437aec29cafa798290dbd9f98fa7d3be3c5b01b4.png',
                audioSrc: 'https://files.catbox.moe/gw8cv1.mp3'
            },
            // 你可以在这里继续添加更多歌曲
            // 示例：
            // {
            //     title: '你的新歌曲标题',
            //     artist: '你的新艺术家',
            //     albumArt: 'https://xiaoiwg.dongfeng-nissan.com.cn/aicc-workbench/res/download/default/temp/images/20251113/437aec29cafa798290dbd9f98fa7d3be3c5b01b4.png', // 新专辑封面链接
            //     audioSrc: 'https://example.com/your-new-audio.mp3' // 新音频链接
            // },
        ];

        let currentSongIndex = 0; // 当前播放歌曲的索引

        // --- 函数定义 ---

        // 格式化时间
        function formatTime(seconds) {
            const minutes = Math.floor(seconds / 60);
            const secs = Math.floor(seconds % 60);
            return `${minutes}:${secs < 10 ? '0' : ''}${secs}`;
        }

        // 加载指定索引的歌曲
        function loadSong(index) {
            if (index < 0) { // 如果是上一首，且当前是第一首，则跳到最后一首
                currentSongIndex = songs.length - 1;
            } else if (index >= songs.length) { // 如果是下一首，且当前是最后一首，则跳到第一首
                currentSongIndex = 0;
            } else {
                currentSongIndex = index;
            }

            const song = songs[currentSongIndex];
            
            albumArtImg.src = song.albumArt;
            songTitle.textContent = song.title;
            artistName.textContent = song.artist;
            myAudio.src = song.audioSrc;

            // 重新设置播放器状态
            player.classList.remove('playing');
            playBtn.textContent = '▶';
            progressSlider.value = 0;
            progressSlider.style.setProperty('--current-fill', `0%`);
            currentTimeSpan.textContent = formatTime(0);

            // 更新歌曲列表中的高亮显示
            updateSongListHighlight();
        }

        // 动态生成歌曲列表
        function renderSongList() {
            songListUl.innerHTML = ''; // 清空现有列表
            songs.forEach((song, index) => {
                const li = document.createElement('li');
                li.classList.add('song-list-item');
                if (index === currentSongIndex) {
                    li.classList.add('playing-now');
                }
                li.dataset.index = index; // 存储歌曲索引

                li.innerHTML = `
                    <div class="album-art-sm">
                        <img src="${song.albumArt}" alt="专辑封面">
                    </div>
                    <div class="song-list-text">
                        <div class="song-title-sm">${song.title}</div>
                        <div class="artist-sm">${song.artist}</div>
                    </div>
                `;
                songListUl.appendChild(li);

                // 给每个列表项添加点击事件
                li.addEventListener('click', () => {
                    loadSong(index); // 加载点击的歌曲
                    myAudio.play(); // 播放
                    player.classList.add('playing');
                    playBtn.textContent = '⏸';
                    songListPanel.classList.remove('active'); // 隐藏列表
                });
            });
        }

        // 更新歌曲列表中的高亮显示
        function updateSongListHighlight() {
            document.querySelectorAll('.song-list-item').forEach((item, index) => {
                if (index === currentSongIndex) {
                    item.classList.add('playing-now');
                    // 确保当前播放的歌曲在列表中可见
                    setTimeout(() => {
                        item.scrollIntoView({ behavior: 'smooth', block: 'nearest' });
                    }, 0);
                } else {
                    item.classList.remove('playing-now');
                }
            });
        }

        // --- 事件监听器 ---

        // 播放/暂停功能
        playBtn.addEventListener('click', function() {
            if (myAudio.paused) {
                myAudio.play();
                player.classList.add('playing');
                playBtn.textContent = '⏸';
            } else {
                myAudio.pause();
                player.classList.remove('playing');
                playBtn.textContent = '▶';
            }
        });

        // 上一首
        prevBtn.addEventListener('click', () => {
            loadSong(currentSongIndex - 1);
            if (!myAudio.paused || player.classList.contains('playing')) { // 如果之前在播放，则继续播放
                myAudio.play();
            }
        });

        // 下一首
        nextBtn.addEventListener('click', () => {
            loadSong(currentSongIndex + 1);
            if (!myAudio.paused || player.classList.contains('playing')) {
                myAudio.play();
            }
        });

        // 歌曲列表按钮 - 现在也负责关闭列表
        listBtn.addEventListener('click', () => {
            if (songListPanel.classList.contains('active')) {
                // 如果列表已显示，则关闭它
                songListPanel.classList.remove('active');
            } else {
                // 如果列表未显示，则显示它并重新渲染
                renderSongList();
                songListPanel.classList.add('active');
            }
        });

        // 音乐加载完成后，更新总时长和初始化音量滑块
        myAudio.addEventListener('loadedmetadata', function() {
            durationSpan.textContent = formatTime(myAudio.duration);
            if (myAudio.volume === 1) { 
                myAudio.volume = 0.7;
                volumeSlider.value = 70;
            }
            volumeSlider.style.setProperty('--current-fill', `${volumeSlider.value}%`);
        });

        // 音乐播放时，更新当前时间和进度条
        myAudio.addEventListener('timeupdate', function() {
            const progressPercent = (myAudio.currentTime / myAudio.duration) * 100;
            progressSlider.value = progressPercent;
            progressSlider.style.setProperty('--current-fill', `${progressPercent}%`);
            currentTimeSpan.textContent = formatTime(myAudio.currentTime);
        });

        // 进度条滑动时，改变音乐播放位置
        progressSlider.addEventListener('input', function() {
            const seekTime = (this.value / 100) * myAudio.duration;
            myAudio.currentTime = seekTime;
            this.style.setProperty('--current-fill', `${this.value}%`);
            currentTimeSpan.textContent = formatTime(seekTime);
        });

        // 音乐播放结束时
        myAudio.addEventListener('ended', function() {
            if (currentSongIndex < songs.length - 1) {
                loadSong(currentSongIndex + 1);
                myAudio.play();
                player.classList.add('playing');
                playBtn.textContent = '⏸';
            } else {
                loadSong(0);
                player.classList.remove('playing');
                playBtn.textContent = '▶';
            }
        });

        // 音量滑块滑动时，改变音乐音量
        volumeSlider.addEventListener('input', function() {
            myAudio.volume = this.value / 100;
            this.style.setProperty('--current-fill', `${this.value}%`);
        });

        // --- 初始化 ---
        loadSong(currentSongIndex);
        volumeSlider.value = myAudio.volume * 100;
        volumeSlider.style.setProperty('--current-fill', `${volumeSlider.value}%`);
    </script>
</body>
</html>
