        }

        .window-controls {
            display: flex;
            gap: 8px;
        }

        .control-btn {
            width: 15px;
            height: 15px;
            border-radius: 50%;
            border: 2px solid rgba(0, 0, 0, 0.1);
        }

        .btn-close { background-color: #FF605C; }
        .btn-min { background-color: #FFBD44; }
        .btn-max { background-color: #00CA4E; }

        .window-title {
            margin: 0 auto;
            font-weight: bold;
            font-size: 1.1em;
            color: var(--dark-blue);
        }

        .window-body {
            padding: 20px 30px;
        }

        .main-title {
            font-family: 'ZCOOL KuaiLe', cursive;
            text-align: center;
            font-size: 2.5em;
            color: var(--dark-blue);
            text-shadow: 2px 2px 0px var(--light-blue), 4px 4px 0px rgba(160, 210, 255, 0.5);
            margin-bottom: 25px;
