        .preface {
            background-color: var(--yellow);
            border: 2px dashed var(--border-color);
            padding: 15px;
            border-radius: 10px;
            text-align: center;
            font-size: 1.1em;
            margin-bottom: 25px;
            position: relative;
        }
        .preface::before {
            content: "“";
            position: absolute;
            top: -10px;
            left: 10px;
            font-size: 3em;
            color: rgba(160, 210, 255, 0.5);
            line-height: 1;
