        .profile-pic {
            width: 120px;
            height: 160px;
            object-fit: cover;
            border-radius: 8px;
            border: 3px solid white;
        }

        .summary-text {
            display: flex;
            flex-direction: column;
        }

        .summary-text h2 {
            margin: 0 0 5px 0;
            font-size: 1.5em;
            color: var(--dark-blue);
        }
        .summary-text p {
            margin: 0;
            font-size: 0.9em;
            font-style: italic;
            color: var(--dark-blue);
        }

        .summary-text .click-prompt {
            margin-top: 10px;
            font-size: 0.9em;
            font-weight: bold;
            color: var(--dark-blue);
            background-color: rgba(255, 255, 255, 0.5);
            padding: 3px 8px;
            border-radius: 5px;
            display: inline-block;
        }

        .details-content {
            background-color: rgba(255, 255, 255, 0.8);
            padding: 15px 25px;
            border-top: 2px solid var(--border-color);
            font-size: 0.95em;
            line-height: 1.6;
        }

