            border-bottom: 2px dotted var(--light-blue);
            padding-bottom: 5px;
            margin-top: 15px;
            margin-bottom: 10px;
        }

        .details-content ul {
            list-style-type: none;
            padding-left: 10px;
        }

        .details-content ul li {
            margin-bottom: 8px;
            padding-left: 20px;
            position: relative;
        }

        .details-content ul li::before {
            content: "✨";
            position: absolute;
            left: 0;
            top: 2px;
            color: #FFC107;
        }

        .details-content blockquote {
            border-left: 4px solid var(--yellow);
            padding-left: 15px;
            margin: 15px 0;
            background-color: #FFFDE7;
            border-radius: 0 5px 5px 0;
        }

        .scenarios-section {
            background-color: var(--light-blue);
            border: 2px solid var(--border-color);
            padding: 20px;
            border-radius: 10px;
            margin-bottom: 25px;
        }

        .scenarios-section h3 {
            text-align: center;
            color: var(--dark-blue);
            margin-top: 0;
            margin-bottom: 15px;
        }

        .scenarios-section ul {
            list-style-type: none;
            padding: 0;
            margin: 0;
            font-size: 0.9em;
            line-height: 1.7;
        }

        .scenarios-section li {
            padding: 5px 10px;
            border-radius: 5px;
            margin-bottom: 5px;
            transition: background-color 0.2s;
        }

        .scenarios-section li:nth-child(odd) {
            background-color: rgba(255, 255, 255, 0.6);
        }
        .scenarios-section li:hover {
            background-color: var(--yellow);
        }

        .footer-section {
            text-align: center;
            font-size: 0.85em;
            color: #9E9E9E;
            padding: 15px;
            background-color: #F8F8F8;
            border-radius: 10px;
        }

        pre {
            white-space: pre-wrap;
            word-wrap: break-word;
            margin: 0;
            font-family: inherit;
        }

        .next-arrow {
            position: absolute;
            bottom: 15px;
            right: 20px;
            font-size: 2.5em;
            color: var(--light-blue);
            text-shadow: 2px 2px 0px rgba(160, 210, 255, 0.5);
            transform: rotate(0deg);
            transition: transform 0.4s ease;
        }
        .next-arrow:hover {
            transform: rotate(15deg) scale(1.1);
        }

        /* Responsive Design */
        @media (max-width: 600px) {
