    characters.forEach((char, index) => {
      const icon = document.createElement('div');
      icon.classList.add('icon');
      icon.dataset.characterId = char.id;
      icon.style.backgroundImage = `url('${char.icon}')`;
      icon.textContent = ''; // Ensure no text overlaps image

      icon.addEventListener('click', () => handleIconClick(char, icon));
      iconBar.appendChild(icon);

      // Select the first character by default on load
      if (index === 0) {
        // Use setTimeout to ensure initial render completes before applying active state
        setTimeout(() => handleIconClick(char, icon), 0);
      }
    });
  </script>
</body>
</html>
