           width: 100%;
        }
    }
</style>

<!-- JavaScript Logic -->
<script>
(function() {
    // --- USER EDITABLE SECTION: PLAYLIST ---
    const playlist = [
        {
            title: "beatin' of your heart",
            artist: "Genrix",
            src: "https://files.catbox.moe/75pgci.mp3",
            art: "https://i.postimg.cc/LXJCF1b3/Camera-XHS-1761478172757.jpg"
        },
        {
            title: "Cloud",
            artist: "Galdive",
            src: "https://files.catbox.moe/jvrqsy.mp3",
            art: "https://i.postimg.cc/LXJCF1b3/Camera-XHS-1761478172757.jpg"
        },
        {
            title: "I wish my mind could shut up",
            artist: "lvoris",
            src: "https://files.catbox.moe/gjxh9k.mp3",
            art: "https://i.postimg.cc/LXJCF1b3/Camera-XHS-1761478172757.jpg"
        }
    ];
    // --- END OF USER EDITABLE SECTION ---

    const audio = document.getElementById('nova-audio-element');
    const playPauseBtn = document.getElementById('nova-play-pause-btn');
    const prevBtn = document.getElementById('nova-prev-btn');
    const nextBtn = document.getElementById('nova-next-btn');
    const progressContainer = document.getElementById('nova-progress-container');
    const progressBar = document.getElementById('nova-progress-bar');
    const currentTimeEl = document.getElementById('nova-current-time');
    const durationEl = document.getElementById('nova-duration');
    const trackTitleEl = document.getElementById('nova-track-title');
    const trackArtistEl = document.getElementById('nova-track-artist');
    const albumArtEl = document.getElementById('nova-album-art');
    const defaultAlbumArt = "https://postimg.cc/8JVBhjD7";

    let currentTrackIndex = 0;
    let isPlaying = false;

    function loadTrack(index) {
        if (!playlist || playlist.length === 0) return;
        const track = playlist[index];
        audio.src = track.src;
        trackTitleEl.textContent = track.title;
        trackArtistEl.textContent = track.artist;
        albumArtEl.src = track.art || defaultAlbumArt;
        audio.load();
    }

    function playTrack() {
        if (!playlist || playlist.length === 0) return;
        isPlaying = true;
        playPauseBtn.textContent = 'âšâš';
        audio.play().catch(error => console.error("Playback failed:", error));
    }

    function pauseTrack() {
        isPlaying = false;
        playPauseBtn.textContent = 'â–¶';
        audio.pause();
    }

    function togglePlayPause() {
        if (isPlaying) {
            pauseTrack();
        } else {
            playTrack();
        }
    }

    function nextTrack() {
       if (!playlist || playlist.length === 0) return;
        currentTrackIndex = (currentTrackIndex + 1) % playlist.length;
        loadTrack(currentTrackIndex);
        playTrack();
    }

    function prevTrack() {
        if (!playlist || playlist.length === 0) return;
        currentTrackIndex = (currentTrackIndex - 1 + playlist.length) % playlist.length;
        loadTrack(currentTrackIndex);
        playTrack();
    }

    function updateProgress(e) {
        const { duration, currentTime } = e.srcElement;
        const progressPercent = (currentTime / duration) * 100;
        progressBar.style.width = `${progressPercent}%`;

        durationEl.textContent = formatTime(duration);
        currentTimeEl.textContent = formatTime(currentTime);
    }

    function setProgress(e) {
        const width = this.clientWidth;
        const clickX = e.offsetX;
        const duration = audio.duration;
        if (duration) {
          audio.currentTime = (clickX / width) * duration;
        }
    }

    function formatTime(seconds) {
        if (isNaN(seconds)) return '00:00';
        const minutes = Math.floor(seconds / 60);
        const secs = Math.floor(seconds % 60);
        return `${minutes.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
    }

    // Event Listeners
    playPauseBtn.addEventListener('click', togglePlayPause);
    nextBtn.addEventListener('click', nextTrack);
    prevBtn.addEventListener('click', prevTrack);
    audio.addEventListener('timeupdate', updateProgress);
    progressContainer.addEventListener('click', setProgress);
    audio.addEventListener('ended', nextTrack);

    if(playlist.length > 0) {
       loadTrack(currentTrackIndex);
    }
})();
</script>
