        --hover-bg: rgba(255, 224, 189, 0.6);
        --glass-bg: rgba(255, 255, 255, 0.8);
    }
    @keyframes particle-drift { 0% { transform: translate(0, 0); } 100% { transform: translate(-15px, -25px); } }
    @keyframes subtle-glow { 0% { box-shadow: 0 0 20px rgba(211, 134, 0, 0.15); } 50% { box-shadow: 0 0 30px rgba(211, 134, 0, 0.25); } 100% { box-shadow: 0 0 20px rgba(211, 134, 0, 0.15); } }

    .hub-container {
        font-family: 'Noto Sans SC', sans-serif;
        background-color: #f9f5f0;
        background-image: linear-gradient(145deg, var(--bg-start), var(--bg-end));
        max-width: 450px;
        margin: 20px auto;
        padding: 20px;
        border-radius: 25px;
        position: relative;
        overflow: hidden;
        border: 1px solid rgba(255, 255, 255, 0.8);
        box-shadow: 0 12px 35px rgba(0, 0, 0, 0.1);
        animation: subtle-glow 10s infinite alternate;
    }
    .hub-container::before {
        content: ''; position: absolute; top:0;left:0;right:0;bottom:0;
        background-image: url('data:image/svg+xml;base64,');
        background-repeat: repeat; background-size: 90px; opacity: 0.6; mix-blend-mode: soft-light; pointer-events: none;
    }
    .hub-container::after {
        content: ''; position: absolute; inset: 0;
        box-shadow: 5px 10% 2px rgba(255, 191, 0, 0.2), 90% 25% 1px rgba(255, 191, 0, 0.15), 15% 45% 1px rgba(255, 191, 0, 0.2), 80% 60% 2px rgba(255, 191, 0, 0.25), 20% 85% 1px rgba(255, 191, 0, 0.2), 75% 95% 2px rgba(255, 191, 0, 0.2);
        pointer-events: none; animation: particle-drift 25s infinite alternate linear;
    }
    .header-info {
        position: relative; z-index: 2; text-align: center;
        padding: 15px; margin-bottom: 20px;
        background: rgba(255, 255, 255, 0.4);
        backdrop-filter: blur(5px); -webkit-backdrop-filter: blur(5px);
        border-radius: 15px; border: 1px solid rgba(255,255,255,0.6);
    }
    .header-info p { margin: 0; font-size: 13px; color: #705b46; font-weight: 500;}
    .header-info .warning { margin: 5px 0 8px; font-size: 14px; color: #c46a6a; font-weight: bold; }
    .header-info .char-name { display: block; font-size: 18px; font-weight: bold; color: var(--primary-text); text-shadow: 1px 1px 2px #fff; }
    .header-info .char-cmd { font-size: 12px; color: #9c8a79; }

    .hub-dropdown {
        position: relative; z-index: 2; margin-bottom: 15px;
        border-radius: 18px; overflow: hidden;
        background: var(--glass-bg);
        backdrop-filter: blur(12px); -webkit-backdrop-filter: blur(12px);
        border: 1px solid rgba(255,255,255,0.9);
        box-shadow: 0 4px 15px rgba(0,0,0,0.05);
        transition: box-shadow 0.3s;
    }
    .hub-dropdown:hover { box-shadow: 0 6px 20px rgba(0,0,0,0.08); }
