            const firstMessage = messages[0];
            const totalSwipes = firstMessage.swipes.length;

            if (swipeIndex < 1 || swipeIndex >= totalSwipes) {
                // 注意：SillyTavern的swipe索引是从0开始的，但我们通常从1开始计数。
