    transition: transform 0.2s ease-in-out;
}
.status-container-cy details[open] > summary::after {
    transform: rotate(180deg);
}
.status-container-cy details[open] > summary {
    padding-bottom: 15px; /* 展开时给标题留出一些空间 */
}

.status-content-cy {
    padding-top: 15px;
    border-top: 1px solid var(--border-color);
