    background-position: center;
}
.moments-username-cy {
    font-weight: 600;
    color: var(--link-color);
}

.moments-body-cy .text {
    white-space: pre-wrap;
    font-size: 1.05em;
    color: var(--text-color);
    margin-bottom: 10px;
    line-height: 1.6;
}
.moments-body-cy .image-placeholder-cy {
    background-color: var(--placeholder-bg);
    border: 1px solid var(--border-color);
    border-radius: 5px;
    padding: 12px;
    margin-top: 8px;
    max-width: 250px;
}
.moments-body-cy .image-description-cy {
    font-family: 'KaiTi', 'STKaiti', serif;
    font-size: 0.95em;
    color: var(--subtle-text-color);
    line-height: 1.5;
    white-space: pre-wrap;
}

.moments-footer-cy {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-top: 15px;
}
.moments-timestamp-cy {
    font-size: 0.9em;
    color: var(--subtle-text-color);
}
.moments-actions-cy {
    font-size: 1.2em;
    cursor: pointer;
    color: var(--link-color);
    padding: 2px 5px;
    border-radius: 3px;
    user-select: none;
}
.moments-actions-cy:hover {
    background-color: #ededed;
}

</style>
