            margin-top: 10px;
            letter-spacing: 2px;
            font-weight: 700;
        }

        /* --- 作者留言区域 --- */
        .author-note {
            background-color: rgba(62, 104, 104, 0.1);
            border-left: 4px solid #3E6868;
            padding: 15px 20px;
            margin-bottom: 30px;
            border-radius: 0 8px 8px 0;
        }

        .author-note h2 {
            font-family: 'Noto Serif SC', serif;
            color: #C1AB85;
            margin: 0 0 15px 0;
            font-size: 1.4em;
            display: flex;
            align-items: center;
        }

        .author-note h2::after {
            content: '🦊';
            margin-left: 10px;
            font-size: 1.2em;
        }

        .author-note p {
            margin: 8px 0;
            line-height: 1.7;
            font-size: 0.95em;
        }

        .author-note .warning {
            color: #C94E44;
            font-weight: 700;
        }

        .author-note .feedback {
            color: #C1AB85;
            font-style: italic;
        }

        .author-note .setting-note {
            font-size: 0.9em;
            color: #A0A0A0;
        }

        /* --- 开场白选择列表 --- */
        .scenario-selector {
            margin-bottom: 30px;
        }

        .scenario-selector h2 {
            text-align: center;
            font-family: 'Noto Serif SC', serif;
            color: #C1AB85;
            letter-spacing: 5px;
            font-size: 1.5em;
            margin-bottom: 25px;
        }

        .scenario-list {
            list-style: none;
            padding: 0;
            margin: 0;
            display: grid;
            grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
            gap: 15px;
        }

        .scenario-list li {
            background-color: #2F2F2F;
            border: 1px solid #3E6868;
            border-radius: 8px;
            padding: 15px 20px;
            font-size: 1em;
            transition: all 0.3s ease;
            cursor: pointer;
            display: flex;
            align-items: center;
        }

        .scenario-list .item-number {
            color: #C1AB85;
            font-weight: 700;
            margin-right: 12px;
            font-size: 1.1em;
        }

        .scenario-list li:hover {
            transform: translateY(-5px) scale(1.03);
            background-color: #3E6868;
            border-color: #C1AB85;
            box-shadow: 0 5px 15px rgba(193, 171, 133, 0.2);
            color: #FFFFFF;
        }

        .scenario-list li:hover .item-number {
            color: #FFFFFF;
        }

        .scenario-list .special-item {
            color: #C94E44;
            font-weight: 400;
        }

        .scenario-list .extra-item {
            background-color: #2F2F2F; /* Can be a different color to stand out */
        }

        .scenario-list .extra-item:hover {
            background-color: #3E6868;
            border-color: #C1AB85;
            color: #FFFFFF;
        }

        .scenario-list .special-item:hover {
            background-color: #C94E44;
            border-color: #FFFFFF;
            color: #FFFFFF;
        }

        .scenario-list .special-item:hover .item-number {
            color: #FFFFFF;
        }

        /* --- 结尾区域 --- */
        .footer {
            text-align: center;
            padding-top: 20px;
            margin-top: 20px;
            border-top: 1px dashed #3E6868;
        }

        .footer p {
            color: #C1AB85;
            font-style: italic;
            margin: 0;
        }

        /* --- 响应式设计 --- */
        @media (max-width: 600px) {
            body {
                padding: 10px;
            }
            .main-container {
                padding: 20px;
            }
            .header h1 {
                font-size: 2em;
            }
            .scenario-list {
                grid-template-columns: 1fr;
            }
        }
    </style>
</head>
