<html>
  <head>
    <style>
      /* --- 全局样式设定 --- */
      :root {
        --bg-main: linear-gradient(170deg, #EAF2F8 10%, #FEFDFB 70%);
        --bg-glow-1: radial-gradient(circle at top left, rgba(220, 210, 180, 0.3), transparent 40%);
        --bg-glow-2: radial-gradient(circle at bottom right, rgba(200, 210, 225, 0.4), transparent 50%);
        --bg-card: rgba(253, 251, 246, 0.7);
        --bg-card-hover: #FFFFFF;
        --bg-title: #FBF8EF; /* 宣纸颜色 */
        --text-primary: #3D332A; /* 深墨色 */
        --text-secondary: #6A737D;
        --text-title-accent: #8A7C5B;
        --border-color-main: rgba(204, 195, 170, 0.5);
        --border-color-card: #DED8C9;
        --border-color-card-accent: #C0B283;
        --shadow-color: rgba(192, 178, 131, 0.3);
        --font-main: 'Georgia', 'Source Han Serif SC', '思源宋体', serif;
        --font-title: 'STXingkai', 'KaiTi', 'Source Han Serif SC', '思源宋体', serif;
        --ease-out-quint: cubic-bezier(0.22, 1, 0.36, 1);
        --ease-in-out-cubic: cubic-bezier(0.65, 0, 0.35, 1);
      }
      * { margin: 0; padding: 0; box-sizing: border-box; }
      body {
        background: var(--bg-main), var(--bg-glow-1), var(--bg-glow-2);
        color: var(--text-secondary);
        font-family: var(--font-main);
        overflow-x: hidden;
        -webkit-font-smoothing: antialiased;
        -moz-osx-font-smoothing: grayscale;
      }
      ::-webkit-scrollbar { width: 6px; }
      ::-webkit-scrollbar-track { background: #EAF2F8; }
      ::-webkit-scrollbar-thumb { background: var(--border-color-card-accent); border-radius: 3px; }
      .main-container-nova {
        max-width: 1200px; margin: 0 auto; padding: 60px 24px; opacity: 0;
        animation: fadeIn 1s var(--ease-in-out-cubic) forwards;
      }
      @keyframes fadeIn { to { opacity: 1; } }

      /* --- 古风标题样式 --- */
      .header-nova {
        margin-bottom: 70px;
        display: flex;
        flex-direction: column;
        align-items: center;
      }
      .title-wrapper-nova {
        background-color: var(--bg-title);
        padding: 10px 60px;
        border: 1px solid var(--border-color-card-accent);
        border-radius: 2px;
        box-shadow: 0 5px 15px rgba(0,0,0,0.05), inset 0 0 10px rgba(222, 216, 201, 0.5);
        position: relative;
        margin-bottom: 25px;
        max-width: 90%;
      }
      .title-wrapper-nova::before, .title-wrapper-nova::after {
        content: '';
        position: absolute;
        top: 50%;
        transform: translateY(-50%);
        width: 12px;
        height: 12px;
        background-color: var(--text-title-accent);
        border: 2px solid var(--bg-title);
        box-shadow: 0 0 0 1px var(--text-title-accent);
        border-radius: 50%;
      }
      .title-wrapper-nova::before { left: 15px; }
      .title-wrapper-nova::after { right: 15px; }
      .main-title-nova {
        font-family: var(--font-title);
        font-size: clamp(2.5rem, 4vw, 3.5rem);
        font-weight: 500;
        color: var(--text-primary);
        letter-spacing: 4px;
        margin: 0;
        padding: 5px 0;
        text-align: center;
      }
      .subtitle-nova {
        font-size: 1rem; line-height: 1.7; font-weight: 400; color: var(--text-secondary); max-width: 700px; margin: 0 auto; text-align: center;
      }

      /* --- 卡片及其他样式 --- */
      .narrative-grid-nova {
        display: grid; grid-template-columns: repeat(auto-fill, minmax(320px, 1fr)); gap: 24px;
      }
      .card-nova {
        position: relative; background-color: var(--bg-card);
        border: 1px solid var(--border-color-card); border-left: 5px solid var(--border-color-card-accent); border-right: 5px solid var(--border-color-card-accent);
        border-radius: 4px; padding: 24px; display: flex; flex-direction: column; cursor: pointer;
        opacity: 0; transform: translateY(20px);
        transition: transform 0.4s var(--ease-out-quint), box-shadow 0.4s var(--ease-out-quint), border-color 0.4s var(--ease-out-quint), background-color 0.4s var(--ease-out-quint);
        animation: cardFadeIn 0.6s var(--ease-out-quint) forwards;
        animation-delay: calc(var(--card-index) * 0.08s);
      }
      @keyframes cardFadeIn { to { opacity: 1; transform: translateY(0); } }
      .card-nova:hover {
        transform: translateY(-8px) scale(1.03); border-color: var(--border-color-card-accent);
        background-color: var(--bg-card-hover); box-shadow: 0 10px 30px var(--shadow-color);
      }
      .card-header-nova { margin-bottom: 16px; }
      .card-title-nova {
        font-size: 1.5rem; font-weight: 600; color: var(--text-title-accent);
      }
      .card-desc-nova {
        font-size: 0.95rem; line-height: 1.6; color: var(--text-secondary);
        flex-grow: 1; margin-bottom: 24px;
        display: -webkit-box; -webkit-line-clamp: 4; -webkit-box-orient: vertical;
        overflow: hidden; text-overflow: ellipsis;
      }
      .card-footer-nova { margin-top: auto; }
      .enter-button-nova {
        width: 100%; padding: 12px; border-radius: 8px; border: 1px solid var(--border-color-card-accent);
        background: transparent; color: var(--text-title-accent); font-size: 1rem; font-weight: 500;
        cursor: pointer; transition: background-color 0.3s, color 0.3s;
      }
      .card-nova:hover .enter-button-nova { background-color: var(--border-color-card-accent); color: white; }
      .state-container-nova {
        display: flex; justify-content: center; align-items: center; padding: 80px 20px; text-align: center; grid-column: 1 / -1;
      }
      .loading-spinner-nova {
        width: 48px; height: 48px; border-radius: 50%; position: relative; animation: rotate 1s linear infinite;
      }
      .loading-spinner-nova::before, .loading-spinner-nova::after {
        content: ""; box-sizing: border-box; position: absolute; inset: 0px; border-radius: 50%;
        border: 4px solid transparent; border-bottom-color: var(--border-color-card-accent);
      }
      .loading-spinner-nova::before { border-bottom-color: var(--text-primary); animation: rotate 0.5s linear infinite reverse; }
      @keyframes rotate { 100% { transform: rotate(360deg); } }
      .error-message-nova { font-size: 1.2rem; color: var(--text-secondary); }
      .transition-overlay-nova {
        position: fixed; inset: 0; background: #EAF2F8; z-index: 9999;
        display: flex; justify-content: center; align-items: center; opacity: 0;
        pointer-events: none; transition: opacity 0.5s var(--ease-in-out-cubic);
      }
      .transition-overlay-nova.active { opacity: 1; pointer-events: all; }
      @media (max-width: 768px) {
        .main-container-nova { padding: 40px 16px; }
        .narrative-grid-nova { grid-template-columns: 1fr; }
        .title-wrapper-nova { padding: 10px 40px; }
        .title-wrapper-nova::before { left: 10px; }
        .title-wrapper-nova::after { right: 10px; }
      }
    </style>
  </head>
  <body>
    <div id="transitionOverlay" class="transition-overlay-nova"><div class="loading-spinner-nova"></div></div>
    <div class="main-container-nova">
      <header class="header-nova">
        <div class="title-wrapper-nova">
           <h1 class="main-title-nova">人蛇两根吃不饱</h1>
        </div>
        <p class="subtitle-nova">此卡仅发布于暮色铃兰全女群和旅程女性向卡区，禁止二传商用倒卖。<br>如果在其他地方见到此卡请联系村妹儿（dc：chenjuan1314）。祝食用愉快。</p >
      </header>
      <main>
        <div id="narrativesGrid" class="narrative-grid-nova">
          <div class="state-container-nova" id="loadingState"><div class="loading-spinner-nova"></div></div>
        </div>
      </main>
    </div>
    <script>
      document.addEventListener('DOMContentLoaded', () => {
        const grid = document.getElementById('narrativesGrid');
        const overlay = document.getElementById('transitionOverlay');
        async function loadNarratives() {
          const loadingState = document.getElementById('loadingState');
          try {
            const messages = await getChatMessages("0", { include_swipe: true });
            if (loadingState) loadingState.remove();
            if (!messages || messages.length === 0 || !messages[0].swipes || !messages[0].swipes.length || messages[0].swipes.length <= 1) {
              grid.innerHTML = `<div class="state-container-nova"><p class="error-message-nova">妈妈没有找到任何开场白哦，快去添加一些吧。</p ></div>`;
              return;
            }
            const swipes = messages[0].swipes.slice(1);
            swipes.forEach((content, index) => {
              const swipeId = index + 1;
              let title = `卷轴 ${swipeId}`;
              let description = "点击探索这个未被描述的神秘故事。";
              const titleMatch = content.match(/<!--\s*title:\s*(.*?)\s*-->/);
              if (titleMatch) title = titleMatch[1].trim();
              const descMatch = content.match(/<!--\s*desc:\s*(.*?)\s*-->/s);
              if (descMatch) description = descMatch[1].trim();
              const card = document.createElement('div');
              card.className = 'card-nova';
              card.style.setProperty('--card-index', index);
              card.dataset.swipeId = swipeId;
              card.innerHTML = `
                <div class="card-header-nova"><h2 class="card-title-nova">${title}</h2></div>
                <p class="card-desc-nova">${description}</p >
                <div class="card-footer-nova"><button class="enter-button-nova">开启卷轴</button></div>
              `;
              grid.appendChild(card);
              card.addEventListener('click', () => switchToNarrative(swipeId));
            });
          } catch (error) {
            console.error('加载开场白时出错了:', error);
            if (loadingState) loadingState.remove();
            grid.innerHTML = `<div class="state-container-nova"><p class="error-message-nova">呀，加载失败了，请检查控制台或刷新重试。</p ></div>`;
          }
        }
        async function switchToNarrative(swipeId) {
          overlay.classList.add('active');
          try {
            const messages = await getChatMessages("0", { include_swipe: true });
            if (messages && messages[0].swipes && messages[0].swipes[swipeId]) {
              await setChatMessage(messages[0].swipes[swipeId], 0, {
                swipe_id: swipeId,
                refresh: 'display_and_render_current'
              });
            }
          } catch (error) {
            console.error('切换开场白时出错了:', error);
            overlay.classList.remove('active');
          }
        }
        loadNarratives();
      });
    </script>
  </body>
</html>
