    cursor: pointer;
    vertical-align: middle;
    margin-right: 8px;
    width: 22px;
    height: 22px;
    background-color: rgba(255, 250, 235, 0.4); 
    backdrop-filter: blur(5px); 
    -webkit-backdrop-filter: blur(5px);
    border: 1px solid rgba(255, 248, 225, 0.6); 
    border-radius: 8px; 
    box-shadow: 0 4px 10px rgba(211, 199, 163, 0.2);
    transition: all 0.2s ease-in-out;
}


input[type="checkbox"]:hover {
    border-color: rgba(255, 248, 225, 0.8);
    box-shadow: 0 5px 15px rgba(211, 199, 163, 0.3);
}


input[type="checkbox"]:checked {
    background: linear-gradient(135deg, #fff8dc, #ffefb5);
    border-color: rgba(255, 248, 225, 0.9);
    box-shadow: 0 2px 12px rgba(141, 110, 99, 0.7);
