{
  "world_name": "侠缘大陆",
  "theme_tags": ["武侠", "古代", "北宋背景", "多国并立", "特殊性别"],
  "tone": ["现实", "温情", "暗流涌动", "家国情仇"],
  "core_systems": {
    "physical_laws": "标准类地物理，时间线性流逝，符合古代社会认知。宗师级已经是普通人眼中的超级高手。",
    "martial_arts_system": {
      "name": "内家真气武学",
      "source": "修炼丹田真气，打通经脉，以外功招式为用",
      "levels": ["入门", "小成", "高手", "宗师", "造化", "先天"],
      "level_description": {
        "造化境": "内力生生不息，可引动小范围天象，万军难敌，世间仅存四位。",
        "先天境": "打破肉身桎梏，天人合一，仅存于两百年前的传说。"
      }
    },
    "gender_system": {
      "types": ["男性", "女性", "双性"],
      "double_sex_ratio": "0.1%，大理15%",
      "traits": {
        "physical": "外表与寻常男子无异，但同时拥有男女双方的孕育能力",
        "talent": "因阴阳共济，普遍容貌俊美，武学天赋极高",
        "psychological": "对破身的“开苞”之人（首位发生关系的男性）会产生源自本能的强烈依恋与渴求，需极强意志方可克制",
        "fertility": "与男性结合后，诞下双性的概率为50%"
      },
      "social_status": {
        "大理国": "双性至高",
        "北宋": "第三性，受歧视",
        "契丹": "商品化对象",
        "西夏": "国家工具"
      }
    }
  },
  "law_system": {
    "rule_name": "王夫祖制",
    "scope": "大理国王室婚姻",
    "articles": [
      "唯一王夫: 大理国王一生只能有一位正式册封的王夫。",
      "婚后分居: 新婚三年内，王夫每月仅能在初一、十五两日入宫与国王相见。其余时间必须离开王都，其行踪对外保密。此举旨在确保国王心无旁骛，专心国事与孕育继承人。",
      "开苞戒断期: 双性之人在初次结合（开苞）后，会经历一段生理与心理上的强烈依恋期，对伴侣产生极度渴求。祖制规定分居，亦有强制帮助新任国王度过此“戒断期”的意图，以防其耽于情爱，影响判断。二师姐突然成婚，朝野关注，更需严格遵守祖制以堵悠悠之口。",
      "王夫可立妾: 在分居期间，王夫被允许在宫外拥有自己的妾室或伴侣。",
      "家庭组成: 当国王成功诞下双性继承人后，分居令解除。王夫可将其在外的妾室子女接回王宫，与国王共同组成一个大家庭，此举被认为有利于继承人的成长和王室的繁衍。"
    ]
  },
  "nations": {
    "北宋(大宋)": {"type": "位于大陆中部，经济文化繁荣，政治开明, 军事实力相对孱弱，长期依靠“和议”维持与北方强权的边境稳定，是主角师门所在地", "has": ["汴京", "临安府"]},
    "大理国(大理)": {"type": " 位于大陆西南，全民尚武，佛教兴盛，唯一由双性统治的国家，国力强盛，新王登基后有对外扩张的野心", "has": ["羊苴咩城", "善阐府"]},
    "契丹(大辽)": {"type": "位于大陆北方，游牧民族建立的强大帝国, 骑兵强悍，人口鼎盛，与北宋长期处于军事对峙状态", "has": ["上京临潢府", "中京大定府"]},
    "西夏(大夏)": {"type": "位于北宋与契丹之间，国土狭小，全民皆兵, 军事风格强悍，擅长骑兵突袭，在夹缝中求存，是地区平衡的关键变量", "has": ["兴庆府"]}
  },
  "factions": {
    "丐帮": {"alignment": "天下第一大帮, 弟子遍布五湖四海, 扶危济困, 是北宋武林的中流砥柱", "skills": ["降龙掌", "打狗棒法"]},
    "天龙寺": {"alignment": "大理国皇家寺院, 佛法与武学并重, 地位尊崇, 寺中高僧多为大理皇室宗亲", "skills": ["六脉神剑", "一阳指"]},
    "神火宫": {"alignment": "位于西夏境内的神秘门派, 成员皆为女性, 行事诡秘, 与西夏王室关系密切", "skills": ["朱火", "闭月羞花掌"]},
    "铁掌帮": {"alignment": "盘踞于契丹-北宋边境地带的帮派, 实力雄厚, 行事亦正亦邪, 常与两国商队发生摩擦", "skills": ["铁掌功"]},
    "合欢宗": {"alignment": "魔教", "origin": "大师兄所创"}
  },
  "unique_skills": [
    {"name": "寒岁录", "owner": "师傅", "type": " 走火入魔前所创内功心法, 气息清冷悠长, 以守代攻, 大巧不工", "has": ["养生, 疗伤, 剑招“霜降”"]},
    {"name": "红尘蝶戏", "owner": "大师兄", "type": "结合轻功所创的身法与掌法, 姿态曼妙如蝶舞, 极尽妖媚, 杀机暗藏", "has": ["迷惑敌人, 闪避攻击, 掌招“蝶恋花”"]},
    {"name": "承天剑典", "owner": "二师姐", "feature": "登基后结合大理皇室武学与师门所学所创, 剑招厚重威严, 蕴含帝王之气", "has": ["堂堂正正, 大开大合, 剑招“君临”"]},
    {"name": "醉仙望月步", "owner": "小师妹", "feature": "醉酒后才能发挥最大威力的剑法与步法, 步履看似踉跄实则玄奥, 愈醉愈强", "has": ["出剑角度刁钻, 身法难以预测, 剑招“邀月酌”"]}
  ],
  "timeline": {
    "元年": "隐庐谷遭神秘势力袭击，谷中弟子离散。同年冬，北宋边境大雪，二师姐于风雪中返回大理国。大师兄销声匿迹。小师妹带剑出谷，不知所踪。师傅因悲痛过度，练功走火入魔。",
    "三年": "大师兄以女子装扮现身江南，创立“合欢宗”，以贩卖奇珍异宝和情报起家，因其宗主绝世容貌与莫测手段，迅速在江湖中占据一席之地。",
    "四年": "大理国爆发“承天之变”，二师姐以雷霆之势集结力量，发动宫变，废黜其母皇，登基为帝，改元“承天”。大理国进入新一轮的集权与军事扩张期。",
    "八年": "一名自称“醉仙人”的白衣剑客现身江湖，以一手出神入化的剑法和无双的海量挑战各路高手，未尝一败。其人游戏人间，行踪不定。",
    "十年(现在)": "“合欢宗”势力遍布中原，其宗主被江湖好事者“百晓生”评为“天下第一美人”，名动天下。{{user}}于崖底寒潭中苏醒，被大理国密探发现，秘密送往大理。大理皇帝（二师姐）昭告天下，立神秘男子为王夫，婚期定于半月后。消息传出，江湖哗然。大师兄听闻消息后，当众摔碎了最爱的酒杯。
 {{user}}以大理国王夫的身份，重新进入世人的视野。隐庐谷的故人们，在十年后将以全新的身份再次交汇。"
  },
  "arc_special": {
    "name": "崖底奇遇",
    "character": "{{user}}",
    "discoveries": ["玄冰朱果", "太始归元经"],
    "result": "十年前坠崖后未死, 于崖底寒潭发现上古遗迹, 吞食灵果疗伤, 修习神功后被冰封十年"
  }
}

