  "企划简介": "VirtuaReal是NIJISANJI（彩虹社）与bilibili合作成立的面向中国的虚拟主播企划。企划名称为'虚拟（Virtual）'和'现实（Real）'的合写，从某种意义上来说与彩虹社的'二次（NIJI）三次（SANJI）'形成了对仗。作为中国最大的企业系虚拟UP主团体，VirtuaReal在中文虚拟主播圈具有重要影响力。",
  "企划背景": "2019年4月19日，即Ichikara（今称ANYCOLOR）方面收回VEgo的'二次三次'授权后不到一个月，便宣布与bilibili合作成立VirtuaReal Project，并于5月推出了第一期艺人——艾因与琉绮。此举标志着彩虹社正式进入中国市场，开启了中日虚拟主播企划合作的新篇章。",
  "组织架构": {
    "三分体系": {
      "VirtuaReal_Project": {
        "说明": "VR本部，最大的分支，简称VRP",
        "特点": "在彩虹社官网艺人名单列名，参与彩虹社国际活动，被观众视为'正式的彩虹社序列'",
        "活跃成员数": "50余位（截至2024年）"
      },
      "VirtuaReal_Star": {
        "说明": "与知名人士的合作企划",
        "特点": "成员要么是知名UP主原地虚拟UP主化，要么公开中之人，VR方面不干涉原先的艺人经纪关系",
        "首次公布": "2019年7月19日BML-VR现场",
        "首位成员": "泠鸢yousa"
      },
      "VirtuaReal_Link": {
        "说明": "虚拟主播伙伴计划",
        "特点": "招收的成员大部分是已经开始活动一段时间的虚拟UP主，不乏与其他企划合作者",
        "公布时间": "2019年7月19日"
      }
    }
    }
  },
  "世代制度": {
    "说明": "VR官方在四期生之后不再采用'X期生'的说法，但这种编号方式依旧被艺人与观众延续使用，一般将同时公开消息的一组艺人视为一期",
    "总世代数": "26期（截至2025年）",
    "特殊组合": "从二十期开始，出现了明确的具有组合名的出道组合"
  },
  "成员构成": {
    "总览": "VirtuaReal旗下拥有多期成员，涵盖不同风格和特色的虚拟主播",
    "世代": "持续扩充，截至2025年已发展至26期",
    "成员特色": "包括游戏主播、歌势、聊天势、技术势等多种类型",
    "组合文化": "存在多个知名组合，如ROZA、VR三fei人、VRF4等"
  },
  "社区文化": {
    "运营特色": {
      "自由度高": "主播在内容创作上有较大自由度",
      "个性发展": "鼓励主播发展自己的特色内容和风格",
      "技术支持": "提供专业的动捕技术和3D模型支持",
      "活动丰富": "定期举办大型线下活动和线上企划",
      "国际交流": "作为彩虹社分部，有机会参与国际性活动"
    }
  },
  "工作室": {
    "KAIOUSEI_STUDIO": {
      "负责人": "时守星沙",
      "业务": "制作3DCG等内容"
    }
  },
  "知名工作人员": [
    {
      "名字": "时守星沙",
      "职位": "企划总制作人（名义上）",
      "其他": "S1 VTB板块版主"
    },
    {
      "名字": "光驱子",
      "职位": "实际总制作人",
      "关系": "有'光驱子=时守星沙'的说法"
    }
  ],
  "总成员数": {
    "说明": "截至2024年10月",
    "VirtuaReal_Project": "50位",
    "VirtuaReal_Star": "4位",
    "VirtuaReal_Link": "10位",
    "总计": "64位"
  },
  "特色": [
    "中国最大的虚拟UP主企划之一",
    "彩虹社在中国的官方合作企划",
    "截至2024年8月，是中国最受欢迎的企业系VUP团体",
    "旗下成员在bilibili拥有超过1200万粉丝",
    "独特的三分支体系运营模式",
    "较强的独立运营能力,箱推较多"
  ],
  "历史事件": {
    "成立初期": "2019年5月6日，一期生艾因和琉绮正式出道",
    "首个10万粉丝": "2020年2月24日，琉绮成为首位在任何平台突破10万粉丝的海外虚拟主播",
    "企划分支": "2019年7月19日，公布VirtuaReal Star和VirtuaReal Link两个分支企划",
    "世代发展": "截至2025年，已发展至26期生"
  },
  "关联企划": {
    "NIJISANJI": "母企划/技术支持方",
    "bilibili": "合作运营方",
    "登乐V计划": "多位成员从该计划加入VirtuaReal Link"
  }
}

