    "敬语体系": {
      "描述": "对前辈、上司、长辈必须使用敬语（-입니다, -요, -세요）",
      "示例": ["습니다", "세요", "이에요"],
      "情境": "职场、正式场合、与陌生人交谈"
    },
    "感叹词": {
      "描述": "口语中频繁使用表达情绪的感叹词",
      "示例": ["아이고 (Aigo, 表无奈/累)", "어머나/헐 (Eomeona/Heol, 表惊讶)", "대박 (Daebak, 表赞叹)", "안 돼 (Andwae, 表拒绝/震惊)"]
    },
    "非语言习惯": {
      "描述": "伴随对话的肢体礼仪",
      "示例": ["递接物品用双手或托手腕", "侧身饮酒以示尊敬", "大笑时用手遮嘴"]
    }
  },
  "饮食习惯": {
    "基础餐制": {
      "描述": "每餐基本配置",
      "组成": ["米饭", "汤品（如大酱汤、泡菜汤）", "多种共享小菜（伴餐）"],
      "餐具使用": "勺子用于米饭和汤，筷子用于夹菜"
    },
    "标志性食物": {
      "描述": "具有高度辨识度的日常食物",
      "核心": ["泡菜 (Kimchi, 每餐必备)"],
      "即时食品": ["三角饭团", "杯面", "香蕉牛奶", "便利店三明治"],
      "流行饮品": ["美式咖啡 (极度流行)"]
    },
    "饮食文化": {
      "描述": "典型的用餐方式",
      "特点": ["主菜共享制", "汤饭常用塑料盒打包", "咖啡馆文化发达"]
    }
  }
}
{
  "主题": "韩国考试院 (Gosiwon) 解释",
  "核心定义": {
    "描述": "一种提供极端小型化居住单元的超低成本租赁住所，是韩国居住条件最底层的标志之一。",
    "原名": "고시원",
    "字面含义": "最初为准备各类考试（如公务员、司法考试）的考生提供短期住宿，现已泛化为底层单身人士的长期住所。"
  },
  "核心特征": [
    {
      "特征": "极小的空间",
      "描述": "单个房间通常仅3-5平方米，仅能容纳一张床和一张小书桌。"
    },
    {
      "特征": "共用设施",
      "描述": "卫生间、淋浴间和简易厨房（通常仅限泡面）为所有住户共用。"
    },
    {
      "特征": "月付模式",
      "描述": "通常按月初支付租金，押金极低或为零，是其主要吸引力。"
    },
    {
      "特征": "全包式账单",
      "描述": "租金通常包含水、电、网费，且提供免费的米饭和泡菜。"
    },
    {
      "特征": "极差的隔音与隐私",
      "描述": "墙壁薄，噪音问题严重，隐私几乎无从谈起。"
    }
  ],
  "居住者画像": [
    "备考的考生",
    "经济极度拮据的底层打工者",
    "刚找到工作的贫困实习生",
    "临时落脚的无家可归者"
  ],
  "叙事功能": {
    "象征意义": "在故事中是角色陷入贫困、绝望和生存挣扎的强烈视觉符号。",
    "情节作用": "能直观体现角色的社会经济地位，并为其提供一种压抑、无隐私、渴望逃离的生存背景。"
  }
}

