<%_
  //模块1: 初始化所有变量
  let data = {};
  try {
    const messages = await getChatMessages(getCurrentMessageId());
    if (messages && messages.length > 0 && messages[0].data && messages[0].data.stat_data) {
      data = JSON.parse(JSON.stringify(messages[0].data.stat_data));
    } else {
        // 如果没有历史记录，就直接中止脚本，防止出错
        return;
    }
  } catch (e) {
    // 捕获错误，直接中止脚本
    return;
  }

  // 确保核心结构存在
  if (!data['李闵'] || !data['李闵']['核心数值'] || !data['李闵']['核心数值']['与User的好感度']) {
      return; // 核心数据不存在，中止
  }

  // 模块2: 计算好感度变化
  let currentAffection = data['李闵']['核心数值']['与User的好感度'][0];
  let change = 0;

  // 使用 __.buffer 获取上下文，这比 getUserInput 更具兼容性
  // __.buffer 是一个包含最近聊天记录的数组，每个元素是一个对象 {role, content}
  // 我们通常关心最后两条：User的输入和AI的回复
  const lastUserMessage = __.buffer[__.buffer.length - 2]?.content || '';
  const lastAiMessage = __.buffer[__.buffer.length - 1]?.content || '';
  const context = lastUserMessage + lastAiMessage;

  // 根据我们之前设定的规则来判断
  // 积极互动
  if (/(抱|夸|笑|温柔|关心|靠近|触碰)/.test(context) && !/(滚|离|讨厌)/.test(context)) {
    change += _.random(1, 3);
  }
  // 负面互动
  if (/(打|骂|滚|羞辱|命令|嘲笑|推开|恶心)/.test(context)) {
    change -= _.random(2, 4);
  }
  // 满足"被需要"的核心需求
  if (/(依赖|需要你|保护我|脆弱|只有你)/.test(context)) {
    change += _.random(3, 5);
  }
  // 触发"被抛弃"的恐惧
  if (/(离开|别人|分手|其他人)/.test(context)) {
    change -= _.random(4, 5);
  }

  // 限制单次变化幅度
  change = _.clamp(change, -5, 5);

  // 如果没有变化，就直接结束，不进行后续操作
  if (change === 0) {
      return;
  }

  let newAffection = currentAffection + change;
  // 限制好感度范围
  newAffection = _.clamp(newAffection, -20, 100);

  // 模块3: 更新数据
  data['李闵']['核心数值']['与User的好感度'][0] = newAffection;

  // 找出变化的原因
  let reason = "根据互动内容自动调整。";
  if (change > 0) {
      if (/(依赖|需要你|保护我|脆弱|只有你)/.test(context)) reason = "User的言行满足了李闵被需要的情感核心。";
      else reason = "User展现出的积极或亲密互动提升了好感。";
  } else if (change < 0) {
      if (/(离开|别人|分手|其他人)/.test(context)) reason = "User的言行触发了李闵被抛弃的深层恐惧。";
      else reason = "User的负面或攻击性互动降低了好感。";
  }

  // 模块4: 将更新后的数据存入 stat_data 宏
  const finalData = { stat_data: data };
  setGlobal(finalData);

  // 在控制台输出一条信息，方便我们调试
  toastr.info(`李闵好感度变化: ${currentAffection} -> ${newAffection} (变化: ${change})。原因: ${reason}`);
_%>

